import React, { useState, useEffect } from 'react';

const ApprovedDataComponent = () => {
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('horasExtras');
  const [notificacoes, setNotificacoes] = useState([]);
  
  // Estados para armazenar dados aprovados pelo administrador
  const [horasExtrasApproved, setHorasExtrasApproved] = useState([]);
  const [feriasApproved, setFeriasApproved] = useState([]);
  const [folgasApproved, setFolgasApproved] = useState([]);
  const [bancoHoras, setBancoHoras] = useState(null);
  const [ausenciasApproved, setAusenciasApproved] = useState([]);
  const [jornada, setJornada] = useState(null);
  
  const [userData, setUserData] = useState(() => {
    const storedUser = JSON.parse(localStorage.getItem('user') || '{}');
    return {
      id: storedUser.id || 1,
      name: storedUser.name || 'Usuário',
      email: storedUser.email || ''
    };
  });

  useEffect(() => {
    const checkAndSetData = (key, setState) => {
      const storedData = localStorage.getItem(key);
      if (storedData) {
        const parsedData = JSON.parse(storedData);
        const approved = parsedData.filter(item => item.employeeId === userData.id && item.status === 'aprovado');
        setState(approved);
        if (approved.length > 0) {
          setNotificacoes(prev => [...prev, `${key} aprovado!`]);
        }
      }
    };
    
    checkAndSetData('horasExtrasRequests', setHorasExtrasApproved);
    checkAndSetData('feriasRequests', setFeriasApproved);
    checkAndSetData('folgasRequests', setFolgasApproved);
    checkAndSetData('ausencias', setAusenciasApproved);
    
    const storedBancoHoras = localStorage.getItem('bancoHoras');
    if (storedBancoHoras) {
      const bancoHorasData = JSON.parse(storedBancoHoras);
      const userBancoHoras = bancoHorasData.find(item => item.employeeId === userData.id);
      if (userBancoHoras) {
        setBancoHoras(userBancoHoras);
        setNotificacoes(prev => [...prev, 'Banco de Horas atualizado!']);
      }
    }
    
    const storedJornadas = localStorage.getItem('jornadas');
    if (storedJornadas) {
      const jornadas = JSON.parse(storedJornadas);
      const userJornada = jornadas.find(item => item.employeeId === userData.id);
      if (userJornada) {
        setJornada(userJornada);
        setNotificacoes(prev => [...prev, 'Jornada de trabalho definida!']);
      }
    }
  }, [userData.id]);
  
  return (
    <>
      <button onClick={() => setShowModal(true)} className="w-full sm:w-64 bg-purple-600 text-white font-bold py-4 rounded-lg shadow-lg">
        Dados do Administrador
      </button>
      
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">Dados do Administrador</h3>
              <button onClick={() => setShowModal(false)} className="text-purple-300 hover:text-white">✖</button>
            </div>
            
            <div className="mb-6 bg-purple-800 p-3 rounded-lg">
              <h4 className="text-white font-semibold">Notificações</h4>
              {notificacoes.length > 0 ? (
                <ul className="text-purple-300">
                  {notificacoes.map((noti, index) => (
                    <li key={index}>✅ {noti}</li>
                  ))}
                </ul>
              ) : (
                <p className="text-purple-300">Nenhuma notificação</p>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ApprovedDataComponent;
