import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [userName, setUserName] = useState('Usuário');
  const navigate = useNavigate();

  useEffect(() => {
    // Verificar se o usuário está autenticado
    const isAuthenticated = localStorage.getItem('user');
    if (!isAuthenticated) {
      // Redirecionar para a página de login se não estiver autenticado
      navigate('/');
      return;
    }

    // Buscar o nome do usuário no localStorage (se disponível)
    const storedUserName = localStorage.getItem('userName');
    if (storedUserName) {
      setUserName(storedUserName);
    }
  }, [navigate]);

  const handleLogout = () => {
    // Remover dados de autenticação
    localStorage.removeItem('user');
    localStorage.removeItem('userName');
    // Redirecionar para a página de login
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      {/* Header */}
      <header className="bg-purple-800 bg-opacity-50 shadow-lg">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center">
            <span className="bg-purple-600 rounded-full p-1 mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
              </svg>
            </span>
            <h1 className="text-2xl font-bold">CuidaEmprego</h1>
          </div>
          
          <div className="flex items-center">
            <div className="mr-4 flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
              </svg>
              <span className="font-medium">{userName}</span>
            </div>
            <button
              onClick={handleLogout}
              className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-1 rounded-lg transition duration-200"
            >
              Sair
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-8">
        <h2 className="text-2xl font-bold mb-6">Painel de Controle</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Admin Horas Extras Card */}
          <Link to="/admin/horas-extras" className="bg-purple-800 bg-opacity-40 rounded-lg p-6 hover:bg-opacity-50 transition duration-200 shadow-lg border border-purple-500">
            <div className="flex items-center mb-4">
              <span className="bg-purple-600 rounded-full p-2 mr-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </span>
              <h3 className="text-xl font-semibold">Horas Extras</h3>
            </div>
            <p className="text-gray-300">Gerencie e solicite horas extras para aprovação.</p>
          </Link>
          
          {/* Admin Férias Card */}
          <Link to="/admin/ferias" className="bg-purple-800 bg-opacity-40 rounded-lg p-6 hover:bg-opacity-50 transition duration-200 shadow-lg border border-purple-500">
            <div className="flex items-center mb-4">
              <span className="bg-purple-600 rounded-full p-2 mr-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </span>
              <h3 className="text-xl font-semibold">Férias</h3>
            </div>
            <p className="text-gray-300">Planeje e solicite períodos de férias.</p>
          </Link>
          
          {/* Admin Folgas Card */}
          <Link to="/admin/folgas" className="bg-purple-800 bg-opacity-40 rounded-lg p-6 hover:bg-opacity-50 transition duration-200 shadow-lg border border-purple-500">
            <div className="flex items-center mb-4">
              <span className="bg-purple-600 rounded-full p-2 mr-3">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
              </span>
              <h3 className="text-xl font-semibold">Folgas</h3>
            </div>
            <p className="text-gray-300">Gerencie dias de folga e compensações.</p>
          </Link>
        </div>
        
        {/* Resumo/Status Section */}
        <div className="mt-8 bg-purple-800 bg-opacity-40 rounded-lg p-6 border border-purple-500 shadow-lg">
          <h3 className="text-xl font-semibold mb-4">Resumo</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-purple-900 bg-opacity-50 p-4 rounded-lg">
              <h4 className="font-medium text-purple-300 mb-1">Horas Extras</h4>
              <p className="text-2xl font-bold">0h</p>
              <p className="text-sm text-purple-300">Este mês</p>
            </div>
            <div className="bg-purple-900 bg-opacity-50 p-4 rounded-lg">
              <h4 className="font-medium text-purple-300 mb-1">Férias</h4>
              <p className="text-2xl font-bold">30 dias</p>
              <p className="text-sm text-purple-300">Disponíveis</p>
            </div>
            <div className="bg-purple-900 bg-opacity-50 p-4 rounded-lg">
              <h4 className="font-medium text-purple-300 mb-1">Folgas</h4>
              <p className="text-2xl font-bold">2 dias</p>
