import React, { useState, useEffect } from 'react';

const DashboardPage = () => {
  const [activeTab, setActiveTab] = useState('ponto'); // Aba ativa (Ponto, Horas Extras, Férias, Folgas)

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      {/* Header */}
      <header className="bg-purple-900 bg-opacity-80 shadow-lg sticky top-0 z-10">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <span className="text-xl font-bold">CuidaEmprego</span>
        </div>
      </header>

      {/* Menu de navegação */}
      <nav className="flex justify-center space-x-4 bg-purple-800 p-3">
        <button onClick={() => setActiveTab('ponto')} className={`px-4 py-2 rounded ${activeTab === 'ponto' ? 'bg-purple-600' : 'bg-purple-700'}`}>
          Registro de Ponto
        </button>
        <button onClick={() => setActiveTab('horasExtras')} className={`px-4 py-2 rounded ${activeTab === 'horasExtras' ? 'bg-purple-600' : 'bg-purple-700'}`}>
          Horas Extras
        </button>
        <button onClick={() => setActiveTab('ferias')} className={`px-4 py-2 rounded ${activeTab === 'ferias' ? 'bg-purple-600' : 'bg-purple-700'}`}>
          Férias
        </button>
        <button onClick={() => setActiveTab('folgas')} className={`px-4 py-2 rounded ${activeTab === 'folgas' ? 'bg-purple-600' : 'bg-purple-700'}`}>
          Folgas
        </button>
      </nav>

      {/* Conteúdo das abas */}
      <main className="container mx-auto px-4 py-6">
        {activeTab === 'ponto' && (
          <div>
            <h2 className="text-2xl font-bold mb-4">Registro de Ponto</h2>
            <p>Aqui ficará a funcionalidade de registro de ponto.</p>
          </div>
        )}

        {activeTab === 'horasExtras' && (
          <div>
            <h2 className="text-2xl font-bold mb-4">Horas Extras</h2>
            <p>Aqui ficará o controle de horas extras.</p>
          </div>
        )}

        {activeTab === 'ferias' && (
          <div>
            <h2 className="text-2xl font-bold mb-4">Férias</h2>
            <p>Aqui ficará o controle de férias.</p>
          </div>
        )}

        {activeTab === 'folgas' && (
          <div>
            <h2 className="text-2xl font-bold mb-4">Folgas</h2>
            <p>Aqui ficará o controle de folgas.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default DashboardPage;
