import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import HorasExtrasPage from './HorasExtrasPage';

// Layout principal com menu de navegação
const Layout = ({ children }) => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-900 to-indigo-900 text-white">
      <nav className="bg-purple-800 bg-opacity-70 p-4">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <div className="text-xl font-bold">Sistema de Gestão</div>
          <ul className="flex space-x-6">
            <li>
              <Link to="/" className="text-white hover:text-purple-300">
                Dashboard
              </Link>
            </li>
            <li>
              <Link to="/horas-extras" className="text-white hover:text-purple-300">
                Horas Extras
              </Link>
            </li>
            {/* Outros links do menu principal */}
          </ul>
        </div>
      </nav>
      <main className="p-4 md:p-8">
        <div className="max-w-7xl mx-auto">
          {children}
        </div>
      </main>
    </div>
  );
};

// Página Dashboard
const Dashboard = () => (
  <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
    <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
      <h2 className="text-xl font-bold mb-4">Funções Administrativas</h2>
      <ul className="space-y-2">
        <li>
          <Link to="/horas-extras" className="text-purple-300 hover:text-white flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            Gerenciamento de Horas Extras
          </Link>
        </li>
        {/* Adicione outros links aqui conforme necessário */}
      </ul>
    </div>
  </div>
);

// Página de Horas Extras como componente direto
const HorasExtrasStandalone = () => {
  return <HorasExtrasTab />;
};

// Importe o componente HorasExtrasTab diretamente
import HorasExtrasTab from './HorasExtrasTab';

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={
          <Layout>
            <Dashboard />
          </Layout>
        } />
        
        <Route path="/horas-extras" element={
          <Layout>
            <HorasExtrasTab />
          </Layout>
        } />
        
        {/* Rota alternativa direta para testes */}
        <Route path="/horas-extras-page" element={<HorasExtrasPage />} />
        
        {/* Rota para página não encontrada */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
