import React, { useState, useEffect } from 'react';

const JornadasTab = () => {
  // Estado para armazenar funcionários
  const [employees, setEmployees] = useState(() => {
    const storedEmployees = localStorage.getItem('employees');
    return storedEmployees ? JSON.parse(storedEmployees) : [
      { id: 1, name: 'João Silva', email: 'joao.silva@empresa.com', department: 'TI' },
      { id: 2, name: 'Maria Oliveira', email: 'maria.oliveira@empresa.com', department: 'RH' },
      { id: 3, name: 'Carlos Pereira', email: 'carlos.pereira@empresa.com', department: 'Financeiro' },
      { id: 4, name: 'Ana Santos', email: 'ana.santos@empresa.com', department: 'Marketing' },
      { id: 5, name: 'Bruno Almeida', email: 'bruno.almeida@empresa.com', department: 'Operações' }
    ];
  });
  
  // Estado para armazenar jornadas de trabalho
  const [jornadas, setJornadas] = useState(() => {
    const storedJornadas = localStorage.getItem('jornadas');
    return storedJornadas ? JSON.parse(storedJornadas) : [
      { 
        id: 1, 
        employeeId: 1, 
        employeeName: 'João Silva', 
        tipo: 'padrão', 
        horasDiarias: 8,
        diasSemana: ['segunda', 'terça', 'quarta', 'quinta', 'sexta'],
        horarios: { entrada: '08:00', saidaAlmoco: '12:00', retornoAlmoco: '13:00', saida: '17:00' },
        tolerancia: 15,
        notified: true
      },
      { 
        id: 2, 
        employeeId: 2, 
        employeeName: 'Maria Oliveira', 
        tipo: 'padrão', 
        horasDiarias: 8,
        diasSemana: ['segunda', 'terça', 'quarta', 'quinta', 'sexta'],
        horarios: { entrada: '09:00', saidaAlmoco: '12:00', retornoAlmoco: '13:00', saida: '18:00' },
        tolerancia: 10,
        notified: true
      },
      { 
        id: 3, 
        employeeId: 3, 
        employeeName: 'Carlos Pereira', 
        tipo: 'flexível', 
        horasDiarias: 8,
        diasSemana: ['segunda', 'terça', 'quarta', 'quinta', 'sexta'],
        horarios: { entrada: '08:00', saidaAlmoco: '12:00', retornoAlmoco: '13:00', saida: '17:00' },
        tolerancia: 30,
        notified: true
      },
      { 
        id: 4, 
        employeeId: 4, 
        employeeName: 'Ana Santos', 
        tipo: 'alternativo', 
        horasDiarias: 6,
        diasSemana: ['segunda', 'terça', 'quarta', 'quinta', 'sexta', 'sábado'],
        horarios: { entrada: '14:00', saidaAlmoco: null, retornoAlmoco: null, saida: '20:00' },
        tolerancia: 15,
        notified: true
      },
      { 
        id: 5, 
        employeeId: 5, 
        employeeName: 'Bruno Almeida', 
        tipo: 'plantão', 
        horasDiarias: 12,
        diasSemana: ['segunda', 'quarta', 'sexta'],
        horarios: { entrada: '07:00', saidaAlmoco: '12:00', retornoAlmoco: '13:00', saida: '19:00' },
        tolerancia: 15,
        notified: true
      }
    ];
  });
  
  // Estados para modais e filtros
  const [showJornadaModal, setShowJornadaModal] = useState(false);
  const [showJornadaDetailsModal, setShowJornadaDetailsModal] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [selectedJornada, setSelectedJornada] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilter, setActiveFilter] = useState('todos');
  
  // State para nova jornada
  const [newJornada, setNewJornada] = useState({
    tipo: 'padrão',
    horasDiarias: 8,
    diasSemana: ['segunda', 'terça', 'quarta', 'quinta', 'sexta'],
    horarios: { entrada: '08:00', saidaAlmoco: '12:00', retornoAlmoco: '13:00', saida: '17:00' },
    tolerancia: 15
  });
  
  // Salvar dados no localStorage quando mudarem
  useEffect(() => {
    localStorage.setItem('jornadas', JSON.stringify(jornadas));
    
    // Atualizar notificações dos funcionários quando jornadas forem atualizadas
    const pendingNotifications = jornadas
      .filter(jornada => !jornada.notified)
      .map(jornada => {
        return {
          id: Date.now() + Math.random(),
          userId: jornada.employeeId,
          message: `Sua jornada de trabalho foi atualizada. Tipo: ${jornada.tipo}, Entrada: ${jornada.horarios.entrada}, Saída: ${jornada.horarios.saida}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
      });
    
    if (pendingNotifications.length > 0) {
      const currentNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      localStorage.setItem('userNotifications', JSON.stringify([
        ...pendingNotifications,
        ...currentNotifications
      ]));
      
      // Marcar como notificado
      setJornadas(jornadas.map(jornada => 
        !jornada.notified ? { ...jornada, notified: true } : jornada
      ));
    }
  }, [jornadas]);
  
  // Filtrar jornadas com base no filtro ativo e termo de busca
  const filteredJornadas = jornadas.filter(jornada => {
    const matchesFilter = 
      (activeFilter === 'padrao' && jornada.tipo === 'padrão') ||
      (activeFilter === 'flexivel' && jornada.tipo === 'flexível') ||
      (activeFilter === 'alternativo' && jornada.tipo === 'alternativo') ||
      (activeFilter === 'plantao' && jornada.tipo === 'plantão') ||
      (activeFilter === 'todos');
    
    const searchLower = searchTerm.toLowerCase();
    const matchesSearch = 
      !searchTerm || 
      jornada.employeeName.toLowerCase().includes(searchLower);
    
    return matchesFilter && matchesSearch;
  });
  
  // Filtrar funcionários com base no termo de busca
  const filteredEmployees = employees.filter(employee => {
    const searchLower = searchTerm.toLowerCase();
    return !searchTerm || 
      employee.name.toLowerCase().includes(searchLower) ||
      employee.department.toLowerCase().includes(searchLower);
  });
  
  // Função para abrir modal de detalhes da jornada
  const openJornadaDetailsModal = (jornada) => {
    setSelectedJornada(jornada);
    setShowJornadaDetailsModal(true);
  };
  
  // Função para abrir modal de criação/edição de jornada
  const openJornadaModal = (employee) => {
    setSelectedEmployee(employee);
    
    // Verificar se já existe uma jornada para esse funcionário
    const jornadaExistente = jornadas.find(j => j.employeeId === employee.id);
    
    if (jornadaExistente) {
      // Se existir, mostrar os dados existentes
      setNewJornada({
        tipo: jornadaExistente.tipo,
        horasDiarias: jornadaExistente.horasDiarias,
        diasSemana: jornadaExistente.diasSemana,
        horarios: jornadaExistente.horarios,
        tolerancia: jornadaExistente.tolerancia
      });
    } else {
      // Se não existir, usar valores padrão
      setNewJornada({
        tipo: 'padrão',
        horasDiarias: 8,
        diasSemana: ['segunda', 'terça', 'quarta', 'quinta', 'sexta'],
        horarios: { entrada: '08:00', saidaAlmoco: '12:00', retornoAlmoco: '13:00', saida: '17:00' },
        tolerancia: 15
      });
    }
    
    setShowJornadaModal(true);
  };
  
  // Função para salvar/atualizar jornada
  const saveJornada = () => {
    // Verificar se já existe uma jornada para esse funcionário
    const jornadaExistente = jornadas.find(j => j.employeeId === selectedEmployee.id);
    
    if (jornadaExistente) {
      // Atualizar jornada existente
      setJornadas(jornadas.map(j => 
        j.employeeId === selectedEmployee.id 
          ? { 
              ...j, 
              tipo: newJornada.tipo,
              horasDiarias: newJornada.horasDiarias,
              diasSemana: newJornada.diasSemana,
              horarios: newJornada.horarios,
              tolerancia: newJornada.tolerancia,
              notified: false
            } 
          : j
      ));
    } else {
      // Criar nova jornada
      const novaJornada = {
        id: jornadas.length + 1,
        employeeId: selectedEmployee.id,
        employeeName: selectedEmployee.name,
        tipo: newJornada.tipo,
        horasDiarias: newJornada.horasDiarias,
        diasSemana: newJornada.diasSemana,
        horarios: newJornada.horarios,
        tolerancia: newJornada.tolerancia,
        notified: false
      };
      
      setJornadas([...jornadas, novaJornada]);
    }
    
    setShowJornadaModal(false);
  };
  
  // Manipuladores para os inputs do formulário
  const handleJornadaChange = (e) => {
    const { name, value } = e.target;
    setNewJornada(prev => ({ ...prev, [name]: value }));
  };
  
  // Manipulador para checkbox de dias da semana
  const handleDiaSemanaChange = (dia) => {
    setNewJornada(prev => {
      if (prev.diasSemana.includes(dia)) {
        return { ...prev, diasSemana: prev.diasSemana.filter(d => d !== dia) };
      } else {
        return { ...prev, diasSemana: [...prev.diasSemana, dia] };
      }
    });
  };
  
  // Manipulador para horários
  const handleHorarioChange = (e) => {
    const { name, value } = e.target;
    setNewJornada(prev => ({
      ...prev,
      horarios: {
        ...prev.horarios,
        [name]: value
      }
    }));
  };
  
  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h1 className="text-2xl font-bold mb-1">Jornadas de Trabalho</h1>
          <p className="text-purple-300">Configure as jornadas de trabalho dos funcionários</p>
        </div>
        
        <div className="flex flex-col sm:flex-row gap-3">
          <div className="relative">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Buscar por nome, departamento..."
              className="w-full sm:w-64 px-3 py-2 pl-10 bg-purple-800 bg-opacity-40 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 absolute left-3 top-2.5 text-purple-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
        </div>
      </div>
      
      {/* Filtros */}
      <div className="bg-purple-800 bg-opacity-40 rounded-lg p-1 flex flex-wrap space-x-1">
        <button
          onClick={() => setActiveFilter('todos')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'todos' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Todos
        </button>
        <button
          onClick={() => setActiveFilter('padrao')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'padrao' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Padrão
        </button>
        <button
          onClick={() => setActiveFilter('flexivel')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'flexivel' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Flexível
        </button>
        <button
          onClick={() => setActiveFilter('alternativo')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'alternativo' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Alternativo
        </button>
        <button
          onClick={() => setActiveFilter('plantao')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'plantao' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Plantão
        </button>
        <button
          onClick={() => setActiveFilter('funcionarios')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'funcionarios' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Funcionários
        </button>
      </div>
      
      {/* Conteúdo baseado na tab ativa */}
      {activeFilter !== 'funcionarios' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
          <div className="p-4 border-b border-purple-700 flex justify-between items-center">
            <h2 className="text-xl font-semibold">Jornadas Cadastradas</h2>
          </div>
          
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm">
                  <th className="text-left p-4">Funcionário</th>
                  <th className="text-left p-4">Tipo</th>
                  <th className="text-left p-4">Horas Diárias</th>
                  <th className="text-left p-4">Entrada</th>
                  <th className="text-left p-4">Saída</th>
                  <th className="text-left p-4">Dias da Semana</th>
                  <th className="text-left p-4">Ações</th>
                </tr>
              </thead>
              <tbody>
                {filteredJornadas.length === 0 ? (
                  <tr>
                    <td colSpan={7} className="p-4 text-center">Nenhuma jornada encontrada.</td>
                  </tr>
                ) : (
                  filteredJornadas.map(jornada => (
                    <tr key={jornada.id} className="border-t border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                      <td className="p-4">{jornada.employeeName}</td>
                      <td className="p-4">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs 
                          ${jornada.tipo === 'padrão' ? 'bg-blue-600' : 
                            jornada.tipo === 'flexível' ? 'bg-green-600' : 
                              jornada.tipo === 'alternativo' ? 'bg-purple-600' : 'bg-orange-600'}`}
                        >
                          {jornada.tipo.toUpperCase()}
                        </span>
                      </td>
                      <td className="p-4">{jornada.horasDiarias}h</td>
                      <td className="p-4">{jornada.horarios.entrada}</td>
                      <td className="p-4">{jornada.horarios.saida}</td>
                      <td className="p-4">{jornada.diasSemana.length} dias</td>
                      <td className="p-4">
                        <div className="flex space-x-2">
                          <button
                            onClick={() => openJornadaDetailsModal(jornada)}
                            className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded-md text-sm"
                          >
                            Detalhes
                          </button>
                          <button
                            onClick={() => openJornadaModal(employees.find(e => e.id === jornada.employeeId))}
                            className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded-md text-sm"
                          >
                            Editar
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
      
      {activeFilter === 'funcionarios' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
          <div className="p-4 border-b border-purple-700">
            <h2 className="text-xl font-semibold">Lista de Funcionários</h2>
            <p className="text-purple-300 text-sm">Configure jornadas por funcionário</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
            {filteredEmployees.map(employee => {
              // Encontrar jornada do funcionário
              const jornadaFuncionario = jornadas.find(j => j.employeeId === employee.id);
              
              return (
                <div key={employee.id} className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                  <h3 className="font-bold text-lg">{employee.name}</h3>
                  <p className="text-purple-300">{employee.department}</p>
                  <p className="text-sm mb-4">{employee.email}</p>
                  
                  {jornadaFuncionario ? (
                    <div className="mb-4">
                      <p className="text-sm text-purple-300">Jornada Atual:</p>
                      <p className="text-md font-bold">{jornadaFuncionario.tipo.toUpperCase()}</p>
                      <p className="text-sm">Entrada: {jornadaFuncionario.horarios.entrada} - Saída: {jornadaFuncionario.horarios.saida}</p>
                    </div>
                  ) : (
                    <p className="text-sm mb-4 text-yellow-300">Sem jornada definida</p>
                  )}
                  
                  <button
                    onClick={() => openJornadaModal(employee)}
                    className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded text-sm w-full"
                  >
                    {jornadaFuncionario ? 'Editar Jornada' : 'Definir Jornada'}
                  </button>
                </div>
              );
            })}
          </div>
        </div>
      )}
      
      {/* Modal para visualizar detalhes da jornada */}
      {showJornadaDetailsModal && selectedJornada && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">Detalhes da Jornada</h3>
            
            <div className="mb-4">
              <p><span className="text-purple-300">Funcionário:</span> {selectedJornada.employeeName}</p>
              <p><span className="text-purple-300">Tipo:</span> {selectedJornada.tipo.toUpperCase()}</p>
              <p><span className="text-purple-300">Horas Diárias:</span> {selectedJornada.horasDiarias}h</p>
              <p><span className="text-purple-300">Tolerância:</span> {selectedJornada.tolerancia} minutos</p>
            </div>
            
            <div className="mb-4">
              <p className="text-purple-300 mb-1">Horários:</p>
              <div className="bg-purple-800 bg-opacity-40 p-3 rounded-md">
                <p><span className="text-purple-300">Entrada:</span> {selectedJornada.horarios.entrada}</p>
                {selectedJornada.horarios.saidaAlmoco && (
                  <>
                    <p><span className="text-purple-300">Saída para Almoço:</span> {selectedJornada.horarios.saidaAlmoco}</p>
                    <p><span className="text-purple-300">Retorno do Almoço:</span> {selectedJornada.horarios.retornoAlmoco}</p>
                  </>
                )}
                <p><span className="text-purple-300">Saída:</span> {selectedJornada.horarios.saida}</p>
              </div>
            </div>
            
            <div className="mb-6">
              <p className="text-purple-300 mb-1">Dias da Semana:</p>
              <div className="flex flex-wrap gap-2">
                {selectedJornada.diasSemana.map(dia => (
                  <span key={dia} className="bg-purple-700 px-2 py-1 rounded-md text-xs">
                    {dia.charAt(0).toUpperCase() + dia.slice(1)}
                  </span>
                ))}
              </div>
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowJornadaDetailsModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Fechar
              </button>
              <button 
                onClick={() => {
                  setShowJornadaDetailsModal(false);
                  openJornadaModal(employees.find(e => e.id === selectedJornada.employeeId));
                }}
                className="px-4 py-2 bg-purple-600 hover:bg-purple-500 rounded-md"
              >
                Editar
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal para criar/editar jornada */}
      {showJornadaModal && selectedEmployee && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md max-h-[90vh] overflow-y-auto">
            <h3 className="text-xl font-bold mb-4">
              {jornadas.find(j => j.employeeId === selectedEmployee.id) ? 'Editar' : 'Definir'} Jornada para {selectedEmployee.name}
            </h3>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Tipo de Jornada</label>
              <select
                name="tipo"
                value={newJornada.tipo}
                onChange={handleJornadaChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value="padrão">Padrão</option>
                <option value="flexível">Flexível</option>
                <option value="alternativo">Alternativo</option>
                <option value="plantão">Plantão</option>
              </select>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Horas Diárias</label>
              <input 
                type="number" 
                name="horasDiarias"
                value={newJornada.horasDiarias}
                onChange={handleJornadaChange}
                min="1"
                max="24"
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Tolerância (minutos)</label>
              <input 
                type="number" 
                name="tolerancia"
                value={newJornada.tolerancia}
                onChange={handleJornadaChange}
                min="0"
                max="60"
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Dias da Semana</label>
              <div className="grid grid-cols-3 gap-2">
                {['domingo', 'segunda', 'terça', 'quarta', 'quinta', 'sexta', 'sábado'].map(dia => (
                  <label key={dia} className="flex items-center space-x-2">
                    <input 
                      type="checkbox" 
                      checked={newJornada.diasSemana.includes(dia)}
                      onChange={() => handleDiaSemanaChange(dia)}
                      className="rounded border-purple-600 text-purple-600 focus:ring-purple-500 bg-purple-800"
                    />
                    <span>{dia.charAt(0).toUpperCase() + dia.slice(1)}</span>
                  </label>
                ))}
              </div>
            </div>
            
            <div className="mb-6">
              <label className="block text-sm font-medium mb-2">Horários</label>
              <div className="space-y-3">
                <div>
                  <label className="block text-xs text-purple-300 mb-1">Entrada</label>
                  <input 
                    type="time" 
                    name="entrada"
                    value={newJornada.horarios.entrada}
                    onChange={handleHorarioChange}
                    className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  />
                </div>
                
                {newJornada.tipo !== 'alternativo' && (
                  <>
                    <div>
                      <label className="block text-xs text-purple-300 mb-1">Saída para Almoço</label>
                      <input 
                        type="time" 
                        name="saidaAlmoco"
                        value={newJornada.horarios.saidaAlmoco || ''}
                        onChange={handleHorarioChange}
                        className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                      />
                    </div>
                    
                    <div>
                      <label className="block text-xs text-purple-300 mb-1">Retorno do Almoço</label>
                      <input 
                        type="time" 
                        name="retornoAlmoco"
                        value={newJornada.horarios.retornoAlmoco || ''}
                        onChange={handleHorarioChange}
                        className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                      />
                    </div>
                  </>
                )}
                
                <div>
                  <label className="block text-xs text-purple-300 mb-1">Saída</label>
                  <input 
                    type="time" 
                    name="saida"
                    value={newJornada.horarios.saida}
                    onChange={handleHorarioChange}
                    className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  />
                </div>
              </div>
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowJornadaModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={saveJornada}
                className="px-4 py-2 bg-purple-600 hover:bg-purple-500 rounded-md"
              >
                Salvar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default JornadasTab;
