import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Modal, Form, Button } from 'react-bootstrap';
import { format, parseISO } from 'date-fns';
import ptBR from 'date-fns/locale/pt-BR';

const AdminPage = () => {
  const navigate = useNavigate();
  
  // Estado de autenticação e usuário
  const [userData, setUserData] = useState({
    name: 'Administrador',
    initials: 'AD',
    isAdmin: true
  });
  
  // Estado para a lista de funcionários
  const [funcionarios, setFuncionarios] = useState([]);
  const [funcionarioSelecionado, setFuncionarioSelecionado] = useState(null);
  
  // Estados para os tabs
  const [activeTab, setActiveTab] = useState('overtimes');
  
  // Estados para contadores
  const [contadores, setContadores] = useState({
    horasExtrasPendentes: 0,
    feriasPendentes: 0,
    folgasPendentes: 0,
    solicitacoesPendentes: 0
  });
  
  // Estados para horas extras
  const [overtimeEntries, setOvertimeEntries] = useState([]);
  const [newOvertime, setNewOvertime] = useState({
    funcionarioId: '',
    dataHoraInicio: '',
    dataHoraFim: '',
    justificativa: '',
    tipo: 'NORMAL',
    paraCompensacao: false,
    paraRemuneracao: true
  });
  const [showOvertimeModal, setShowOvertimeModal] = useState(false);
  
  // Estados para férias
  const [vacationRequests, setVacationRequests] = useState([]);
  const [newVacation, setNewVacation] = useState({
    funcionarioId: '',
    dataInicio: '',
    dataFim: '',
    diasUteis: 0,
    periodoAquisitivo: new Date().getFullYear(),
    adiantamento13: false,
    vendaUmTerco: false,
    observacao: ''
  });
  const [showVacationModal, setShowVacationModal] = useState(false);
  
  // Estados para folgas
  const [dayOffRequests, setDayOffRequests] = useState([]);
  const [newDayOff, setNewDayOff] = useState({
    funcionarioId: '',
    dataInicio: '',
    dataFim: '',
    tipoFolga: 'COMPENSATORIA',
    motivo: ''
  });
  const [showDayOffModal, setShowDayOffModal] = useState(false);
  
  // Estados para modais de resposta
  const [showRespostaModal, setShowRespostaModal] = useState(false);
  const [itemParaResponder, setItemParaResponder] = useState(null);
  const [respostaTexto, setRespostaTexto] = useState('');
  
  // Estado para solicitações de revisão
  const [solicitacoesRevisao, setSolicitacoesRevisao] = useState([]);
  
  // Estado para mostrar/esconder o menu do perfil
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  
  // Carregar dados do dashboard e verificar autenticação
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    
    // Obter dados do dashboard
    axios.get('/api/admin/gestao-tempo/dashboard', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        const data = response.data;
        
        // Atualizar lista de funcionários
        setFuncionarios(data.funcionarios || []);
        
        // Atualizar contadores
        setContadores({
          horasExtrasPendentes: data.horasExtrasPendentes || 0,
          feriasPendentes: data.feriasPendentes || 0,
          folgasPendentes: data.folgasPendentes || 0,
          solicitacoesPendentes: data.solicitacoesRevisaoPendentes || 0,
          pontosNaoValidados: data.pontosNaoValidados || 0
        });
        
        // Carregar pendências
        carregarPendencias();
      })
      .catch(error => {
        console.error('Erro ao carregar dashboard:', error);
        if (error.response && error.response.status === 403) {
          alert('Você não tem permissão para acessar esta página');
          navigate('/');
        }
      });
      
    // Carregar dados do usuário do localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUserData({
          name: parsedUser.nome || 'Administrador',
          initials: getInitials(parsedUser.nome),
          isAdmin: true
        });
      } catch (error) {
        console.error('Erro ao analisar dados do usuário:', error);
      }
    }
  }, [navigate]);
  
  // Função para carregar todas as pendências
  const carregarPendencias = () => {
    const token = localStorage.getItem('token');
    if (!token) return;
    
    const headers = { Authorization: `Bearer ${token}` };
    
    // Carregar horas extras pendentes
    axios.get('/api/admin/gestao-tempo/horas-extras/pendentes', { headers })
      .then(response => {
        setOvertimeEntries(response.data.map(item => ({
          id: item.id,
          funcionarioId: item.funcionarioId,
          nomeFuncionario: item.nomeFuncionario,
          date: format(parseISO(item.dataHoraInicio), 'dd/MM/yyyy'),
          hours: item.minutosTotais / 60,
          status: item.status.toLowerCase(),
          reason: item.justificativa
        })));
      })
      .catch(error => console.error('Erro ao carregar horas extras:', error));
    
    // Carregar férias pendentes
    axios.get('/api/admin/gestao-tempo/ferias/pendentes', { headers })
      .then(response => {
        setVacationRequests(response.data.map(item => ({
          id: item.id,
          funcionarioId: item.funcionarioId,
          nomeFuncionario: item.nomeFuncionario,
          startDate: format(parseISO(item.dataInicio), 'dd/MM/yyyy'),
          endDate: format(parseISO(item.dataFim), 'dd/MM/yyyy'),
          status: item.status.toLowerCase(),
          workingDays: item.diasUteis
        })));
      })
      .catch(error => console.error('Erro ao carregar férias:', error));
    
    // Carregar folgas pendentes
    axios.get('/api/admin/gestao-tempo/folgas/pendentes', { headers })
      .then(response => {
        setDayOffRequests(response.data.map(item => ({
          id: item.id,
          funcionarioId: item.funcionarioId,
          nomeFuncionario: item.nomeFuncionario,
          date: format(parseISO(item.dataInicio), 'dd/MM/yyyy'),
          endDate: item.dataInicio !== item.dataFim ? format(parseISO(item.dataFim), 'dd/MM/yyyy') : null,
          reason: item.motivo,
          status: item.status.toLowerCase(),
          tipoFolga: item.tipoFolga
        })));
      })
      .catch(error => console.error('Erro ao carregar folgas:', error));
    
    // Carregar solicitações de revisão
    axios.get('/api/admin/gestao-tempo/solicitacoes/abertas', { headers })
      .then(response => {
        setSolicitacoesRevisao(response.data || []);
      })
      .catch(error => console.error('Erro ao carregar solicitações:', error));
  };
  
  // Função para selecionar um funcionário específico
  const selecionarFuncionario = (funcionarioId) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    
    axios.get(`/api/admin/gestao-tempo/funcionario/${funcionarioId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        const data = response.data;
        setFuncionarioSelecionado(data.funcionario);
        
        // Atualizar dados específicos por tab
        if (data.horasExtras) {
          setOvertimeEntries(data.horasExtras.map(he => ({
            id: he.id,
            funcionarioId: he.funcionarioId,
            nomeFuncionario: he.nomeFuncionario,
            date: format(parseISO(he.dataHoraInicio), 'dd/MM/yyyy'),
            hours: he.minutosTotais / 60,
            status: he.status.toLowerCase(),
            reason: he.justificativa
          })));
        }
        
        if (data.ferias) {
          setVacationRequests(data.ferias.map(f => ({
            id: f.id,
            funcionarioId: f.funcionarioId,
            nomeFuncionario: f.nomeFuncionario,
            startDate: format(parseISO(f.dataInicio), 'dd/MM/yyyy'),
            endDate: format(parseISO(f.dataFim), 'dd/MM/yyyy'),
            status: f.status.toLowerCase(),
            workingDays: f.diasUteis
          })));
        }
        
        if (data.folgas) {
          setDayOffRequests(data.folgas.map(f => ({
            id: f.id,
            funcionarioId: f.funcionarioId,
            nomeFuncionario: f.nomeFuncionario,
            date: format(parseISO(f.dataInicio), 'dd/MM/yyyy'),
            endDate: f.dataInicio !== f.dataFim ? format(parseISO(f.dataFim), 'dd/MM/yyyy') : null,
            reason: f.motivo,
            status: f.status.toLowerCase(),
            tipoFolga: f.tipoFolga
          })));
        }
        
        if (data.solicitacoes) {
          setSolicitacoesRevisao(data.solicitacoes);
        }
      })
      .catch(error => {
        console.error('Erro ao carregar dados do funcionário:', error);
      });
  };
  
  // Função para calcular dias úteis entre duas datas
  const calculateWorkingDays = (startDate, endDate) => {
    if (!startDate || !endDate) return 0;
    
    const start = typeof startDate === 'string' ? parseDate(startDate) : startDate;
    const end = typeof endDate === 'string' ? parseDate(endDate) : endDate;
    
    let count = 0;
    const current = new Date(start);
    
    while (current <= end) {
      const dayOfWeek = current.getDay();
      if (dayOfWeek !== 0 && dayOfWeek !== 6) {
        // Não é sábado nem domingo
        count++;
      }
      current.setDate(current.getDate() + 1);
    }
    
    return count;
  };
  
  // Helper para parsear data
  const parseDate = (dateString) => {
    if (!dateString) return null;
    
    if (dateString.includes('-')) {
      // Formato ISO
      return new Date(dateString);
    } else {
      // Formato dd/mm/yyyy
      const [day, month, year] = dateString.split('/').map(Number);
      return new Date(year, month - 1, day);
    }
  };
  
  // Pega as iniciais do nome para o avatar
  const getInitials = (name) => {
    if (!name) return 'AD';
    const nameParts = name.split(' ');
    if (nameParts.length === 1) return nameParts[0].charAt(0).toUpperCase();
    return (nameParts[0].charAt(0) + nameParts[nameParts.length - 1].charAt(0)).toUpperCase();
  };
  
  // Adicionar nova hora extra
  const handleAddOvertime = (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    if (!token) return;
    
    if (funcionarioSelecionado && !newOvertime.funcionarioId) {
      newOvertime.funcionarioId = funcionarioSelecionado.id;
    }
    
    axios.post('/api/admin/gestao-tempo/horas-extras', newOvertime, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        const horaExtra = response.data;
        
        // Adicionar à lista
        const novaHoraExtra = {
          id: horaExtra.id,
          funcionarioId: horaExtra.funcionarioId,
          nomeFuncionario: horaExtra.nomeFuncionario,
          date: format(parseISO(horaExtra.dataHoraInicio), 'dd/MM/yyyy'),
          hours: horaExtra.minutosTotais / 60,
          status: horaExtra.status.toLowerCase(),
          reason: horaExtra.justificativa
        };
        
        setOvertimeEntries([novaHoraExtra, ...overtimeEntries]);
        setShowOvertimeModal(false);
        setNewOvertime({
          funcionarioId: '',
          dataHoraInicio: '',
          dataHoraFim: '',
          justificativa: '',
          tipo: 'NORMAL',
          paraCompensacao: false,
          paraRemuneracao: true
        });
        
        alert('Hora extra registrada com sucesso!');
      })
      .catch(error => {
        console.error('Erro ao registrar hora extra:', error);
        alert('Falha ao registrar hora extra: ' + (error.response?.data || 'Erro desconhecido'));
      });
  };
  
  // Adicionar nova solicitação de férias
  const handleAddVacation = (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    if (!token) return;
    
    if (funcionarioSelecionado && !newVacation.funcionarioId) {
      newVacation.funcionarioId = funcionarioSelecionado.id;
    }
    
    // Calcular dias úteis se não fornecido
    if (!newVacation.diasUteis && newVacation.dataInicio && newVacation.dataFim) {
      newVacation.diasUteis = calculateWorkingDays(newVacation.dataInicio, newVacation.dataFim);
    }
    
    axios.post('/api/admin/gestao-tempo/ferias', newVacation, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        const ferias = response.data;
        
        // Adicionar à lista
        const novaFerias = {
          id: ferias.id,
          funcionarioId: ferias.funcionarioId,
          nomeFuncionario: ferias.nomeFuncionario,
          startDate: format(parseISO(ferias.dataInicio), 'dd/MM/yyyy'),
          endDate: format(parseISO(ferias.dataFim), 'dd/MM/yyyy'),
          status: ferias.status.toLowerCase(),
          workingDays: ferias.diasUteis
        };
        
        setVacationRequests([novaFerias, ...vacationRequests]);
        setShowVacationModal(false);
        setNewVacation({
          funcionarioId: '',
          dataInicio: '',
          dataFim: '',
          diasUteis: 0,
          periodoAquisitivo: new Date().getFullYear(),
          adiantamento13: false,
          vendaUmTerco: false,
          observacao: ''
        });
        
        alert('Férias registradas com sucesso!');
      })
      .catch(error => {
        console.error('Erro ao registrar férias:', error);
        alert('Falha ao registrar férias: ' + (error.response?.data || 'Erro desconhecido'));
      });
  };
  
  // Adicionar nova solicitação de folga
  const handleAddDayOff = (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    if (!token) return;
    
    if (funcionarioSelecionado && !newDayOff.funcionarioId) {
      newDayOff.funcionarioId = funcionarioSelecionado.id;
    }
    
    axios.post('/api/admin/gestao-tempo/folgas', newDayOff, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        const folga = response.data;
        
        // Adicionar à lista
        const novaFolga = {
          id: folga.id,
          funcionarioId: folga.funcionarioId,
          nomeFuncionario: folga.nomeFuncionario,
          date: format(parseISO(folga.dataInicio), 'dd/MM/yyyy'),
          endDate: folga.dataInicio !== folga.dataFim ? format(parseISO(folga.dataFim), 'dd/MM/yyyy') : null,
          reason: folga.motivo,
          status: folga.status.toLowerCase(),
          tipoFolga: folga.tipoFolga
        };
        
        setDayOffRequests([novaFolga, ...dayOffRequests]);
        setShowDayOffModal(false);
        setNewDayOff({
          funcionarioId: '',
          dataInicio: '',
          dataFim: '',
          tipoFolga: 'COMPENSATORIA',
          motivo: ''
        });
        
        alert('Folga registrada com sucesso!');
      })
      .catch(error => {
        console.error('Erro ao registrar folga:', error);
        alert('Falha ao registrar folga: ' + (error.response?.data || 'Erro desconhecido'));
      });
  };
  
  // Preparar para responder a um item
  const prepararResposta = (type, id, acao) => {
    setItemParaResponder({ type, id, acao });
    setRespostaTexto('');
    setShowRespostaModal(true);
  };
  
  // Enviar resposta/aprovar/rejeitar
  const enviarResposta = () => {
    if (!itemParaResponder) return;
    
    const token = localStorage.getItem('token');
    if (!token) return;
    
    const { type, id, acao } = itemParaResponder;
    let endpoint = '';
    const data = { observacao: respostaTexto };
    
    // Determinar endpoint com base no tipo e ação
    if (type === 'overtime') {
      endpoint = `/api/admin/gestao-tempo/horas-extras/${id}/${acao}`;
    } else if (type === 'vacation') {
      endpoint = `/api/admin/gestao-tempo/ferias/${id}/${acao}`;
    } else if (type === 'dayoff') {
      endpoint = `/api/admin/gestao-tempo/folgas/${id}/${acao}`;
    } else if (type === 'solicitacao') {
      endpoint = `/api/admin/gestao-tempo/solicitacoes/${id}/responder`;
      data.aceitar = acao === 'aprovar';
      data.resposta = respostaTexto;
    }
    
    axios.patch(endpoint, data, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        setShowRespostaModal(false);
        
        // Atualizar estado local
        if (type === 'overtime') {
          const updated = overtimeEntries.map(item => {
            if (item.id === id) {
              return { ...item, status: acao === 'aprovar' ? 'aprovado' : 'rejeitado' };
            }
            return item;
          });
          setOvertimeEntries(updated);
        } else if (type === 'vacation') {
          const updated = vacationRequests.map(item => {
            if (item.id === id) {
              return { ...item, status: acao === 'aprovar' ? 'aprovado' : 'rejeitado' };
            }
            return item;
          });
          setVacationRequests(updated);
        } else if (type === 'dayoff') {
          const updated = dayOffRequests.map(item => {
            if (item.id === id) {
              return { ...item, status: acao === 'aprovar' ? 'aprovado' : 'rejeitado' };
            }
            return item;
          });
          setDayOffRequests(updated);
        } else if (type === 'solicitacao') {
          const updated = solicitacoesRevisao.filter(item => item.id !== id);
          setSolicitacoesRevisao(updated);
        }
        
        // Recarregar contadores
        axios.get('/api/admin/gestao-tempo/dashboard', {
          headers: { Authorization: `Bearer ${token}` }
        })
          .then(response => {
            const data = response.data;
            setContadores({
              horasExtrasPendentes: data.horasExtrasPendentes || 0,
              feriasPendentes: data.feriasPendentes || 0,
              folgasPendentes: data.folgasPendentes || 0,
              solicitacoesPendentes: data.solicitacoesRevisaoPendentes || 0
            });
          })
          .catch(error => console.error('Erro ao atualizar contadores:', error));
        
        alert('Resposta enviada com sucesso!');
      })
      .catch(error => {
        console.error('Erro ao enviar resposta:', error);
        alert('Falha ao processar solicitação: ' + (error.response?.data || 'Erro desconhecido'));
      });
  };
  
  // Função para fazer logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };
  
  // Função para voltar ao dashboard
  const goToDashboard = () => {
    navigate('/');
  };
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      {/* Header */}
      <header className="bg-purple-900 bg-opacity-80 shadow-lg sticky top-0 z-10">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center">
            <div className="bg-purple-600 rounded-full p-1 mr-2 cursor-pointer" onClick={goToDashboard}>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
              </svg>
            </div>
            <span className="text-xl font-bold">CuidaEmprego</span>
          </div>
          
          <div className="relative">
            <button 
              onClick={() => setShowProfileMenu(!showProfileMenu)}
              className="flex items-center space-x-2 focus:outline-none"
            >
              <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center">
                <span className="font-medium text-sm">{userData.initials}</span>
              </div>
              <span className="hidden md:inline-block">{userData.name}</span>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            
            {showProfileMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-purple-800 rounded-md shadow-lg py-1 z-20">
                <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Meu Perfil</a>
                <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Configurações</a>
                <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-sm hover:bg-purple-700">Sair</button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-6">
        {/* Seleção de funcionário */}
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4 mb-8">
          <h2 className="text-xl font-semibold mb-4">Selecione um funcionário</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {funcionarios.map(funcionario => (
              <div 
                key={funcionario.id}
                className={`p-4 rounded-lg cursor-pointer transition-all ${
                  funcionarioSelecionado?.id === funcionario.id 
                    ? 'bg-purple-600' 
                    : 'bg-purple-900 bg-opacity-60 hover:bg-purple-700'
                }`}
                onClick={() => selecionarFuncionario(funcionario.id)}
              >
                <div className="flex items-center">
                  <div className="w-10 h-10 bg-purple-500 rounded-full flex items-center justify-center mr-3">
                    <span className="font-medium text-sm">
                      {funcionario.nome.split(' ').map(n => n[0]).join('').toUpperCase().substring(0, 2)}
                    </span>
                  </div>
                  <div>
                    <p className="font-medium">{funcionario.nome}</p>
                    <p className="text-sm text-purple-300">{funcionario.cargo}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
              
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4 mb-8">
          <h1 className="text-2xl font-bold mb-6">Gerenciamento de Horas Extras, Férias e Folgas</h1>
          
          {/* Tabs */}
          <div className="flex border-b border-purple-700 mb-6">
            <button 
              className={`px-4 py-2 mr-2 focus:outline-none ${activeTab === 'overtimes' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
              onClick={() => setActiveTab('overtimes')}
            >
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                Horas Extras
                {contadores.horasExtrasPendentes > 0 && (
                  <span className="ml-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">
                    {contadores.horasExtrasPendentes}
                  </span>
                )}
              </div>
            </button>
            <button 
              className={`px-4 py-2 mr-2 focus:outline-none ${activeTab === 'vacations' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
              onClick={() => setActiveTab('vacations')}
            >
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
                Férias
                {contadores.feriasPendentes > 0 && (
                  <span className="ml-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">
                    {contadores.feriasPendentes}
                  </span>
                )}
              </div>
            </button>
            <button 
              className={`px-4 py-2 mr-2 focus:outline-none ${activeTab === 'daysoff' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
              onClick={() => setActiveTab('daysoff')}
            >
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
                Folgas
                {contadores.folgasPendentes > 0 && (
                  <span className="ml-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">
                    {contadores.folgasPendentes}
                  </span>
                )}
              </div>
            </button>
            <button 
              className={`px-4 py-2 focus:outline-none ${activeTab === 'requests' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
              onClick={() => setActiveTab('requests')}
            >
              <div className="flex items-center">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z" />
                </svg>
                Solicitações
                {contadores.solicitacoesPendentes > 0 && (
                  <span className="ml-2 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs">
                    {contadores.solicitacoesPendentes}
                  </span>
                )}
              </div>
            </button>
          </div>
          
          {/* Conteúdo da Tab - Horas Extras */}
          {activeTab === 'overtimes' && (
            <div>
              <div className="mb-6">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-xl font-semibold">Registrar Nova Hora Extra</h2>
                  <button 
                    onClick={() => setShowOvertimeModal(true)}
                    className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg"
                  >
                    + Nova Hora Extra
                  </button>
                </div>
              </div>
              
              <div>
                <h2 className="text-xl font-semibold mb-4">Horas Extras {funcionarioSelecionado ? `de ${funcionarioSelecionado.nome}` : 'Pendentes'}</h2>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="text-purple-300 text-sm">
                        <th className="text-left p-2">Funcionário</th>
                        <th className="text-left p-2">Data</th>
                        <th className="text-left p-2">Horas</th>
                        <th className="text-left p-2">Motivo</th>
                        <th className="text-left p-2">Status</th>
                        <th className="text-left p-2">Ações</th>
                      </tr>
                    </thead>
                    <tbody>
                      {overtimeEntries.length === 0 ? (
                        <tr>
                          <td colSpan="6" className="text-center p-4">Nenhuma hora extra registrada</td>
                        </tr>
                      ) : (
                        overtimeEntries.map((entry, index) => (
                          <tr key={index} className="border-t border-purple-700">
                            <td className="p-2">{entry.nomeFuncionario || 'Não especificado'}</td>
                            <td className="p-2">{entry.date}</td>
                            <td className="p-2">{typeof entry.hours === 'number' ? entry.hours.toFixed(1) : entry.hours}h</td>
                            <td className="p-2">{entry.reason}</td>
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                                entry.status === 'aprovado' ? 'bg-green-600' : 
                                entry.status === 'rejeitado' ? 'bg-red-600' :
                                entry.status === 'detectado' ? 'bg-blue-600' : 'bg-yellow-600'
                              }`}>
                                {entry.status.toUpperCase()}
                              </span>
                            </td>
                            <td className="p-2">
                              {entry.status === 'pendente' && (
                                <div className="flex space-x-2">
                                  <button 
                                    onClick={() => prepararResposta('overtime', entry.id, 'aprovar')}
                                    className="bg-green-600 hover:bg-green-700 text-white text-xs px-2 py-1 rounded"
                                  >
                                    Aprovar
                                  </button>
                                  <button 
                                    onClick={() => prepararResposta('overtime', entry.id, 'rejeitar')}
                                    className="bg-red-600 hover:bg-red-700 text-white text-xs px-2 py-1 rounded"
                                  >
                                    Rejeitar
                                  </button>
                                </div>
                              )}
                              {entry.status === 'detectado' && (
                                <button 
                                  onClick={() => prepararResposta('overtime', entry.id, 'aprovar')}
                                  className="bg-green-600 hover:bg-green-700 text-white text-xs px-2 py-1 rounded"
                                >
                                  Confirmar
                                </button>
                              )}
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}
          
          {/* Conteúdo da Tab - Férias */}
          {activeTab === 'vacations' && (
            <div>
              <div className="mb-6">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-xl font-semibold">Registrar Férias</h2>
                  <button 
                    onClick={() => setShowVacationModal(true)}
                    className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg"
                  >
                    + Novas Férias
                  </button>
                </div>
              </div>
              
              <div>
                <h2 className="text-xl font-semibold mb-4">Férias {funcionarioSelecionado ? `de ${funcionarioSelecionado.nome}` : 'Pendentes'}</h2>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="text-purple-300 text-sm">
                        <th className="text-left p-2">Funcionário</th>
                        <th className="text-left p-2">Data de Início</th>
                        <th className="text-left p-2">Data de Término</th>
                        <th className="text-left p-2">Dias Úteis</th>
                        <th className="text-left p-2">Status</th>
                        <th className="text-left p-2">Ações</th>
                      </tr>
                    </thead>
                    <tbody>
                      {vacationRequests.length === 0 ? (
                        <tr>
                          <td colSpan="6" className="text-center p-4">Nenhuma férias registrada</td>
                        </tr>
                      ) : (
                        vacationRequests.map((request, index) => (
                          <tr key={index} className="border-t border-purple-700">
                            <td className="p-2">{request.nomeFuncionario || 'Não especificado'}</td>
                            <td className="p-2">{request.startDate}</td>
                            <td className="p-2">{request.endDate}</td>
                            <td className="p-2">{request.workingDays}</td>
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                                request.status === 'aprovado' ? 'bg-green-600' : 
                                request.status === 'rejeitado' ? 'bg-red-600' : 
                                request.status === 'concluido' ? 'bg-blue-600' : 'bg-yellow-600'
                              }`}>
                                {request.status.toUpperCase()}
                              </span>
                            </td>
                            <td className="p-2">
                              {request.status === 'pendente' && (
                                <div className="flex space-x-2">
                                  <button 
                                    onClick={() => prepararResposta('vacation', request.id, 'aprovar')}
                                    className="bg-green-600 hover:bg-green-700 text-white text-xs px-2 py-1 rounded"
                                  >
                                    Aprovar
                                  </button>
                                  <button 
                                    onClick={() => prepararResposta('vacation', request.id, 'rejeitar')}
                                    className="bg-red-600 hover:bg-red-700 text-white text-xs px-2 py-1 rounded"
                                  >
                                    Rejeitar
                                  </button>
                                </div>
                              )}
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}
          
          {/* Conteúdo da Tab - Folgas */}
          {activeTab === 'daysoff' && (
            <div>
              <div className="mb-6">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-xl font-semibold">Registrar Folga</h2>
                  <button 
                    onClick={() => setShowDayOffModal(true)}
                    className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg"
                  >
                    + Nova Folga
                  </button>
                </div>
              </div>
              
              <div>
                <h2 className="text-xl font-semibold mb-4">Folgas {funcionarioSelecionado ? `de ${funcionarioSelecionado.nome}` : 'Pendentes'}</h2>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="text-purple-300 text-sm">
                        <th className="text-left p-2">Funcionário</th>
                        <th className="text-left p-2">Data</th>
                        <th className="text-left p-2">Tipo</th>
                        <th className="text-left p-2">Motivo</th>
                        <th className="text-left p-2">Status</th>
                        <th className="text-left p-2">Ações</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dayOffRequests.length === 0 ? (
                        <tr>
                          <td colSpan="6" className="text-center p-4">Nenhuma folga registrada</td>
                        </tr>
                      ) : (
                        dayOffRequests.map((request, index) => (
                          <tr key={index} className="border-t border-purple-700">
                            <td className="p-2">{request.nomeFuncionario || 'Não especificado'}</td>
                            <td className="p-2">
                              {request.date}
                              {request.endDate && ` a ${request.endDate}`}
                            </td>
                            <td className="p-2">
                              {request.tipoFolga === 'COMPENSATORIA' ? 'Compensatória' : 
                               request.tipoFolga === 'ABONO' ? 'Abono' : 'Banco de Horas'}
                            </td>
                            <td className="p-2">{request.reason}</td>
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                                request.status === 'aprovado' ? 'bg-green-600' : 
                                request.status === 'rejeitado' ? 'bg-red-600' : 'bg-yellow-600'
                              }`}>
                                {request.status.toUpperCase()}
                              </span>
                            </td>
                            <td className="p-2">
                              {request.status === 'pendente' && (
                                <div className="flex space-x-2">
                                  <button 
                                    onClick={() => prepararResposta('dayoff', request.id, 'aprovar')}
                                    className="bg-green-600 hover:bg-green-700 text-white text-xs px-2 py-1 rounded"
                                  >
                                    Aprovar
                                  </button>
                                  <button 
                                    onClick={() => prepararResposta('dayoff', request.id, 'rejeitar')}
                                    className="bg-red-600 hover:bg-red-700 text-white text-xs px-2 py-1 rounded"
                                  >
                                    Rejeitar
                                  </button>
                                </div>
                              )}
                            </td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}
          
          {/* Conteúdo da Tab - Solicitações */}
          {activeTab === 'requests' && (
            <div>
              <h2 className="text-xl font-semibold mb-4">Solicitações de Revisão {funcionarioSelecionado ? `de ${funcionarioSelecionado.nome}` : 'Pendentes'}</h2>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="text-purple-300 text-sm">
                      <th className="text-left p-2">Funcionário</th>
                      <th className="text-left p-2">Data</th>
                      <th className="text-left p-2">Tipo</th>
                      <th className="text-left p-2">Descrição</th>
                      <th className="text-left p-2">Status</th>
                      <th className="text-left p-2">Ações</th>
                    </tr>
                  </thead>
                  <tbody>
                    {solicitacoesRevisao.length === 0 ? (
                      <tr>
                        <td colSpan="6" className="text-center p-4">Nenhuma solicitação pendente</td>
                      </tr>
                    ) : (
                      solicitacoesRevisao.map((solicitacao, index) => (
                        <tr key={index} className="border-t border-purple-700">
                          <td className="p-2">{solicitacao.nomeFuncionario}</td>
                          <td className="p-2">{format(parseISO(solicitacao.dataCriacao), 'dd/MM/yyyy')}</td>
                          <td className="p-2">
                            {solicitacao.tipo === 'AJUSTE_PONTO' ? 'Ajuste de Ponto' : 
                             solicitacao.tipo === 'REVISAO_BANCO_HORAS' ? 'Revisão de Banco de Horas' : 
                             solicitacao.tipo === 'SOLICITACAO_FERIAS' ? 'Solicitação de Férias' : 
                             solicitacao.tipo === 'SOLICITACAO_FOLGA' ? 'Solicitação de Folga' : 
                             solicitacao.tipo === 'SOLICITACAO_HORA_EXTRA' ? 'Solicitação de Hora Extra' : 'Outros'}
                          </td>
                          <td className="p-2">{solicitacao.descricao.length > 50 ? solicitacao.descricao.substring(0, 50) + '...' : solicitacao.descricao}</td>
                          <td className="p-2">
                            <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                              solicitacao.status === 'CONCLUIDA' ? 'bg-green-600' : 
                              solicitacao.status === 'REJEITADA' ? 'bg-red-600' : 
                              solicitacao.status === 'EM_ANALISE' ? 'bg-blue-600' : 'bg-yellow-600'
                            }`}>
                              {solicitacao.status.replace('_', ' ')}
                            </span>
                          </td>
                          <td className="p-2">
                            {(solicitacao.status === 'ABERTA' || solicitacao.status === 'EM_ANALISE') && (
                              <div className="flex space-x-2">
                                <button 
                                  onClick={() => prepararResposta('solicitacao', solicitacao.id, 'aprovar')}
                                  className="bg-green-600 hover:bg-green-700 text-white text-xs px-2 py-1 rounded"
                                >
                                  Aceitar
                                </button>
                                <button 
                                  onClick={() => prepararResposta('solicitacao', solicitacao.id, 'rejeitar')}
                                  className="bg-red-600 hover:bg-red-700 text-white text-xs px-2 py-1 rounded"
                                >
                                  Rejeitar
                                </button>
                              </div>
                            )}
                          </td>
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
        
        {/* Dashboard Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          {/* Card - Horas Extras */}
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <div className="flex items-center mb-4">
              <div className="bg-purple-600 p-2 rounded-full mr-2">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <h2 className="text-lg font-semibold">Horas Extras</h2>
            </div>
            <p className="text-purple-300 mb-2">Pendentes: {contadores.horasExtrasPendentes}</p>
            <button 
              onClick={() => setActiveTab('overtimes')}
              className="text-sm text-purple-300 hover:text-white"
            >
              Ver detalhes →
            </button>
          </div>
          
          {/* Card - Férias */}
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <div className="flex items-center mb-4">
              <div className="bg-purple-600 p-2 rounded-full mr-2">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg>
              </div>
              <h2 className="text-lg font-semibold">Férias</h2>
            </div>
            <p className="text-purple-300 mb-2">Pendentes: {contadores.feriasPendentes}</p>
            <button 
              onClick={() => setActiveTab('vacations')}
              className="text-sm text-purple-300 hover:text-white"
            >
              Ver detalhes →
            </button>
          </div>
          
          {/* Card - Folgas */}
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <div className="flex items-center mb-4">
              <div className="bg-purple-600 p-2 rounded-full mr-2">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
              </div>
              <h2 className="text-lg font-semibold">Folgas</h2>
            </div>
            <p className="text-purple-300 mb-2">Pendentes: {contadores.folgasPendentes}</p>
            <button 
              onClick={() => setActiveTab('daysoff')}
              className="text-sm text-purple-300 hover:text-white"
            >
              Ver detalhes →
            </button>
          </div>
          
          {/* Card - Solicitações */}
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <div className="flex items-center mb-4">
              <div className="bg-purple-600 p-2 rounded-full mr-2">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z" />
                </svg>
              </div>
              <h2 className="text-lg font-semibold">Solicitações</h2>
            </div>
            <p className="text-purple-300 mb-2">Pendentes: {contadores.solicitacoesPendentes}</p>
            <button 
              onClick={() => setActiveTab('requests')}
              className="text-sm text-purple-300 hover:text-white"
            >
              Ver detalhes →
            </button>
          </div>
        </div>
      </main>
      
      {/* Modal para adicionar hora extra */}
      <Modal show={showOvertimeModal} onHide={() => setShowOvertimeModal(false)} centered>
        <Modal.Header closeButton className="bg-purple-900 text-white">
          <Modal.Title>Registrar Hora Extra</Modal.Title>
        </Modal.Header>
        <Modal.Body className="bg-purple-800 text-white">
          <Form onSubmit={handleAddOvertime}>
            {!funcionarioSelecionado && (
              <Form.Group className="mb-3">
                <Form.Label>Funcionário</Form.Label>
                <Form.Select
                  name="funcionarioId"
                  value={newOvertime.funcionarioId}
                  onChange={(e) => setNewOvertime({...newOvertime, funcionarioId: e.target.value})}
                  required
                  className="bg-purple-700 text-white border-purple-600"
                >
                  <option value="">Selecione um funcionário</option>
                  {funcionarios.map(funcionario => (
                    <option key={funcionario.id} value={funcionario.id}>
                      {funcionario.nome} ({funcionario.matricula})
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            )}
            
            <Form.Group className="mb-3">
              <Form.Label>Data e Hora de Início</Form.Label>
              <Form.Control
                type="datetime-local"
                name="dataHoraInicio"
                value={newOvertime.dataHoraInicio}
                onChange={(e) => setNewOvertime({...newOvertime, dataHoraInicio: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Data e Hora de Fim</Form.Label>
              <Form.Control
                type="datetime-local"
                name="dataHoraFim"
                value={newOvertime.dataHoraFim}
                onChange={(e) => setNewOvertime({...newOvertime, dataHoraFim: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Tipo</Form.Label>
              <Form.Select 
                name="tipo" 
                value={newOvertime.tipo} 
                onChange={(e) => setNewOvertime({...newOvertime, tipo: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              >
                <option value="NORMAL">Normal</option>
                <option value="NOTURNA">Noturna</option>
                <option value="DOMINGO">Domingo</option>
                <option value="FERIADO">Feriado</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Justificativa</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="justificativa"
                value={newOvertime.justificativa}
                onChange={(e) => setNewOvertime({...newOvertime, justificativa: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                label="Para compensação (banco de horas)"
                name="paraCompensacao"
                checked={newOvertime.paraCompensacao}
                onChange={(e) => setNewOvertime({...newOvertime, paraCompensacao: e.target.checked})}
                className="text-white"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                label="Para remuneração (pagamento)"
                name="paraRemuneracao"
                checked={newOvertime.paraRemuneracao}
                onChange={(e) => setNewOvertime({...newOvertime, paraRemuneracao: e.target.checked})}
                className="text-white"
              />
            </Form.Group>

            <div className="flex justify-end">
              <Button variant="secondary" className="mr-2 bg-gray-600 hover:bg-gray-700" onClick={() => setShowOvertimeModal(false)}>
                Cancelar
              </Button>
              <Button variant="primary" type="submit" className="bg-purple-600 hover:bg-purple-700">
                Registrar
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
      
      {/* Modal para adicionar férias */}
      <Modal show={showVacationModal} onHide={() => setShowVacationModal(false)} centered>
        <Modal.Header closeButton className="bg-purple-900 text-white">
          <Modal.Title>Registrar Férias</Modal.Title>
        </Modal.Header>
        <Modal.Body className="bg-purple-800 text-white">
          <Form onSubmit={handleAddVacation}>
            {!funcionarioSelecionado && (
              <Form.Group className="mb-3">
                <Form.Label>Funcionário</Form.Label>
                <Form.Select
                  name="funcionarioId"
                  value={newVacation.funcionarioId}
                  onChange={(e) => setNewVacation({...newVacation, funcionarioId: e.target.value})}
                  required
                  className="bg-purple-700 text-white border-purple-600"
                >
                  <option value="">Selecione um funcionário</option>
                  {funcionarios.map(funcionario => (
                    <option key={funcionario.id} value={funcionario.id}>
                      {funcionario.nome} ({funcionario.matricula})
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            )}
            
            <Form.Group className="mb-3">
              <Form.Label>Data de Início</Form.Label>
              <Form.Control
                type="date"
                name="dataInicio"
                value={newVacation.dataInicio}
                onChange={(e) => setNewVacation({...newVacation, dataInicio: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Data de Fim</Form.Label>
              <Form.Control
                type="date"
                name="dataFim"
                value={newVacation.dataFim}
                onChange={(e) => setNewVacation({...newVacation, dataFim: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Dias Úteis (calculado automaticamente)</Form.Label>
              <Form.Control
                type="number"
                name="diasUteis"
                value={newVacation.diasUteis || (newVacation.dataInicio && newVacation.dataFim ? 
                  calculateWorkingDays(newVacation.dataInicio, newVacation.dataFim) : 0)}
                onChange={(e) => setNewVacation({...newVacation, diasUteis: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Período Aquisitivo</Form.Label>
              <Form.Control
                type="number"
                name="periodoAquisitivo"
                value={newVacation.periodoAquisitivo}
                onChange={(e) => setNewVacation({...newVacation, periodoAquisitivo: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                label="Adiantamento do 13º salário"
                name="adiantamento13"
                checked={newVacation.adiantamento13}
                onChange={(e) => setNewVacation({...newVacation, adiantamento13: e.target.checked})}
                className="text-white"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                label="Venda de 1/3 das férias"
                name="vendaUmTerco"
                checked={newVacation.vendaUmTerco}
                onChange={(e) => setNewVacation({...newVacation, vendaUmTerco: e.target.checked})}
                className="text-white"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Observação</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="observacao"
                value={newVacation.observacao}
                onChange={(e) => setNewVacation({...newVacation, observacao: e.target.value})}
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <div className="flex justify-end">
              <Button variant="secondary" className="mr-2 bg-gray-600 hover:bg-gray-700" onClick={() => setShowVacationModal(false)}>
                Cancelar
              </Button>
              <Button variant="primary" type="submit" className="bg-purple-600 hover:bg-purple-700">
                Registrar
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
      
      {/* Modal para adicionar folga */}
      <Modal show={showDayOffModal} onHide={() => setShowDayOffModal(false)} centered>
        <Modal.Header closeButton className="bg-purple-900 text-white">
          <Modal.Title>Registrar Folga</Modal.Title>
        </Modal.Header>
        <Modal.Body className="bg-purple-800 text-white">
          <Form onSubmit={handleAddDayOff}>
            {!funcionarioSelecionado && (
              <Form.Group className="mb-3">
                <Form.Label>Funcionário</Form.Label>
                <Form.Select
                  name="funcionarioId"
                  value={newDayOff.funcionarioId}
                  onChange={(e) => setNewDayOff({...newDayOff, funcionarioId: e.target.value})}
                  required
                  className="bg-purple-700 text-white border-purple-600"
                >
                  <option value="">Selecione um funcionário</option>
                  {funcionarios.map(funcionario => (
                    <option key={funcionario.id} value={funcionario.id}>
                      {funcionario.nome} ({funcionario.matricula})
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            )}
            
            <Form.Group className="mb-3">
              <Form.Label>Data de Início</Form.Label>
              <Form.Control
                type="date"
                name="dataInicio"
                value={newDayOff.dataInicio}
                onChange={(e) => setNewDayOff({...newDayOff, dataInicio: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Data de Fim</Form.Label>
              <Form.Control
                type="date"
                name="dataFim"
                value={newDayOff.dataFim}
                onChange={(e) => setNewDayOff({...newDayOff, dataFim: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
              <Form.Text className="text-purple-300">
                Para folga de um dia, use a mesma data de início e fim.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Tipo de Folga</Form.Label>
              <Form.Select 
                name="tipoFolga" 
                value={newDayOff.tipoFolga} 
                onChange={(e) => setNewDayOff({...newDayOff, tipoFolga: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              >
                <option value="COMPENSATORIA">Compensatória</option>
                <option value="ABONO">Abono</option>
                <option value="BANCO_HORAS">Banco de Horas</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Motivo</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                name="motivo"
                value={newDayOff.motivo}
                onChange={(e) => setNewDayOff({...newDayOff, motivo: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              />
            </Form.Group>

            <div className="flex justify-end">
              <Button variant="secondary" className="mr-2 bg-gray-600 hover:bg-gray-700" onClick={() => setShowDayOffModal(false)}>
                Cancelar
              </Button>
              <Button variant="primary" type="submit" className="bg-purple-600 hover:bg-purple-700">
                Registrar
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
      
      {/* Modal para responder/aprovar/rejeitar */}
      <Modal show={showRespostaModal} onHide={() => setShowRespostaModal(false)} centered>
        <Modal.Header closeButton className="bg-purple-900 text-white">
          <Modal.Title>
            {itemParaResponder?.acao === 'aprovar' ? 'Aprovar ' : 'Rejeitar '}
            {itemParaResponder?.type === 'overtime' ? 'Hora Extra' : 
             itemParaResponder?.type === 'vacation' ? 'Férias' : 
             itemParaResponder?.type === 'dayoff' ? 'Folga' : 'Solicitação'}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className="bg-purple-800 text-white">
          <Form.Group className="mb-3">
            <Form.Label>Observação / Justificativa</Form.Label>
            <Form.Control
              as="textarea"
              rows={4}
              value={respostaTexto}
              onChange={(e) => setRespostaTexto(e.target.value)}
              className="bg-purple-700 text-white border-purple-600"
              placeholder="Informe uma observação ou justificativa (opcional)"
            />
          </Form.Group>
          
          <div className="flex justify-end">
            <Button variant="secondary" className="mr-2 bg-gray-600 hover:bg-gray-700" onClick={() => setShowRespostaModal(false)}>
              Cancelar
            </Button>
            <Button 
              variant={itemParaResponder?.acao === 'aprovar' ? 'success' : 'danger'} 
              onClick={enviarResposta}
              className={itemParaResponder?.acao === 'aprovar' ? 'bg-green-600 hover:bg-green-700' : 'bg-red-600 hover:bg-red-700'}
            >
              {itemParaResponder?.acao === 'aprovar' ? 'Aprovar' : 'Rejeitar'}
            </Button>
          </div>
        </Modal.Body>
      </Modal>
      
      {/* Footer */}
      <footer className="bg-purple-900 bg-opacity-80 shadow-lg py-4 mt-auto">
        <div className="container mx-auto px-4 text-center text-purple-300 text-sm">
          <p>&copy; 2025 CuidaEmprego. Todos os direitos reservados.</p>
        </div>
      </footer>
    </div>
  );
};

export default AdminPage;
