import React, { createContext, useState, useContext, useEffect } from 'react';

const FuncionariosContext = createContext();

export const useFuncionarios = () => useContext(FuncionariosContext);

export const FuncionariosProvider = ({ children }) => {
  const [funcionarios, setFuncionarios] = useState(() => {
    const storedFuncionarios = localStorage.getItem('funcionarios');
    const registeredUsers = localStorage.getItem('registeredUsers');
    
    if (storedFuncionarios) {
      return JSON.parse(storedFuncionarios);
    } else if (registeredUsers) {
      // Converte usuários registrados em funcionários
      const users = JSON.parse(registeredUsers);
      return users.map(user => ({ 
        id: user.id, 
        nome: user.name 
      }));
    } else {
      // Dados padrão
      return [
        { id: 101, nome: 'João Silva' },
        { id: 102, nome: 'Maria Oliveira' },
        { id: 103, nome: 'Carlos Pereira' },
        { id: 104, nome: 'Ana Souza' },
        { id: 105, nome: 'Pedro Santos' }
      ];
    }
  });

  useEffect(() => {
    // Sincroniza os funcionários com localStorage
    localStorage.setItem('funcionarios', JSON.stringify(funcionarios));
  }, [funcionarios]);

  // Adiciona um novo funcionário
  const adicionarFuncionario = (funcionario) => {
    setFuncionarios(prev => [...prev, funcionario]);
  };

  // Atualiza um funcionário existente
  const atualizarFuncionario = (id, dadosAtualizados) => {
    setFuncionarios(prev => 
      prev.map(func => func.id === id ? { ...func, ...dadosAtualizados } : func)
    );
  };

  // Remove um funcionário
  const removerFuncionario = (id) => {
    setFuncionarios(prev => prev.filter(func => func.id !== id));
  };

  // Busca funcionários por termo de pesquisa
  const buscarFuncionarios = (termo) => {
    if (!termo) return funcionarios;
    const termoLower = termo.toLowerCase();
    return funcionarios.filter(func => 
      func.nome.toLowerCase().includes(termoLower)
    );
  };

  const value = {
    funcionarios,
    adicionarFuncionario,
    atualizarFuncionario,
    removerFuncionario,
    buscarFuncionarios
  };

  return (
    <FuncionariosContext.Provider value={value}>
      {children}
    </FuncionariosContext.Provider>
  );
};
