import React, { useState, useEffect } from 'react';

const BancoHorasTab = () => {
  // Estado para o banco de horas dos funcionários
  const [bancoHoras, setBancoHoras] = useState(() => {
    const storedBancoHoras = localStorage.getItem('bancoHoras');
    return storedBancoHoras ? JSON.parse(storedBancoHoras) : [
      {
        funcionarioId: 101,
        funcionarioNome: 'João Silva',
        saldoAtual: 9.5,
        ultimaAtualizacao: '19/03/2025',
        historico: [
          { id: 1, tipo: 'crédito', motivo: 'Hora extra', horas: 2, data: '10/03/2025', observacao: 'Aprovada pelo gestor' },
          { id: 2, tipo: 'crédito', motivo: 'Hora extra', horas: 2.5, data: '18/03/2025', observacao: 'Detectada automaticamente' },
          { id: 3, tipo: 'crédito', motivo: 'Hora extra', horas: 7, data: '25/02/2025', observacao: 'Trabalho no final de semana' },
          { id: 4, tipo: 'débito', motivo: 'Compensação', horas: 2, data: '15/03/2025', observacao: 'Saída antecipada' }
        ]
      },
      {
        funcionarioId: 102,
        funcionarioNome: 'Maria Oliveira',
        saldoAtual: 3.0,
        ultimaAtualizacao: '18/03/2025',
        historico: [
          { id: 5, tipo: 'crédito', motivo: 'Hora extra', horas: 1.5, data: '05/03/2025', observacao: 'Reunião com cliente' },
          { id: 6, tipo: 'crédito', motivo: 'Hora extra', horas: 3, data: '10/03/2025', observacao: 'Fechamento mensal' },
          { id: 7, tipo: 'débito', motivo: 'Compensação', horas: 1.5, data: '15/03/2025', observacao: 'Entrada tardia' }
        ]
      },
      {
        funcionarioId: 103,
        funcionarioNome: 'Carlos Pereira',
        saldoAtual: 5.0,
        ultimaAtualizacao: '17/03/2025',
        historico: [
          { id: 8, tipo: 'crédito', motivo: 'Hora extra', horas: 3, data: '15/03/2025', observacao: 'Implantação de sistema' },
          { id: 9, tipo: 'crédito', motivo: 'Hora extra', horas: 2, data: '10/03/2025', observacao: 'Suporte emergencial' }
        ]
      },
      {
        funcionarioId: 104,
        funcionarioNome: 'Ana Souza',
        saldoAtual: 0.0,
        ultimaAtualizacao: '16/03/2025',
        historico: []
      }
    ];
  });
  
  // Estado para filtros
  const [filtros, setFiltros] = useState({
    funcionario: '',
    saldoMinimo: ''
  });
  
  // Estado para nova movimentação
  const [novaMovimentacao, setNovaMovimentacao] = useState({
    funcionarioId: '',
    tipo: 'crédito',
    motivo: '',
    horas: '',
    observacao: ''
  });
  
  // Estado para funcionário selecionado (detalhes)
  const [funcionarioSelecionado, setFuncionarioSelecionado] = useState(null);
  
  // Lista de funcionários para o dropdown
  const [funcionarios, setFuncionarios] = useState(() => {
    const storedFuncionarios = localStorage.getItem('funcionarios');
    return storedFuncionarios ? JSON.parse(storedFuncionarios) : [
      { id: 101, nome: 'João Silva' },
      { id: 102, nome: 'Maria Oliveira' },
      { id: 103, nome: 'Carlos Pereira' },
      { id: 104, nome: 'Ana Souza' },
      { id: 105, nome: 'Pedro Santos' }
    ];
  });
  
  // Salvar banco de horas no localStorage quando mudar
  useEffect(() => {
    localStorage.setItem('bancoHoras', JSON.stringify(bancoHoras));
  }, [bancoHoras]);
  
  // Filtrar banco de horas
  const bancoHorasFiltrado = bancoHoras.filter(funcionario => {
    const matchFuncionario = filtros.funcionario === '' || funcionario.funcionarioNome === filtros.funcionario;
    const matchSaldo = filtros.saldoMinimo === '' || funcionario.saldoAtual >= parseFloat(filtros.saldoMinimo);
    
    return matchFuncionario && matchSaldo;
  });
  
  // Selecionar funcionário para nova movimentação
  const handleSelecionarFuncionario = (e) => {
    const funcionarioId = parseInt(e.target.value);
    setNovaMovimentacao({
      ...novaMovimentacao,
      funcionarioId: funcionarioId || ''
    });
  };
  
  // Adicionar nova movimentação
  const handleAdicionarMovimentacao = (e) => {
    e.preventDefault();
    
    if (!novaMovimentacao.funcionarioId || !novaMovimentacao.motivo || !novaMovimentacao.horas) {
      alert('Por favor, preencha todos os campos obrigatórios.');
      return;
    }
    
    const horasNumericas = parseFloat(novaMovimentacao.horas);
    if (isNaN(horasNumericas) || horasNumericas <= 0) {
      alert('Por favor, informe um valor de horas válido.');
      return;
    }
    
    // Verificar se o funcionário já possui registro no banco de horas
    const funcionarioIndex = bancoHoras.findIndex(f => f.funcionarioId === novaMovimentacao.funcionarioId);
    
    if (funcionarioIndex === -1) {
      // Funcionário não possui registro, criar novo
      const funcionarioSelecionado = funcionarios.find(f => f.id === novaMovimentacao.funcionarioId);
      
      if (!funcionarioSelecionado) {
        alert('Funcionário não encontrado.');
        return;
      }
      
      // Criar novo registro para o funcionário
      const novoRegistro = {
        funcionarioId: novaMovimentacao.funcionarioId,
        funcionarioNome: funcionarioSelecionado.nome,
        saldoAtual: novaMovimentacao.tipo === 'crédito' ? horasNumericas : -horasNumericas,
        ultimaAtualizacao: new Date().toLocaleDateString('pt-BR'),
        historico: [
          {
            id: Date.now(),
            tipo: novaMovimentacao.tipo,
            motivo: novaMovimentacao.motivo,
            horas: horasNumericas,
            data: new Date().toLocaleDateString('pt-BR'),
            observacao: novaMovimentacao.observacao || null
          }
        ]
      };
      
      setBancoHoras([...bancoHoras, novoRegistro]);
    } else {
      // Funcionário já possui registro, atualizar
      const bancoHorasAtualizado = [...bancoHoras];
      const funcionario = bancoHorasAtualizado[funcionarioIndex];
      
      // Adicionar movimentação ao histórico
      funcionario.historico.unshift({
        id: Date.now(),
        tipo: novaMovimentacao.tipo,
        motivo: novaMovimentacao.motivo,
        horas: horasNumericas,
        data: new Date().toLocaleDateString('pt-BR'),
        observacao: novaMovimentacao.observacao || null
      });
      
      // Atualizar saldo
      if (novaMovimentacao.tipo === 'crédito') {
        funcionario.saldoAtual += horasNumericas;
      } else {
        // Verificar se há saldo suficiente
        if (funcionario.saldoAtual < horasNumericas) {
          alert(`O funcionário não possui saldo suficiente. Saldo atual: ${funcionario.saldoAtual.toFixed(1)}h`);
          return;
        }
        funcionario.saldoAtual -= horasNumericas;
      }
      
      funcionario.ultimaAtualizacao = new Date().toLocaleDateString('pt-BR');
      
      setBancoHoras(bancoHorasAtualizado);
    }
    
    // Notificar funcionário sobre a movimentação
    const notificacoes = JSON.parse(localStorage.getItem('userNotifications') || '[]');
    notificacoes.push({
      id: Date.now(),
      userId: novaMovimentacao.funcionarioId,
      message: `${novaMovimentacao.tipo === 'crédito' ? 'Crédito' : 'Débito'} de ${novaMovimentacao.horas}h em seu banco de horas: ${novaMovimentacao.motivo}.`,
      date: new Date().toLocaleDateString('pt-BR'),
      read: false
    });
    localStorage.setItem('userNotifications', JSON.stringify(notificacoes));
    
    // Resetar formulário
    setNovaMovimentacao({
      funcionarioId: '',
      tipo: 'crédito',
      motivo: '',
      horas: '',
      observacao: ''
    });
    
    alert('Movimentação registrada com sucesso!');
  };
  
  // Ver detalhes do banco de horas do funcionário
  const verDetalhesFuncionario = (funcionarioId) => {
    const funcionario = bancoHoras.find(f => f.funcionarioId === funcionarioId);
    setFuncionarioSelecionado(funcionario || null);
  };
  
  // Remover movimentação (apenas para fins de demonstração)
  const removerMovimentacao = (funcionarioId, movimentacaoId) => {
    // Corrigido: usando window.confirm em vez de confirm diretamente
    if (!window.confirm('Tem certeza que deseja remover esta movimentação? Esta ação não pode ser desfeita.')) {
      return;
    }
    
    const funcionarioIndex = bancoHoras.findIndex(f => f.funcionarioId === funcionarioId);
    if (funcionarioIndex === -1) return;
    
    const bancoHorasAtualizado = [...bancoHoras];
    const funcionario = bancoHorasAtualizado[funcionarioIndex];
    
    // Encontrar movimentação
    const movimentacaoIndex = funcionario.historico.findIndex(m => m.id === movimentacaoId);
    if (movimentacaoIndex === -1) return;
    
    const movimentacao = funcionario.historico[movimentacaoIndex];
    
    // Atualizar saldo
    if (movimentacao.tipo === 'crédito') {
      funcionario.saldoAtual -= movimentacao.horas;
    } else {
      funcionario.saldoAtual += movimentacao.horas;
    }
    
    // Remover movimentação do histórico
    funcionario.historico.splice(movimentacaoIndex, 1);
    
    funcionario.ultimaAtualizacao = new Date().toLocaleDateString('pt-BR');
    
    setBancoHoras(bancoHorasAtualizado);
    
    // Atualizar detalhes do funcionário se estiver selecionado
    if (funcionarioSelecionado && funcionarioSelecionado.funcionarioId === funcionarioId) {
      setFuncionarioSelecionado(funcionario);
    }
    
    alert('Movimentação removida com sucesso!');
  };
  
  return (
    <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
      <h1 className="text-2xl font-bold mb-6">Banco de Horas</h1>
      
      {/* Filtros */}
      <div className="bg-purple-900 bg-opacity-40 rounded-lg p-4 mb-6">
        <h2 className="text-xl font-semibold mb-4">Filtros</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm text-purple-300 mb-1">Funcionário</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.funcionario}
              onChange={(e) => setFiltros({...filtros, funcionario: e.target.value})}
            >
              <option value="">Todos os funcionários</option>
              {[...new Set(bancoHoras.map(bh => bh.funcionarioNome))].map((nome, index) => (
                <option key={index} value={nome}>{nome}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Saldo Mínimo (horas)</label>
            <input 
              type="number" 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.saldoMinimo}
              onChange={(e) => setFiltros({...filtros, saldoMinimo: e.target.value})}
              min="0"
              step="0.5"
              placeholder="Ex: 5"
            />
          </div>
        </div>
      </div>
      
      {/* Nova Movimentação */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4">Registrar Movimentação</h2>
        <form onSubmit={handleAdicionarMovimentacao} className="bg-purple-900 bg-opacity-40 rounded-lg p-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div>
              <label className="block text-sm text-purple-300 mb-1">Funcionário *</label>
              <select 
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaMovimentacao.funcionarioId}
                onChange={handleSelecionarFuncionario}
                required
              >
                <option value="">Selecione</option>
                {funcionarios.map(funcionario => (
                  <option key={funcionario.id} value={funcionario.id}>{funcionario.nome}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm text-purple-300 mb-1">Tipo *</label>
              <select 
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaMovimentacao.tipo}
                onChange={(e) => setNovaMovimentacao({...novaMovimentacao, tipo: e.target.value})}
                required
              >
                <option value="crédito">Crédito</option>
                <option value="débito">Débito</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-purple-300 mb-1">Horas *</label>
              <input 
                type="number"
                step="0.5"
                min="0.5"
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaMovimentacao.horas}
                onChange={(e) => setNovaMovimentacao({...novaMovimentacao, horas: e.target.value})}
                required
                placeholder="Ex: 2.5"
              />
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm text-purple-300 mb-1">Motivo *</label>
              <input 
                type="text"
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaMovimentacao.motivo}
                onChange={(e) => setNovaMovimentacao({...novaMovimentacao, motivo: e.target.value})}
                required
                placeholder="Motivo da movimentação"
              />
            </div>
            <div>
              <label className="block text-sm text-purple-300 mb-1">Observação</label>
              <input 
                type="text"
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaMovimentacao.observacao}
                onChange={(e) => setNovaMovimentacao({...novaMovimentacao, observacao: e.target.value})}
                placeholder="Observação (opcional)"
              />
            </div>
          </div>
          <button 
            type="submit"
            className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
          >
            Registrar Movimentação
          </button>
        </form>
      </div>
      
      {/* Lista de Banco de Horas */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Saldo de Banco de Horas</h2>
        
        {funcionarioSelecionado ? (
          <div>
            {/* Cabeçalho com informações do funcionário */}
            <div className="bg-purple-900 bg-opacity-40 p-4 rounded-t-lg flex justify-between items-center">
              <div>
                <h3 className="text-lg font-semibold">{funcionarioSelecionado.funcionarioNome}</h3>
                <p className="text-sm text-purple-300">
                  Última atualização: {funcionarioSelecionado.ultimaAtualizacao}
                </p>
              </div>
              <div className="text-right">
                <p className="text-sm text-purple-300">Saldo atual</p>
                <p className={`text-xl font-bold ${funcionarioSelecionado.saldoAtual > 0 ? 'text-green-400' : funcionarioSelecionado.saldoAtual < 0 ? 'text-red-400' : ''}`}>
                  {funcionarioSelecionado.saldoAtual.toFixed(1)}h
                </p>
              </div>
            </div>
            
            {/* Histórico de movimentações */}
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="text-purple-300 text-sm border-b border-purple-700">
                    <th className="px-4 py-2 text-left">Data</th>
                    <th className="px-4 py-2 text-left">Tipo</th>
                    <th className="px-4 py-2 text-left">Horas</th>
                    <th className="px-4 py-2 text-left">Motivo</th>
                    <th className="px-4 py-2 text-left">Observação</th>
                    <th className="px-4 py-2 text-left">Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {funcionarioSelecionado.historico.length > 0 ? (
                    funcionarioSelecionado.historico.map(movimentacao => (
                      <tr key={movimentacao.id} className="border-b border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                        <td className="px-4 py-3">{movimentacao.data}</td>
                        <td className="px-4 py-3 capitalize">
                          <span className={`inline-block px-2 py-1 rounded-full text-xs ${movimentacao.tipo === 'crédito' ? 'bg-green-600' : 'bg-red-600'}`}>
                            {movimentacao.tipo.toUpperCase()}
                          </span>
                        </td>
                        <td className="px-4 py-3">{movimentacao.horas}h</td>
                        <td className="px-4 py-3">{movimentacao.motivo}</td>
                        <td className="px-4 py-3">{movimentacao.observacao || '-'}</td>
                        <td className="px-4 py-3">
                          <button 
                            onClick={() => removerMovimentacao(funcionarioSelecionado.funcionarioId, movimentacao.id)}
                            className="bg-red-600 hover:bg-red-700 text-white text-xs px-2 py-1 rounded"
                          >
                            Remover
                          </button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="6" className="px-4 py-6 text-center text-purple-300">
                        Nenhuma movimentação registrada para este funcionário.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
            
            {/* Botão para voltar */}
            <div className="mt-4">
              <button 
                onClick={() => setFuncionarioSelecionado(null)}
                className="bg-purple-600 hover:bg-purple-700 text-white text-sm px-3 py-1 rounded"
              >
                ← Voltar para lista
              </button>
            </div>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm border-b border-purple-700">
                  <th className="px-4 py-2 text-left">Funcionário</th>
                  <th className="px-4 py-2 text-left">Saldo</th>
                  <th className="px-4 py-2 text-left">Última Atualização</th>
                  <th className="px-4 py-2 text-left">Movimentações</th>
                  <th className="px-4 py-2 text-left">Ações</th>
                </tr>
              </thead>
              <tbody>
                {bancoHorasFiltrado.length > 0 ? (
                  bancoHorasFiltrado.map(funcionario => (
                    <tr key={funcionario.funcionarioId} className="border-b border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                      <td className="px-4 py-3">{funcionario.funcionarioNome}</td>
                      <td className="px-4 py-3">
                        <span className={`font-bold ${funcionario.saldoAtual > 0 ? 'text-green-400' : funcionario.saldoAtual < 0 ? 'text-red-400' : ''}`}>
                          {funcionario.saldoAtual.toFixed(1)}h
                        </span>
                      </td>
                      <td className="px-4 py-3">{funcionario.ultimaAtualizacao}</td>
                      <td className="px-4 py-3">{funcionario.historico.length}</td>
                      <td className="px-4 py-3">
                        <button 
                          onClick={() => verDetalhesFuncionario(funcionario.funcionarioId)}
                          className="bg-purple-600 hover:bg-purple-700 text-white text-xs px-2 py-1 rounded"
                        >
                          Ver Detalhes
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="px-4 py-6 text-center text-purple-300">
                      Nenhum registro de banco de horas encontrado com os filtros selecionados.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        )}
      </div>
      
      {/* Estatísticas */}
      <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Card - Total de Horas no Banco */}
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <div className="flex items-center mb-4">
            <div className="bg-green-600 p-2 rounded-full mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h2 className="text-lg font-semibold">Total de Horas no Banco</h2>
          </div>
          <p className="text-2xl font-bold mb-2">
            {bancoHoras
              .reduce((total, funcionario) => total + (funcionario.saldoAtual > 0 ? funcionario.saldoAtual : 0), 0)
              .toFixed(1)}h
          </p>
          <p className="text-purple-300 text-sm">
            Distribuídas entre {bancoHoras.filter(f => f.saldoAtual > 0).length} funcionários
          </p>
        </div>
        
        {/* Card - Maior Saldo */}
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <div className="flex items-center mb-4">
            <div className="bg-blue-600 p-2 rounded-full mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
              </svg>
            </div>
            <h2 className="text-lg font-semibold">Maior Saldo</h2>
          </div>
          {bancoHoras.length > 0 ? (
            <>
              <p className="text-xl font-bold mb-1">
                {bancoHoras.reduce((max, f) => f.saldoAtual > max.saldoAtual ? f : max, bancoHoras[0]).funcionarioNome}
              </p>
              <p className="text-green-400 font-bold">
                {bancoHoras.reduce((max, f) => f.saldoAtual > max.saldoAtual ? f : max, bancoHoras[0]).saldoAtual.toFixed(1)}h
              </p>
            </>
          ) : (
            <p className="text-purple-300 text-sm">Nenhum registro encontrado</p>
          )}
        </div>
        
        {/* Card - Compensações Recentes */}
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <div className="flex items-center mb-4">
            <div className="bg-red-600 p-2 rounded-full mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
            <h2 className="text-lg font-semibold">Compensações Recentes</h2>
          </div>
          
          {/* Lista de compensações recentes */}
          <ul className="space-y-2">
            {bancoHoras
              .flatMap(funcionario => 
                funcionario.historico
                  .filter(h => h.tipo === 'débito')
                  .map(h => ({
                    ...h,
                    funcionarioNome: funcionario.funcionarioNome
                  }))
              )
              .sort((a, b) => {
                // Converter datas do formato DD/MM/YYYY para objetos Date para comparação
                const [diaA, mesA, anoA] = a.data.split('/').map(Number);
                const [diaB, mesB, anoB] = b.data.split('/').map(Number);
                
                const dateA = new Date(anoA, mesA - 1, diaA);
                const dateB = new Date(anoB, mesB - 1, diaB);
                
                return dateB - dateA; // Ordem decrescente (mais recente primeiro)
              })
              .slice(0, 3)
              .map((compensacao, index) => (
                <li key={index} className="text-sm">
                  <div className="flex justify-between">
                    <span>{compensacao.funcionarioNome}</span>
                    <span className="text-red-400">{compensacao.horas}h</span>
                  </div>
                  <div className="text-xs text-purple-300">{compensacao.data} - {compensacao.motivo}</div>
                </li>
              ))}
              
            {bancoHoras.flatMap(f => f.historico).filter(h => h.tipo === 'débito').length === 0 && (
              <li className="text-purple-300 text-sm">Nenhuma compensação registrada</li>
            )}
          </ul>
        </div>
      </div>
      
      {/* Botões de ação */}
      <div className="mt-6 flex flex-wrap gap-4">
        <button className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12" />
          </svg>
          Exportar Relatório
        </button>
        
        <button className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
          </svg>
          Notificar Saldos aos Funcionários
        </button>
      </div>
    </div>
  );
};

export default BancoHorasTab;
