import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import styled from '@emotion/styled';

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  font-family: Arial, sans-serif;
`;

const Header = styled.header`
  background-color: #4a0e4e;
  color: white;
  padding: 10px 0;
  text-align: center;
`;

const Nav = styled.nav`
  margin-top: 20px;
  a {
    margin-right: 15px;
    color: #4a0e4e;
    text-decoration: none;
    &:hover {
      text-decoration: underline;
    }
  }
`;

const HomePage = () => (
  <div>
    <h1>Bem-vindo ao CuidaEmprego</h1>
    <p>Sistema de gerenciamento de ponto eletrônico</p>
  </div>
);

const DashboardPage = () => (
  <div>
    <h1>Dashboard</h1>
    <p>Aqui você pode ver suas horas trabalhadas e registrar seu ponto.</p>
  </div>
);

const App = () => (
  <Router>
    <Container>
      <Header>
        <h1>CuidaEmprego</h1>
      </Header>
      <Nav>
        <Link to="/">Início</Link>
        <Link to="/dashboard">Dashboard</Link>
      </Nav>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/dashboard" element={<DashboardPage />} />
      </Routes>
    </Container>
  </Router>
);

export default App;
