import React, { useState, useEffect } from 'react';

const NotificacoesTab = () => {
  // Estado para notificações de todos os usuários
  const [notifications, setNotifications] = useState(() => {
    const storedNotifications = localStorage.getItem('userNotifications');
    return storedNotifications ? JSON.parse(storedNotifications) : [];
  });
  
  // Estado para filtros
  const [filtros, setFiltros] = useState({
    usuario: '',
    tipo: '',
    lida: ''
  });
  
  // Estado para nova notificação
  const [novaNotificacao, setNovaNotificacao] = useState({
    userId: '',
    message: '',
    priority: 'normal'
  });
  
  // Lista de usuários
  const [funcionarios, setFuncionarios] = useState(() => {
    const storedFuncionarios = localStorage.getItem('funcionarios');
    return storedFuncionarios ? JSON.parse(storedFuncionarios) : [
      { id: 101, nome: 'João Silva' },
      { id: 102, nome: 'Maria Oliveira' },
      { id: 103, nome: 'Carlos Pereira' },
      { id: 104, nome: 'Ana Souza' },
      { id: 105, nome: 'Pedro Santos' }
    ];
  });
  
  // Salvar notificações no localStorage quando mudar
  useEffect(() => {
    localStorage.setItem('userNotifications', JSON.stringify(notifications));
  }, [notifications]);
  
  // Filtrar notificações
  const notificacoesFiltradas = notifications.filter(notif => {
    // Filtro por usuário
    const matchUsuario = filtros.usuario === '' || 
      (!notif.userId && filtros.usuario === 'all') || 
      (notif.userId && notif.userId.toString() === filtros.usuario);
    
    // Filtro por tipo/prioridade
    const matchTipo = filtros.tipo === '' || notif.priority === filtros.tipo;
    
    // Filtro por status de leitura
    const matchLida = filtros.lida === '' || 
      (filtros.lida === 'lida' && notif.read) || 
      (filtros.lida === 'nao-lida' && !notif.read);
    
    return matchUsuario && matchTipo && matchLida;
  });
  
  // Ordenar notificações (mais recentes primeiro)
  const notificacoesOrdenadas = [...notificacoesFiltradas].sort((a, b) => {
    // Usar o id como critério de ordenação (assumindo que ids maiores são mais recentes)
    return b.id - a.id;
  });
  
  // Selecionar usuário para a nova notificação
  const handleSelecionarUsuario = (e) => {
    setNovaNotificacao({
      ...novaNotificacao,
      userId: e.target.value === 'all' ? '' : parseInt(e.target.value)
    });
  };
  
  // Enviar nova notificação
  const handleSubmitNotificacao = (e) => {
    e.preventDefault();
    
    if (!novaNotificacao.message) {
      alert('Por favor, preencha a mensagem da notificação.');
      return;
    }
    
    const novaNotif = {
      id: Date.now(),
      userId: novaNotificacao.userId || null,
      message: novaNotificacao.message,
      date: new Date().toLocaleDateString('pt-BR'),
      read: false,
      priority: novaNotificacao.priority || 'normal'
    };
    
    setNotifications([...notifications, novaNotif]);
    
    // Resetar formulário
    setNovaNotificacao({
      userId: '',
      message: '',
      priority: 'normal'
    });
    
    alert('Notificação enviada com sucesso!');
  };
  
  // Marcar notificação como lida
  const marcarComoLida = (id) => {
    const notificacoesAtualizadas = notifications.map(notif => 
      notif.id === id ? { ...notif, read: true } : notif
    );
    
    setNotifications(notificacoesAtualizadas);
  };
  
  // Excluir notificação
  const excluirNotificacao = (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta notificação?')) {
      const notificacoesAtualizadas = notifications.filter(notif => notif.id !== id);
      setNotifications(notificacoesAtualizadas);
    }
  };
  
  // Determinar cor de prioridade
  const getPriorityColor = (priority) => {
    switch(priority) {
      case 'high':
        return 'bg-red-600';
      case 'normal':
        return 'bg-blue-600';
      case 'low':
        return 'bg-green-600';
      default:
        return 'bg-blue-600';
    }
  };
  
  return (
    <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
      <h1 className="text-2xl font-bold mb-6">Gerenciamento de Notificações</h1>
      
      {/* Filtros */}
      <div className="bg-purple-900 bg-opacity-40 rounded-lg p-4 mb-6">
        <h2 className="text-xl font-semibold mb-4">Filtros</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-sm text-purple-300 mb-1">Usuário</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.usuario}
              onChange={(e) => setFiltros({...filtros, usuario: e.target.value})}
            >
              <option value="">Todos os usuários</option>
              <option value="all">Geral (sem usuário específico)</option>
              {funcionarios.map(funcionario => (
                <option key={funcionario.id} value={funcionario.id.toString()}>{funcionario.nome}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Prioridade</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.tipo}
              onChange={(e) => setFiltros({...filtros, tipo: e.target.value})}
            >
              <option value="">Todas as prioridades</option>
              <option value="high">Alta</option>
              <option value="normal">Normal</option>
              <option value="low">Baixa</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Status</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.lida}
              onChange={(e) => setFiltros({...filtros, lida: e.target.value})}
            >
              <option value="">Todos os status</option>
              <option value="lida">Lidas</option>
              <option value="nao-lida">Não lidas</option>
            </select>
          </div>
        </div>
      </div>
      
      {/* Nova Notificação */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4">Enviar Nova Notificação</h2>
        <form onSubmit={handleSubmitNotificacao} className="bg-purple-900 bg-opacity-40 rounded-lg p-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            <div>
              <label className="block text-sm text-purple-300 mb-1">Destinatário</label>
              <select 
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaNotificacao.userId}
                onChange={handleSelecionarUsuario}
              >
                <option value="all">Todos os usuários</option>
                {funcionarios.map(funcionario => (
                  <option key={funcionario.id} value={funcionario.id}>{funcionario.nome}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="block text-sm text-purple-300 mb-1">Prioridade</label>
              <select 
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={novaNotificacao.priority}
                onChange={(e) => setNovaNotificacao({...novaNotificacao, priority: e.target.value})}
              >
                <option value="high">Alta</option>
                <option value="normal">Normal</option>
                <option value="low">Baixa</option>
              </select>
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm text-purple-300 mb-1">Mensagem</label>
              <textarea 
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white min-h-24"
                value={novaNotificacao.message}
                onChange={(e) => setNovaNotificacao({...novaNotificacao, message: e.target.value})}
                placeholder="Digite a mensagem da notificação..."
                required
              ></textarea>
            </div>
          </div>
          <button 
            type="submit"
            className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
          >
            Enviar Notificação
          </button>
        </form>
      </div>
      
      {/* Lista de Notificações */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Histórico de Notificações</h2>
        
        {notificacoesOrdenadas.length === 0 ? (
          <div className="bg-purple-900 bg-opacity-30 p-6 rounded-lg text-center text-purple-300">
            Nenhuma notificação encontrada com os filtros selecionados.
          </div>
        ) : (
          <div className="space-y-3">
            {notificacoesOrdenadas.map(notif => (
              <div 
                key={notif.id} 
                className={`bg-purple-900 bg-opacity-40 rounded-lg p-4 border-l-4 ${getPriorityColor(notif.priority)} ${!notif.read ? 'border-r-4' : ''}`}
              >
                <div className="flex justify-between">
                  <div>
                    <p className="text-sm mb-1">
                      <span className="mr-2">
                        {notif.userId ? 
                          funcionarios.find(f => f.id === notif.userId)?.nome || 'Usuário desconhecido' : 
                          'Todos os usuários'}
                      </span>
                      <span className="text-purple-300">{notif.date}</span>
                    </p>
                    <p>{notif.message}</p>
                  </div>
                  <div className="flex space-x-2">
                    {!notif.read && (
                      <button 
                        onClick={() => marcarComoLida(notif.id)}
                        className="text-green-400 hover:text-green-300"
                        title="Marcar como lida"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                      </button>
                    )}
                    <button 
                      onClick={() => excluirNotificacao(notif.id)}
                      className="text-red-400 hover:text-red-300"
                      title="Excluir notificação"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                      </svg>
                    </button>
                  </div>
                </div>
                <div className="flex items-center mt-2">
                  <span className={`inline-block px-2 py-1 rounded-full text-xs ${getPriorityColor(notif.priority)}`}>
                    {notif.priority === 'high' ? 'ALTA' : notif.priority === 'normal' ? 'NORMAL' : 'BAIXA'}
                  </span>
                  <span className={`ml-2 inline-block px-2 py-1 rounded-full text-xs ${notif.read ? 'bg-green-600' : 'bg-yellow-600'}`}>
                    {notif.read ? 'LIDA' : 'NÃO LIDA'}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default NotificacoesTab;
