import { useState, useEffect } from "react";

const JornadasTab = () => {
  const [jornadas, setJornadas] = useState([]);
  const [novaJornada, setNovaJornada] = useState({
    nome: "",
    descricao: "",
    horaInicio: "",
    horaFim: "",
    intervaloInicio: "",
    intervaloFim: "",
    diasTrabalho: [],
    cargaHorariaSemanal: 0,
    toleranciaEntrada: 10,
    toleranciaSaida: 10,
    ativo: true,
    escalaEspecial: false,
    funcionarioId: "",
  });

  useEffect(() => {
    const storedJornadas = localStorage.getItem("jornadas");
    if (storedJornadas) {
      setJornadas(JSON.parse(storedJornadas));
    }
  }, []);

  const adicionarJornada = () => {
    if (!novaJornada.nome.trim() || !novaJornada.horaInicio || !novaJornada.horaFim || !novaJornada.funcionarioId.trim()) {
      alert("Preencha todos os campos obrigatórios!");
      return;
    }

    const novaLista = [...jornadas, { ...novaJornada, id: jornadas.length + 1 }];
    setJornadas(novaLista);
    localStorage.setItem("jornadas", JSON.stringify(novaLista));

    setNovaJornada({
      nome: "",
      descricao: "",
      horaInicio: "",
      horaFim: "",
      intervaloInicio: "",
      intervaloFim: "",
      diasTrabalho: [],
      cargaHorariaSemanal: 0,
      toleranciaEntrada: 10,
      toleranciaSaida: 10,
      ativo: true,
      escalaEspecial: false,
      funcionarioId: "",
    });
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Jornadas de Trabalho</h2>
      <ul>
        {jornadas.map((jornada) => (
          <li key={jornada.id} className="border p-4 mb-2 rounded-md bg-gray-100">
            <h3 className="text-lg font-semibold">{jornada.nome}</h3>
            <p>{jornada.descricao}</p>
            <p><strong>Horário:</strong> {jornada.horaInicio} - {jornada.horaFim}</p>
            <p><strong>Funcionário ID:</strong> {jornada.funcionarioId}</p>
          </li>
        ))}
      </ul>

      <h3 className="text-lg font-bold mt-4">Adicionar Nova Jornada</h3>
      <input type="text" placeholder="Nome da Jornada" value={novaJornada.nome} onChange={(e) => setNovaJornada({ ...novaJornada, nome: e.target.value })} className="border p-2 rounded-md w-full" />
      <input type="time" placeholder="Hora Início" value={novaJornada.horaInicio} onChange={(e) => setNovaJornada({ ...novaJornada, horaInicio: e.target.value })} className="border p-2 rounded-md w-full mt-2" />
      <input type="time" placeholder="Hora Fim" value={novaJornada.horaFim} onChange={(e) => setNovaJornada({ ...novaJornada, horaFim: e.target.value })} className="border p-2 rounded-md w-full mt-2" />
      <input type="text" placeholder="ID do Funcionário" value={novaJornada.funcionarioId} onChange={(e) => setNovaJornada({ ...novaJornada, funcionarioId: e.target.value })} className="border p-2 rounded-md w-full mt-2" />
      <button onClick={adicionarJornada} className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4">Adicionar Jornada</button>
    </div>
  );
};

export default JornadasTab;
