import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginRegisterPage from "./LoginRegisterPage";
import Dashboard from "./DashboardPage";
import AdminPage from "./AdminPage"; // Importando a página de administração

const App = () => {
  return (
    <Router>
      <Routes>
        {/* Página inicial (login e registro) */}
        <Route path="/" element={<LoginRegisterPage />} />

        {/* Rota protegida do Dashboard */}
        <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />

        {/* Rota protegida da Administração */}
        <Route path="/admin" element={<ProtectedRoute><AdminPage /></ProtectedRoute>} />

        {/* Redirecionamento para a página inicial caso a rota não exista */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

// Componente para proteger as rotas autenticadas
const ProtectedRoute = ({ children }) => {
  const user = JSON.parse(localStorage.getItem("user") || "{}"); // Evita erro se 'user' for null
  return user.authenticated ? children : <Navigate to="/" />;
};

export default App;
