import React, { useState, useEffect } from "react";

const Jornada = () => {
  const [jornadas, setJornadas] = useState([]);
  const [funcionarios, setFuncionarios] = useState([]);
  const [novaJornada, setNovaJornada] = useState({
    funcionarioId: "",
    nome: "",
    descricao: "",
    horaInicio: "",
    horaFim: "",
    intervaloInicio: "",
    intervaloFim: "",
    diasTrabalho: [],
    cargaHorariaSemanal: 0,
    toleranciaEntrada: 10,
    toleranciaSaida: 10,
    ativo: true,
    escalaEspecial: false,
  });

  useEffect(() => {
    const storedJornadas = localStorage.getItem("jornadas");
    if (storedJornadas) {
      setJornadas(JSON.parse(storedJornadas));
    }

    const storedFuncionarios = localStorage.getItem("funcionarios");
    if (storedFuncionarios) {
      setFuncionarios(JSON.parse(storedFuncionarios));
    }
  }, []);

  const adicionarJornada = () => {
    if (!novaJornada.nome.trim() || !novaJornada.horaInicio || !novaJornada.horaFim || !novaJornada.funcionarioId) {
      alert("Preencha todos os campos obrigatórios!");
      return;
    }

    const novaLista = [...jornadas, { ...novaJornada, id: jornadas.length + 1 }];
    setJornadas(novaLista);
    localStorage.setItem("jornadas", JSON.stringify(novaLista));

    setNovaJornada({
      funcionarioId: "",
      nome: "",
      descricao: "",
      horaInicio: "",
      horaFim: "",
      intervaloInicio: "",
      intervaloFim: "",
      diasTrabalho: [],
      cargaHorariaSemanal: 0,
      toleranciaEntrada: 10,
      toleranciaSaida: 10,
      ativo: true,
      escalaEspecial: false,
    });
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Criar Jornada</h2>
      <select
        value={novaJornada.funcionarioId}
        onChange={(e) => setNovaJornada({ ...novaJornada, funcionarioId: e.target.value })}
        className="border p-2 rounded-md w-full mt-2"
      >
        <option value="">Selecione um funcionário</option>
        {funcionarios.map((funcionario) => (
          <option key={funcionario.id} value={funcionario.id}>
            {funcionario.nome}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Nome da Jornada"
        value={novaJornada.nome}
        onChange={(e) => setNovaJornada({ ...novaJornada, nome: e.target.value })}
        className="border p-2 rounded-md w-full mt-2"
      />
      <input
        type="time"
        placeholder="Hora Início"
        value={novaJornada.horaInicio}
        onChange={(e) => setNovaJornada({ ...novaJornada, horaInicio: e.target.value })}
        className="border p-2 rounded-md w-full mt-2"
      />
      <input
        type="time"
        placeholder="Hora Fim"
        value={novaJornada.horaFim}
        onChange={(e) => setNovaJornada({ ...novaJornada, horaFim: e.target.value })}
        className="border p-2 rounded-md w-full mt-2"
      />
      <button onClick={adicionarJornada} className="bg-blue-500 text-white p-2 rounded-md mt-4 w-full">
        Adicionar Jornada
      </button>
    </div>
  );
};

export default JornadasTab;
