import { useState, useEffect } from "react";

export default function ControlePonto() {
  const [ultimoPonto, setUltimoPonto] = useState(() => localStorage.getItem("ultimoPonto") || null);
  const [ultimoHorario, setUltimoHorario] = useState(() => localStorage.getItem("ultimoHorario") || null);

  useEffect(() => {
    localStorage.setItem("ultimoPonto", ultimoPonto);
    localStorage.setItem("ultimoHorario", ultimoHorario);
  }, [ultimoPonto, ultimoHorario]);

  function baterPonto(tipo) {
    const agora = new Date().toLocaleTimeString();
    
    if (tipo === "entrada") {
      if (ultimoPonto === "entrada") {
        alert("Você já registrou uma entrada. Registre uma saída primeiro.");
        return;
      }
      setUltimoPonto("entrada");
      setUltimoHorario(agora);
      alert(`Entrada registrada: ${agora}`);
      
      // Sugere a saída após 8 horas
      setTimeout(() => {
        alert("Já se passaram 8 horas. Hora de registrar a saída!");
      }, 8 * 60 * 60 * 1000);
    } else if (tipo === "saida") {
      if (ultimoPonto !== "entrada") {
        alert("Você precisa registrar uma entrada antes de registrar uma saída.");
        return;
      }
      setUltimoPonto("saida");
      alert(`Saída registrada: ${agora}`);
    }
  }

  return (
    <div className="p-4 border rounded shadow-md max-w-sm mx-auto text-center">
      <h2 className="text-xl font-bold">Registro de Ponto</h2>
      <p>Último ponto: {ultimoPonto || "Nenhum"}</p>
      <p>Horário: {ultimoHorario || "--:--:--"}</p>
      <div className="mt-4 space-x-2">
        <button className="px-4 py-2 bg-blue-500 text-white rounded" onClick={() => baterPonto("entrada")}>
          Registrar Entrada
        </button>
        <button className="px-4 py-2 bg-red-500 text-white rounded" onClick={() => baterPonto("saida")}>
          Registrar Saída
        </button>
      </div>
    </div>
  );
}
