import React, { useState, useEffect } from 'react';

const DashboardPage = ({ user }) => {
  const [isAdmin, setIsAdmin] = useState(true); // Simula usuário admin
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [lastAction, setLastAction] = useState(null);
  const [timeEntries, setTimeEntries] = useState([]);

  // Carrega os registros de ponto do localStorage ao iniciar
  useEffect(() => {
    const savedEntries = JSON.parse(localStorage.getItem('timeEntries')) || [];
    setTimeEntries(savedEntries);
  }, []);

  // Atualiza o relógio a cada segundo
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Formata a hora atual
  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  // Registra entrada ou saída
  const registerTimeEntry = (type) => {
    const now = new Date();
    const newEntry = {
      type,
      time: now.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' }),
      date: now.toLocaleDateString('pt-BR')
    };
    const updatedEntries = [newEntry, ...timeEntries];
    setTimeEntries(updatedEntries);
    setLastAction(`${type.charAt(0).toUpperCase() + type.slice(1)} registrada às ${newEntry.time}`);
    // Salva os registros atualizados no localStorage
    localStorage.setItem('timeEntries', JSON.stringify(updatedEntries));
  };

  // Função para obter as iniciais do nome do usuário
  const getInitials = (name) => {
    return name
      .split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      <header className="bg-purple-800 shadow-lg">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold">Dashboard</h1>
          <div className="relative">
            <button
              onClick={() => setShowProfileMenu(!showProfileMenu)}
              className="flex items-center space-x-2 focus:outline-none"
            >
              <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center">
                <span className="font-medium text-sm">{getInitials(user.name)}</span>
              </div>
              <span className="hidden md:inline-block">{user.name}</span>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            {showProfileMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-10">
                <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Perfil</a>
                <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Configurações</a>
                <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Sair</a>
              </div>
            )}
          </div>
        </div>
      </header>
      <main className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="bg-purple-800 rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold mb-4">Relógio</h2>
            <div className="text-4xl font-bold mb-4">{formatTime(currentTime)}</div>
            <div className="flex space-x-4">
              <button
                onClick={() => registerTimeEntry('entrada')}
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
              >
                Entrada
              </button>
              <button
                onClick={() => registerTimeEntry('saída')}
                className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded"
              >
                Saída
              </button>
            </div>
            {lastAction && (
              <p className="mt-4 text-sm">{lastAction}</p>
            )}
          </div>
          <div className="bg-purple-800 rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-semibold mb-4">Registros de Ponto</h2>
            <div className="overflow-y-auto max-h-64">
              <table className="w-full">
                <thead>
                  <tr>
                    <th className="text-left">Data</th>
                    <th className="text-left">Hora</th>
                    <th className="text-left">Tipo</th>
                  </tr>
                </thead>
                <tbody>
                  {timeEntries.map((entry, index) => (
                    <tr key={index}>
                      <td>{entry.date}</td>
                      <td>{entry.time}</td>
                      <td>{entry.type}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
        {isAdmin && (
          <div className="mt-8">
            <h2 className="text-2xl font-semibold mb-4">Painel de Administração</h2>
            {/* Adicione aqui os componentes do painel de administração */}
          </div>
        )}
      </main>
    </div>
  );
};

export default DashboardPage;
