import React, { useState, useEffect } from 'react';

const PontoBatidoTab = () => {
  // Estados
  const [timeEntries, setTimeEntries] = useState([]);
  const [filter, setFilter] = useState('pendente');
  const [filterEmployee, setFilterEmployee] = useState('');
  const [filterDate, setFilterDate] = useState('');
  const [employees, setEmployees] = useState([]);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [approvalReason, setApprovalReason] = useState('');
  const [rejectionReason, setRejectionReason] = useState('');
  
  // Carregar registros de ponto do localStorage
  useEffect(() => {
    const storedEntries = localStorage.getItem('timeEntries');
    if (storedEntries) {
      try {
        const parsedEntries = JSON.parse(storedEntries);
        setTimeEntries(parsedEntries);
        
        // Extrair lista única de funcionários para o filtro
        const uniqueEmployees = [...new Set(parsedEntries.map(entry => entry.employeeName))];
        setEmployees(uniqueEmployees);
      } catch (error) {
        console.error('Erro ao carregar registros de ponto:', error);
      }
    }
  }, []);
  
  // Filtrar os registros com base nos critérios selecionados
  const filteredEntries = timeEntries.filter(entry => {
    const statusMatch = filter === 'todos' || entry.status === filter;
    const employeeMatch = !filterEmployee || entry.employeeName === filterEmployee;
    const dateMatch = !filterDate || entry.date === filterDate;
    return statusMatch && employeeMatch && dateMatch;
  });
  
  // Função para abrir o modal de detalhes
  const openDetailsModal = (entry) => {
    setSelectedEntry(entry);
    setShowDetailsModal(true);
    setApprovalReason('');
    setRejectionReason('');
  };
  
  // Função para aprovar um registro
  const approveEntry = () => {
    const updatedEntries = timeEntries.map(entry => {
      if (entry === selectedEntry) {
        // Criar notificação para o usuário
        const newNotification = {
          id: Date.now(),
          userId: entry.employeeId,
          message: `Seu registro de ${entry.type} do dia ${entry.date} às ${entry.time} foi aprovado${approvalReason ? ': ' + approvalReason : '.'}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
        
        // Atualizar notificações do usuário
        const userNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
        localStorage.setItem('userNotifications', JSON.stringify([
          newNotification,
          ...userNotifications
        ]));
        
        return { ...entry, status: 'aprovado', approvalReason };
      }
      return entry;
    });
    
    setTimeEntries(updatedEntries);
    localStorage.setItem('timeEntries', JSON.stringify(updatedEntries));
    setShowDetailsModal(false);
  };
  
  // Função para rejeitar um registro
  const rejectEntry = () => {
    if (!rejectionReason) {
      alert('Por favor, forneça um motivo para a rejeição.');
      return;
    }
    
    const updatedEntries = timeEntries.map(entry => {
      if (entry === selectedEntry) {
        // Criar notificação para o usuário
        const newNotification = {
          id: Date.now(),
          userId: entry.employeeId,
          message: `Seu registro de ${entry.type} do dia ${entry.date} às ${entry.time} foi rejeitado: ${rejectionReason}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
        
        // Atualizar notificações do usuário
        const userNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
        localStorage.setItem('userNotifications', JSON.stringify([
          newNotification,
          ...userNotifications
        ]));
        
        return { ...entry, status: 'rejeitado', rejectionReason };
      }
      return entry;
    });
    
    setTimeEntries(updatedEntries);
    localStorage.setItem('timeEntries', JSON.stringify(updatedEntries));
    setShowDetailsModal(false);
  };
  
  return (
    <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold mb-6 flex items-center">
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
        Ponto Batido
      </h2>
      
      {/* Filtros */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div>
          <label className="block text-sm font-medium mb-2 text-purple-300">Status</label>
          <select 
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="w-full bg-purple-900 border border-purple-700 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
          >
            <option value="todos">Todos</option>
            <option value="pendente">Pendentes</option>
            <option value="aprovado">Aprovados</option>
            <option value="rejeitado">Rejeitados</option>
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-2 text-purple-300">Funcionário</label>
          <select 
            value={filterEmployee}
            onChange={(e) => setFilterEmployee(e.target.value)}
            className="w-full bg-purple-900 border border-purple-700 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
          >
            <option value="">Todos</option>
            {employees.map((employee, index) => (
              <option key={index} value={employee}>{employee}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium mb-2 text-purple-300">Data</label>
          <input 
            type="date" 
            value={filterDate}
            onChange={(e) => setFilterDate(e.target.value ? new Date(e.target.value).toLocaleDateString('pt-BR') : '')}
            className="w-full bg-purple-900 border border-purple-700 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
          />
        </div>
        <div className="flex items-end">
          <button 
            onClick={() => {
              setFilter('todos');
              setFilterEmployee('');
              setFilterDate('');
            }}
            className="bg-purple-700 hover:bg-purple-600 px-4 py-2 rounded-md flex items-center"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
            Limpar Filtros
          </button>
        </div>
      </div>
      
      {/* Tabela de Registros */}
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="text-purple-300 border-b border-purple-700">
              <th className="text-left p-3">Funcionário</th>
              <th className="text-left p-3">Data</th>
              <th className="text-left p-3">Hora</th>
              <th className="text-left p-3">Tipo</th>
              <th className="text-center p-3">Status</th>
              <th className="text-right p-3">Ações</th>
            </tr>
          </thead>
          <tbody>
            {filteredEntries.length === 0 ? (
              <tr>
                <td colSpan="6" className="text-center py-4 text-purple-300">Nenhum registro encontrado.</td>
              </tr>
            ) : (
              filteredEntries.map((entry, index) => (
                <tr key={index} className={`border-b border-purple-700 ${entry.status === 'pendente' ? 'bg-purple-900 bg-opacity-40' : ''}`}>
                  <td className="p-3">{entry.employeeName}</td>
                  <td className="p-3">{entry.date}</td>
                  <td className="p-3">{entry.time}</td>
                  <td className="p-3">
                    <span className={`inline-block px-2 py-1 rounded-full text-xs ${entry.type === 'entrada' ? 'bg-green-600' : 'bg-red-600'}`}>
                      {entry.type ? entry.type.toUpperCase() : 'N/A'}
                    </span>
                  </td>
                  <td className="p-3 text-center">
                    <span className={`inline-block px-2 py-1 rounded-full text-xs
                      ${entry.status === 'aprovado' ? 'bg-green-700' : 
                        entry.status === 'pendente' ? 'bg-yellow-600' : 'bg-red-700'}`}>
                      {entry.status ? entry.status.toUpperCase() : 'N/A'}
                    </span>
                  </td>
                  <td className="p-3 text-right">
                    <button
                      onClick={() => openDetailsModal(entry)}
                      className="bg-purple-600 hover:bg-purple-500 text-white px-3 py-1 rounded-md text-sm"
                    >
                      Detalhes
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
      
      {/* Modal de Detalhes */}
      {showDetailsModal && selectedEntry && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md" onClick={e => e.stopPropagation()}>
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold">Detalhes do Registro</h3>
              <button 
                onClick={() => setShowDetailsModal(false)}
                className="text-purple-300 hover:text-white"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            
            <div className="mb-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm text-purple-300 mb-1">Funcionário</label>
                  <p className="font-medium">{selectedEntry.employeeName}</p>
                </div>
                <div>
                  <label className="block text-sm text-purple-300 mb-1">Tipo</label>
                  <p className="font-medium">
                    <span className={`inline-block px-2 py-1 rounded-full text-xs ${selectedEntry.type === 'entrada' ? 'bg-green-600' : 'bg-red-600'}`}>
                      {selectedEntry.type ? selectedEntry.type.toUpperCase() : 'N/A'}
                    </span>
                  </p>
                </div>
                <div>
                  <label className="block text-sm text-purple-300 mb-1">Data</label>
                  <p className="font-medium">{selectedEntry.date}</p>
                </div>
                <div>
                  <label className="block text-sm text-purple-300 mb-1">Hora</label>
                  <p className="font-medium">{selectedEntry.time}</p>
                </div>
                <div>
                  <label className="block text-sm text-purple-300 mb-1">Registrado por</label>
                  <p className="font-medium">{selectedEntry.registeredBy}</p>
                </div>
                <div>
                  <label className="block text-sm text-purple-300 mb-1">Status</label>
                  <p className="font-medium">
                    <span className={`inline-block px-2 py-1 rounded-full text-xs
                      ${selectedEntry.status === 'aprovado' ? 'bg-green-700' : 
                        selectedEntry.status === 'pendente' ? 'bg-yellow-600' : 'bg-red-700'}`}>
                      {selectedEntry.status ? selectedEntry.status.toUpperCase() : 'N/A'}
                    </span>
                  </p>
                </div>
              </div>
              
              {selectedEntry.status === 'aprovado' && selectedEntry.approvalReason && (
                <div className="mt-4">
                  <label className="block text-sm text-purple-300 mb-1">Motivo da Aprovação</label>
                  <p className="bg-purple-800 p-2 rounded">{selectedEntry.approvalReason}</p>
                </div>
              )}
              
              {selectedEntry.status === 'rejeitado' && selectedEntry.rejectionReason && (
                <div className="mt-4">
                  <label className="block text-sm text-purple-300 mb-1">Motivo da Rejeição</label>
                  <p className="bg-purple-800 p-2 rounded">{selectedEntry.rejectionReason}</p>
                </div>
              )}
            </div>
            
            {selectedEntry.status === 'pendente' && (
              <div>
                <div className="mb-4">
                  <label className="block text-sm text-purple-300 mb-1">Comentário de Aprovação (opcional)</label>
                  <textarea 
                    value={approvalReason}
                    onChange={(e) => setApprovalReason(e.target.value)}
                    className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500 min-h-20"
                    placeholder="Adicione um comentário para a aprovação..."
                  />
                </div>
                
                <div className="mb-4">
                  <label className="block text-sm text-purple-300 mb-1">Motivo da Rejeição (obrigatório para rejeitar)</label>
                  <textarea 
                    value={rejectionReason}
                    onChange={(e) => setRejectionReason(e.target.value)}
                    className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500 min-h-20"
                    placeholder="Informe o motivo da rejeição..."
                  />
                </div>
                
                <div className="flex justify-end space-x-3">
                  <button 
                    onClick={rejectEntry}
                    className="px-4 py-2 bg-red-600 hover:bg-red-500 rounded-md"
                  >
                    Rejeitar
                  </button>
                  <button 
                    onClick={approveEntry}
                    className="px-4 py-2 bg-green-600 hover:bg-green-500 rounded-md"
                  >
                    Aprovar
                  </button>
                </div>
              </div>
            )}
            
            {selectedEntry.status !== 'pendente' && (
              <div className="flex justify-end">
                <button 
                  onClick={() => setShowDetailsModal(false)}
                  className="px-4 py-2 bg-purple-600 hover:bg-purple-500 rounded-md"
                >
                  Fechar
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default PontoBatidoTab;
