import React, { useState, useEffect, useRef } from 'react';

const PontoBatidoTab = () => {
  // Estado para os registros de ponto
  const [pontoRegistros, setPontoRegistros] = useState(() => {
    const storedRegistros = localStorage.getItem('pontoRegistros');
    return storedRegistros ? JSON.parse(storedRegistros) : [
      { 
        id: 1, 
        funcionarioId: 101, 
        funcionarioNome: 'João Silva', 
        data: '19/03/2025', 
        registros: [
          { tipo: 'entrada', hora: '08:05', status: 'regular' },
          { tipo: 'saída', hora: '12:00', status: 'regular' },
          { tipo: 'entrada', hora: '13:10', status: 'regular' },
          { tipo: 'saída', hora: '--:--', status: 'pendente' }
        ]
      },
      { 
        id: 2, 
        funcionarioId: 102, 
        funcionarioNome: 'Maria Oliveira', 
        data: '19/03/2025', 
        registros: [
          { tipo: 'entrada', hora: '08:15', status: 'atrasado' },
          { tipo: 'saída', hora: '12:05', status: 'regular' },
          { tipo: 'entrada', hora: '13:00', status: 'regular' },
          { tipo: 'saída', hora: '17:10', status: 'regular' }
        ]
      },
      { 
        id: 3, 
        funcionarioId: 103, 
        funcionarioNome: 'Carlos Pereira', 
        data: '19/03/2025', 
        registros: [
          { tipo: 'entrada', hora: '09:20', status: 'atrasado' },
          { tipo: 'saída', hora: '12:15', status: 'regular' },
          { tipo: 'entrada', hora: '13:10', status: 'regular' },
          { tipo: 'saída', hora: '18:35', status: 'hora extra' }
        ]
      },
      { 
        id: 4, 
        funcionarioId: 104, 
        funcionarioNome: 'Ana Souza', 
        data: '19/03/2025', 
        registros: [
          { tipo: 'entrada', hora: '--:--', status: 'falta' },
          { tipo: 'saída', hora: '--:--', status: 'falta' },
          { tipo: 'entrada', hora: '--:--', status: 'falta' },
          { tipo: 'saída', hora: '--:--', status: 'falta' }
        ]
      },
      // Dias anteriores
      { 
        id: 5, 
        funcionarioId: 101, 
        funcionarioNome: 'João Silva', 
        data: '18/03/2025', 
        registros: [
          { tipo: 'entrada', hora: '08:00', status: 'regular' },
          { tipo: 'saída', hora: '12:00', status: 'regular' },
          { tipo: 'entrada', hora: '13:00', status: 'regular' },
          { tipo: 'saída', hora: '17:05', status: 'regular' }
        ]
      },
      { 
        id: 6, 
        funcionarioId: 102, 
        funcionarioNome: 'Maria Oliveira', 
        data: '18/03/2025', 
        registros: [
          { tipo: 'entrada', hora: '08:10', status: 'regular' },
          { tipo: 'saída', hora: '12:00', status: 'regular' },
          { tipo: 'entrada', hora: '13:05', status: 'regular' },
          { tipo: 'saída', hora: '17:00', status: 'regular' }
        ]
      }
    ];
  });
  
  // Estado para filtros
  const [filtros, setFiltros] = useState({
    data: '',
    funcionario: '',
    status: ''
  });
  
  // Lista de funcionários para o filtro
  const funcionarios = [...new Set(pontoRegistros.map(registro => registro.funcionarioNome))];
  
  // Lista de datas para o filtro
  const datas = [...new Set(pontoRegistros.map(registro => registro.data))];
  
  // Função para aplicar os filtros
  const registrosFiltrados = pontoRegistros.filter(registro => {
    return (
      (filtros.data === '' || registro.data === filtros.data) &&
      (filtros.funcionario === '' || registro.funcionarioNome === filtros.funcionario) &&
      (filtros.status === '' || registro.registros.some(r => r.status === filtros.status))
    );
  });

  // Ordenar por data mais recente
  const registrosOrdenados = [...registrosFiltrados].sort((a, b) => {
    // Converter datas do formato DD/MM/YYYY para objetos Date para comparação
    const [diaA, mesA, anoA] = a.data.split('/').map(Number);
    const [diaB, mesB, anoB] = b.data.split('/').map(Number);
    
    const dateA = new Date(anoA, mesA - 1, diaA);
    const dateB = new Date(anoB, mesB - 1, diaB);
    
    return dateB - dateA; // Ordem decrescente (mais recente primeiro)
  });
  
  // Função para notificar funcionário sobre ponto não registrado
  const notificarFuncionario = (funcionarioId, funcionarioNome) => {
    // Adicionar notificação ao sistema
    const notificacoes = JSON.parse(localStorage.getItem('userNotifications') || '[]');
    notificacoes.push({
      id: Date.now(),
      userId: funcionarioId,
      message: 'Por favor, registre seu ponto pendente do dia de hoje.',
      date: new Date().toLocaleDateString('pt-BR'),
      read: false
    });
    localStorage.setItem('userNotifications', JSON.stringify(notificacoes));
    
    alert(`Notificação enviada para ${funcionarioNome} sobre ponto pendente.`);
  };
  
  // Referências para os inputs de edição de ponto
  const horaInputRefs = useRef({});
  
  // Estado para controlar qual registro está sendo editado
  const [editandoRegistro, setEditandoRegistro] = useState(null);
  
  // Função para iniciar edição de um registro
  const iniciarEdicaoPonto = (registroId, indiceRegistro) => {
    setEditandoRegistro({ registroId, indiceRegistro });
    
    // Focar no input após renderização
    setTimeout(() => {
      if (horaInputRefs.current[`${registroId}-${indiceRegistro}`]) {
        horaInputRefs.current[`${registroId}-${indiceRegistro}`].focus();
      }
    }, 100);
  };
  
  // Função para salvar edição de ponto
  const salvarEdicaoPonto = (registroId, indiceRegistro) => {
    const inputRef = horaInputRefs.current[`${registroId}-${indiceRegistro}`];
    if (inputRef && inputRef.value) {
      atualizarPonto(registroId, indiceRegistro, inputRef.value);
    }
    setEditandoRegistro(null);
  };
  
  // Função para cancelar edição
  const cancelarEdicaoPonto = () => {
    setEditandoRegistro(null);
  };
  
  return (
    <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
      <h1 className="text-2xl font-bold mb-6">Registros de Ponto</h1>
      
      {/* Filtros */}
      <div className="bg-purple-900 bg-opacity-40 rounded-lg p-4 mb-6">
        <h2 className="text-xl font-semibold mb-4">Filtros</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-sm text-purple-300 mb-1">Data</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.data}
              onChange={(e) => setFiltros({...filtros, data: e.target.value})}
            >
              <option value="">Todas as datas</option>
              {datas.map((data, index) => (
                <option key={index} value={data}>{data}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Funcionário</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.funcionario}
              onChange={(e) => setFiltros({...filtros, funcionario: e.target.value})}
            >
              <option value="">Todos os funcionários</option>
              {funcionarios.map((funcionario, index) => (
                <option key={index} value={funcionario}>{funcionario}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Status</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.status}
              onChange={(e) => setFiltros({...filtros, status: e.target.value})}
            >
              <option value="">Todos os status</option>
              <option value="regular">Regular</option>
              <option value="atrasado">Atrasado</option>
              <option value="hora extra">Hora Extra</option>
              <option value="falta">Falta</option>
              <option value="falta justificada">Falta Justificada</option>
              <option value="pendente">Pendente</option>
              <option value="ajustado">Ajustado</option>
            </select>
          </div>
        </div>
      </div>
      
      {/* Tabela de Registros */}
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead>
            <tr className="text-purple-300 text-sm border-b border-purple-700">
              <th className="px-4 py-2 text-left">Funcionário</th>
              <th className="px-4 py-2 text-left">Data</th>
              <th className="px-4 py-2 text-left">Entrada 1</th>
              <th className="px-4 py-2 text-left">Saída 1</th>
              <th className="px-4 py-2 text-left">Entrada 2</th>
              <th className="px-4 py-2 text-left">Saída 2</th>
              <th className="px-4 py-2 text-left">Ações</th>
            </tr>
          </thead>
          <tbody>
            {registrosOrdenados.map((registro) => (
              <tr key={registro.id} className="border-b border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                <td className="px-4 py-3">{registro.funcionarioNome}</td>
                <td className="px-4 py-3">{registro.data}</td>
                
                {/* Entrada 1 */}
                <td className="px-4 py-3">
                  {editandoRegistro && editandoRegistro.registroId === registro.id && editandoRegistro.indiceRegistro === 0 ? (
                    <div className="flex items-center space-x-2">
                      <input 
                        type="time" 
                        defaultValue={registro.registros[0].hora !== '--:--' ? registro.registros[0].hora : ''}
                        className="bg-purple-800 border border-purple-600 rounded-md p-1 text-white w-24"
                        ref={el => horaInputRefs.current[`${registro.id}-0`] = el}
                      />
                      <button 
                        onClick={() => salvarEdicaoPonto(registro.id, 0)}
                        className="text-green-500 hover:text-green-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                      </button>
                      <button 
                        onClick={cancelarEdicaoPonto}
                        className="text-red-500 hover:text-red-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <span className={registro.registros[0].status === 'falta' || registro.registros[0].status === 'pendente' ? 'text-red-400' : ''}>
                        {registro.registros[0].hora}
                      </span>
                      <div className="ml-2">{renderizarStatus(registro.registros[0].status)}</div>
                      <button 
                        onClick={() => iniciarEdicaoPonto(registro.id, 0)}
                        className="text-purple-300 hover:text-white ml-auto"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
                        </svg>
                      </button>
                    </div>
                  )}
                </td>
                
                {/* Saída 1 */}
                <td className="px-4 py-3">
                  {editandoRegistro && editandoRegistro.registroId === registro.id && editandoRegistro.indiceRegistro === 1 ? (
                    <div className="flex items-center space-x-2">
                      <input 
                        type="time" 
                        defaultValue={registro.registros[1].hora !== '--:--' ? registro.registros[1].hora : ''}
                        className="bg-purple-800 border border-purple-600 rounded-md p-1 text-white w-24"
                        ref={el => horaInputRefs.current[`${registro.id}-1`] = el}
                      />
                      <button 
                        onClick={() => salvarEdicaoPonto(registro.id, 1)}
                        className="text-green-500 hover:text-green-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                      </button>
                      <button 
                        onClick={cancelarEdicaoPonto}
                        className="text-red-500 hover:text-red-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <span className={registro.registros[1].status === 'falta' || registro.registros[1].status === 'pendente' ? 'text-red-400' : ''}>
                        {registro.registros[1].hora}
                      </span>
                      <div className="ml-2">{renderizarStatus(registro.registros[1].status)}</div>
                      <button 
                        onClick={() => iniciarEdicaoPonto(registro.id, 1)}
                        className="text-purple-300 hover:text-white ml-auto"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
                        </svg>
                      </button>
                    </div>
                  )}
                </td>
                
                {/* Entrada 2 */}
                <td className="px-4 py-3">
                  {editandoRegistro && editandoRegistro.registroId === registro.id && editandoRegistro.indiceRegistro === 2 ? (
                    <div className="flex items-center space-x-2">
                      <input 
                        type="time" 
                        defaultValue={registro.registros[2].hora !== '--:--' ? registro.registros[2].hora : ''}
                        className="bg-purple-800 border border-purple-600 rounded-md p-1 text-white w-24"
                        ref={el => horaInputRefs.current[`${registro.id}-2`] = el}
                      />
                      <button 
                        onClick={() => salvarEdicaoPonto(registro.id, 2)}
                        className="text-green-500 hover:text-green-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                      </button>
                      <button 
                        onClick={cancelarEdicaoPonto}
                        className="text-red-500 hover:text-red-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <span className={registro.registros[2].status === 'falta' || registro.registros[2].status === 'pendente' ? 'text-red-400' : ''}>
                        {registro.registros[2].hora}
                      </span>
                      <div className="ml-2">{renderizarStatus(registro.registros[2].status)}</div>
                      <button 
                        onClick={() => iniciarEdicaoPonto(registro.id, 2)}
                        className="text-purple-300 hover:text-white ml-auto"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
                        </svg>
                      </button>
                    </div>
                  )}
                </td>
                
                {/* Saída 2 */}
                <td className="px-4 py-3">
                  {editandoRegistro && editandoRegistro.registroId === registro.id && editandoRegistro.indiceRegistro === 3 ? (
                    <div className="flex items-center space-x-2">
                      <input 
                        type="time" 
                        defaultValue={registro.registros[3].hora !== '--:--' ? registro.registros[3].hora : ''}
                        className="bg-purple-800 border border-purple-600 rounded-md p-1 text-white w-24"
                        ref={el => horaInputRefs.current[`${registro.id}-3`] = el}
                      />
                      <button 
                        onClick={() => salvarEdicaoPonto(registro.id, 3)}
                        className="text-green-500 hover:text-green-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                      </button>
                      <button 
                        onClick={cancelarEdicaoPonto}
                        className="text-red-500 hover:text-red-400"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <span className={registro.registros[3].status === 'falta' || registro.registros[3].status === 'pendente' ? 'text-red-400' : ''}>
                        {registro.registros[3].hora}
                      </span>
                      <div className="ml-2">{renderizarStatus(registro.registros[3].status)}</div>
                      <button 
                        onClick={() => iniciarEdicaoPonto(registro.id, 3)}
                        className="text-purple-300 hover:text-white ml-auto"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z" />
                        </svg>
                      </button>
                    </div>
                  )}
                </td>
                
                {/* Ações */}
                <td className="px-4 py-3">
                  <div className="flex space-x-2 items-center">
                    {/* Notificar funcionário */}
                    {registro.registros.some(r => r.status === 'pendente') && (
                      <button 
                        onClick={() => notificarFuncionario(registro.funcionarioId, registro.funcionarioNome)}
                        className="bg-purple-600 hover:bg-purple-700 text-white rounded p-1 text-xs flex items-center"
                        title="Notificar funcionário"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
                        </svg>
                        Notificar
                      </button>
                    )}
                    
                    {/* Justificar falta */}
                    {registro.registros.some(r => r.status === 'falta') && (
                      <button 
                        onClick={() => marcarFaltaJustificada(registro.id)}
                        className="bg-orange-600 hover:bg-orange-700 text-white rounded p-1 text-xs flex items-center"
                        title="Marcar falta como justificada"
                      >
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        Justificar
                      </button>
                    )}
                    
                    {/* Exportar registro */}
                    <button 
                      className="bg-blue-600 hover:bg-blue-700 text-white rounded p-1 text-xs flex items-center"
                      title="Exportar registro"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12" />
                      </svg>
                      Exportar
                    </button>
                  </div>
                </td>
              </tr>
            ))}
            
            {registrosOrdenados.length === 0 && (
              <tr>
                <td colSpan="7" className="px-4 py-6 text-center text-purple-300">
                  Nenhum registro encontrado com os filtros selecionados.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
      
      {/* Botões de ação */}
      <div className="mt-6 flex flex-wrap gap-4">
        <button className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12" />
          </svg>
          Exportar Relatório
        </button>
        
        <button className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
          </svg>
          Notificar Todos com Pendências
        </button>
        
        <button className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded flex items-center">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          Gerar Espelho de Ponto
        </button>
      </div>
    </div>
  );
  
  // Função para marcar falta justificada
  const marcarFaltaJustificada = (registroId) => {
    const registroAtualizado = pontoRegistros.find(r => r.id === registroId);
    
    if (registroAtualizado) {
      registroAtualizado.registros = registroAtualizado.registros.map(r => {
        if (r.status === 'falta') {
          return { ...r, status: 'falta justificada' };
        }
        return r;
      });
      
      const novosRegistros = pontoRegistros.map(r => 
        r.id === registroId ? registroAtualizado : r
      );
      
      setPontoRegistros(novosRegistros);
      localStorage.setItem('pontoRegistros', JSON.stringify(novosRegistros));
      
      alert(`Falta justificada registrada para ${registroAtualizado.funcionarioNome} em ${registroAtualizado.data}.`);
    }
  };
  
  // Função para atualizar o ponto
  const atualizarPonto = (registroId, index, novaHora) => {
    const registroAtualizado = {...pontoRegistros.find(r => r.id === registroId)};
    
    if (registroAtualizado && registroAtualizado.registros[index]) {
      const registrosAtualizados = [...registroAtualizado.registros];
      registrosAtualizados[index] = {
        ...registrosAtualizados[index],
        hora: novaHora,
        status: 'ajustado'
      };
      
      registroAtualizado.registros = registrosAtualizados;
      
      const novosRegistros = pontoRegistros.map(r => 
        r.id === registroId ? registroAtualizado : r
      );
      
      setPontoRegistros(novosRegistros);
      localStorage.setItem('pontoRegistros', JSON.stringify(novosRegistros));
      
      // Adicionar notificação para o funcionário
      const notificacoes = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      notificacoes.push({
        id: Date.now(),
        userId: registroAtualizado.funcionarioId,
        message: `Seu registro de ponto do dia ${registroAtualizado.data} foi ajustado pelo administrador.`,
        date: new Date().toLocaleDateString('pt-BR'),
        read: false
      });
      localStorage.setItem('userNotifications', JSON.stringify(notificacoes));
      
      alert(`Ponto atualizado para ${registroAtualizado.funcionarioNome} em ${registroAtualizado.data}.`);
    }
  };
  
  // Salvar pontoRegistros no localStorage quando houver mudanças
  useEffect(() => {
    localStorage.setItem('pontoRegistros', JSON.stringify(pontoRegistros));
  }, [pontoRegistros]);
  
  // Função para renderizar o status com cor apropriada
  const renderizarStatus = (status) => {
    let corClasse = '';
    let texto = status.toUpperCase();
    
    switch(status) {
      case 'regular':
        corClasse = 'bg-green-600';
        break;
      case 'atrasado':
        corClasse = 'bg-yellow-600';
        break;
      case 'hora extra':
        corClasse = 'bg-blue-600';
        break;
      case 'falta':
        corClasse = 'bg-red-600';
        break;
      case 'falta justificada':
        corClasse = 'bg-orange-600';
        texto = 'JUSTIFICADA';
        break;
      case 'pendente':
        corClasse = 'bg-purple-600';
        break;
      case 'ajustado':
        corClasse = 'bg-indigo-600';
        break;
      default:
        corClasse = 'bg-gray-600';
    }
    
    return (
      <span className={`inline-block px-2 py-1 rounded-full text-xs ${corClasse}`}>
        {texto}
      </span>
    );
  }};
