import React, { useState, useEffect } from 'react';

const PontoBatidoTab = () => {
  // Estado para armazenar funcionários
  const [employees, setEmployees] = useState(() => {
    const storedEmployees = localStorage.getItem('employees');
    return storedEmployees ? JSON.parse(storedEmployees) : [
      { id: 1, name: 'João Silva', email: 'joao.silva@empresa.com', department: 'TI' },
      { id: 2, name: 'Maria Oliveira', email: 'maria.oliveira@empresa.com', department: 'RH' },
      { id: 3, name: 'Carlos Pereira', email: 'carlos.pereira@empresa.com', department: 'Financeiro' },
      { id: 4, name: 'Ana Santos', email: 'ana.santos@empresa.com', department: 'Marketing' },
      { id: 5, name: 'Bruno Almeida', email: 'bruno.almeida@empresa.com', department: 'Operações' }
    ];
  });
  
  // Obter dados do usuário (administrador) logado
  const [adminData, setAdminData] = useState(() => {
    const storedUser = JSON.parse(localStorage.getItem('user') || '{}');
    return {
      name: storedUser.name || 'Administrador',
      initials: storedUser.name ? storedUser.name.charAt(0) : 'A',
      isAdmin: true
    };
  });
  
  // Estado para armazenar registros de ponto
  const [timeEntries, setTimeEntries] = useState(() => {
    const storedEntries = localStorage.getItem('timeEntries');
    return storedEntries ? JSON.parse(storedEntries) : [
      { id: 1, employeeId: 1, employeeName: 'João Silva', type: 'entrada', time: '08:03', date: '20/03/2025', status: 'pendente', notified: false, registeredBy: 'João Silva' },
      { id: 2, employeeId: 1, employeeName: 'João Silva', type: 'saída', time: '12:00', date: '20/03/2025', status: 'pendente', notified: false, registeredBy: 'João Silva' },
      { id: 3, employeeId: 1, employeeName: 'João Silva', type: 'entrada', time: '13:05', date: '20/03/2025', status: 'pendente', notified: false, registeredBy: 'João Silva' },
      { id: 4, employeeId: 2, employeeName: 'Maria Oliveira', type: 'entrada', time: '08:00', date: '20/03/2025', status: 'pendente', notified: false, registeredBy: 'Maria Oliveira' },
      { id: 5, employeeId: 3, employeeName: 'Carlos Pereira', type: 'entrada', time: '08:15', date: '20/03/2025', status: 'aprovado', notified: true, registeredBy: 'Carlos Pereira' },
      { id: 6, employeeId: 3, employeeName: 'Carlos Pereira', type: 'saída', time: '12:05', date: '20/03/2025', status: 'aprovado', notified: true, registeredBy: 'Carlos Pereira' },
    ];
  });
  
  // Estados para modais
  const [showApprovalModal, setShowApprovalModal] = useState(false);
  const [showEmployeeEntryModal, setShowEmployeeEntryModal] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [activeFilter, setActiveFilter] = useState('pendentes');
  const [searchTerm, setSearchTerm] = useState('');
  
  // State para novo registro de ponto
  const [newEntry, setNewEntry] = useState({
    employeeId: '',
    type: 'entrada',
    time: '',
    date: new Date().toISOString().split('T')[0],
    status: 'aprovado',
    registeredBy: adminData.name
  });
  
  // Salvar dados no localStorage quando mudarem
  useEffect(() => {
    localStorage.setItem('timeEntries', JSON.stringify(timeEntries));
    
    // Atualizar notificações dos funcionários quando status de pontos forem atualizados
    const pendingNotifications = timeEntries
      .filter(entry => entry.status !== 'pendente' && !entry.notified)
      .map(entry => {
        const status = entry.status === 'aprovado' ? 'aprovado' : 'rejeitado';
        return {
          id: Date.now() + Math.random(),
          userId: entry.employeeId,
          message: `Seu registro de ${entry.type} do dia ${entry.date} às ${entry.time} foi ${status} por ${adminData.name}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
      });
    
    if (pendingNotifications.length > 0) {
      const currentNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      localStorage.setItem('userNotifications', JSON.stringify([
        ...pendingNotifications,
        ...currentNotifications
      ]));
      
      // Marcar como notificado
      setTimeEntries(timeEntries.map(entry => 
        !entry.notified && entry.status !== 'pendente' ? { ...entry, notified: true } : entry
      ));
    }
  }, [timeEntries, adminData.name]);
  
  // Filtrar entradas com base no filtro ativo e termo de busca
  const filteredEntries = timeEntries.filter(entry => {
    const matchesFilter = 
      (activeFilter === 'pendentes' && entry.status === 'pendente') ||
      (activeFilter === 'aprovados' && entry.status === 'aprovado') ||
      (activeFilter === 'rejeitados' && entry.status === 'rejeitado') ||
      (activeFilter === 'todos');
    
    const searchLower = searchTerm.toLowerCase();
    const matchesSearch = 
      !searchTerm || 
      entry.employeeName.toLowerCase().includes(searchLower) ||
      entry.date.includes(searchTerm) ||
      entry.type.toLowerCase().includes(searchLower) ||
      (entry.registeredBy && entry.registeredBy.toLowerCase().includes(searchLower));
    
    return matchesFilter && matchesSearch;
  });
  
  // Função para abrir modal de aprovação
  const openApprovalModal = (entry) => {
    setSelectedEntry(entry);
    setShowApprovalModal(true);
  };
  
  // Função para aprovar ou rejeitar registro de ponto
  const handleEntryStatus = (status) => {
    setTimeEntries(timeEntries.map(entry => 
      entry.id === selectedEntry.id ? { 
        ...entry, 
        status, 
        notified: false, 
        reviewedBy: adminData.name // Adicionar o nome de quem aprovou/rejeitou
      } : entry
    ));
    setShowApprovalModal(false);
  };
  
  // Função para abrir modal de novo registro para um funcionário
  const openEmployeeEntryModal = (employee) => {
    setSelectedEmployee(employee);
    setNewEntry({
      ...newEntry,
      employeeId: employee.id,
      employeeName: employee.name,
      registeredBy: employee.name // Usar o nome do funcionário selecionado como registrador
    });
    setShowEmployeeEntryModal(true);
  };
  
  // Função para criar um novo registro de ponto
  const createTimeEntry = () => {
    const formattedDate = new Date(newEntry.date).toLocaleDateString('pt-BR');
    
    const newEntryData = {
      id: timeEntries.length + 1,
      employeeId: parseInt(newEntry.employeeId),
      employeeName: selectedEmployee.name,
      type: newEntry.type,
      time: newEntry.time,
      date: formattedDate,
      status: newEntry.status,
      notified: newEntry.status !== 'pendente' ? false : null,
      registeredBy: newEntry.registeredBy, // Preservar quem registrou o ponto
      createdBy: adminData.name // Adicionar quem criou o registro (admin)
    };
    
    setTimeEntries([newEntryData, ...timeEntries]);
    
    // Se for aprovado, criar notificação para o funcionário
    if (newEntry.status === 'aprovado') {
      const notificationMessage = {
        id: Date.now() + Math.random(),
        userId: parseInt(newEntry.employeeId),
        message: `Novo registro de ${newEntry.type} para o dia ${formattedDate} às ${newEntry.time} foi adicionado por ${adminData.name}`,
        date: new Date().toLocaleDateString('pt-BR'),
        read: false
      };
      
      const currentNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      localStorage.setItem('userNotifications', JSON.stringify([
        notificationMessage,
        ...currentNotifications
      ]));
    }
    
    setShowEmployeeEntryModal(false);
  };
  
  // Manipuladores para os inputs do formulário
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEntry(prev => ({ ...prev, [name]: value }));
  };
  
  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h1 className="text-2xl font-bold mb-1">Ponto Batido</h1>
          <p className="text-purple-300">Visualize e aprove os registros de ponto dos funcionários</p>
        </div>
        
        <div className="flex flex-col sm:flex-row gap-3">
          <div className="relative">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Buscar por nome, data..."
              className="w-full sm:w-64 px-3 py-2 pl-10 bg-purple-800 bg-opacity-40 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 absolute left-3 top-2.5 text-purple-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
            </svg>
          </div>
        </div>
      </div>
      
      {/* Filtros */}
      <div className="bg-purple-800 bg-opacity-40 rounded-lg p-1 flex flex-wrap space-x-1">
        <button
          onClick={() => setActiveFilter('pendentes')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'pendentes' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Pendentes
        </button>
        <button
          onClick={() => setActiveFilter('aprovados')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'aprovados' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Aprovados
        </button>
        <button
          onClick={() => setActiveFilter('rejeitados')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'rejeitados' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Rejeitados
        </button>
        <button
          onClick={() => setActiveFilter('todos')}
          className={`px-4 py-2 rounded-md ${activeFilter === 'todos' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Todos
        </button>
      </div>
      
      {/* Tabela de registros */}
      <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
        <div className="p-4 border-b border-purple-700 flex justify-between items-center">
          <h2 className="text-xl font-semibold">Registros de Ponto</h2>
          <div className="flex gap-2">
            <button
              onClick={() => setActiveFilter('funcionarios')}
              className="bg-purple-600 hover:bg-purple-500 px-3 py-2 rounded-md text-sm flex items-center"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
              Registrar Ponto
            </button>
          </div>
        </div>
        
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="text-purple-300 text-sm">
                <th className="text-left p-4">Funcionário</th>
                <th className="text-left p-4">Data</th>
                <th className="text-left p-4">Hora</th>
                <th className="text-left p-4">Tipo</th>
                <th className="text-left p-4">Status</th>
                <th className="text-left p-4">Registrado Por</th>
                {activeFilter !== 'pendentes' && <th className="text-left p-4">Revisado Por</th>}
                <th className="text-left p-4">Ações</th>
              </tr>
            </thead>
            <tbody>
              {filteredEntries.length === 0 ? (
                <tr>
                  <td colSpan={activeFilter !== 'pendentes' ? 8 : 7} className="p-4 text-center">Nenhum registro de ponto encontrado.</td>
                </tr>
              ) : (
                filteredEntries.map(entry => (
                  <tr key={entry.id} className="border-t border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                    <td className="p-4">{entry.employeeName}</td>
                    <td className="p-4">{entry.date}</td>
                    <td className="p-4">{entry.time}</td>
                    <td className="p-4">
                      <span className={`inline-block px-2 py-1 rounded-full text-xs ${entry.type === 'entrada' ? 'bg-green-600' : 'bg-red-600'}`}>
                        {entry.type.toUpperCase()}
                      </span>
                    </td>
                    <td className="p-4">
                      <span className={`inline-block px-2 py-1 rounded-full text-xs 
                        ${entry.status === 'aprovado' ? 'bg-green-600' : 
                          entry.status === 'pendente' ? 'bg-yellow-600' : 'bg-red-600'}`}
                      >
                        {entry.status.toUpperCase()}
                      </span>
                    </td>
                    <td className="p-4">{entry.registeredBy || entry.employeeName}</td>
                    {activeFilter !== 'pendentes' && (
                      <td className="p-4">{entry.reviewedBy || (entry.status !== 'pendente' ? adminData.name : '-')}</td>
                    )}
                    <td className="p-4">
                      {entry.status === 'pendente' && (
                        <div className="flex space-x-2">
                          <button
                            onClick={() => openApprovalModal(entry)}
                            className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded-md text-sm"
                          >
                            Revisar
                          </button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
      
      {/* Visualização de funcionários para registrar ponto diretamente */}
      {activeFilter === 'funcionarios' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg mt-6">
          <div className="p-4 border-b border-purple-700">
            <h2 className="text-xl font-semibold">Registrar Ponto para Funcionário</h2>
            <p className="text-purple-300 text-sm">Selecione um funcionário para registrar um ponto manualmente</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
            {employees.map(employee => (
              <div key={employee.id} className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h3 className="font-bold text-lg">{employee.name}</h3>
                <p className="text-purple-300">{employee.department}</p>
                <p className="text-sm mb-4">{employee.email}</p>
                
                <button
                  onClick={() => openEmployeeEntryModal(employee)}
                  className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded text-sm w-full"
                >
                  Registrar Ponto
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
      
      {/* Modal de aprovação/rejeição */}
      {showApprovalModal && selectedEntry && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">Revisar Registro de Ponto</h3>
            
            <div className="mb-4">
              <p><span className="text-purple-300">Funcionário:</span> {selectedEntry.employeeName}</p>
              <p><span className="text-purple-300">Data:</span> {selectedEntry.date}</p>
              <p><span className="text-purple-300">Hora:</span> {selectedEntry.time}</p>
              <p><span className="text-purple-300">Tipo:</span> {selectedEntry.type.toUpperCase()}</p>
              <p><span className="text-purple-300">Registrado Por:</span> {selectedEntry.registeredBy || selectedEntry.employeeName}</p>
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowApprovalModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={() => handleEntryStatus('rejeitado')}
                className="px-4 py-2 bg-red-600 hover:bg-red-500 rounded-md"
              >
                Rejeitar
              </button>
              <button 
                onClick={() => handleEntryStatus('aprovado')}
                className="px-4 py-2 bg-green-600 hover:bg-green-500 rounded-md"
              >
                Aprovar
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal para registrar ponto para funcionário */}
      {showEmployeeEntryModal && selectedEmployee && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">Registrar Ponto para {selectedEmployee.name}</h3>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Tipo de Registro</label>
              <select
                name="type"
                value={newEntry.type}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value="entrada">Entrada</option>
                <option value="saída">Saída</option>
              </select>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Data</label>
              <input 
                type="date" 
                name="date"
                value={newEntry.date}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Hora</label>
              <input 
                type="time" 
                name="time"
                value={newEntry.time}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Status</label>
              <select
                name="status"
                value={newEntry.status}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value="aprovado">Aprovado</option>
                <option value="pendente">Pendente</option>
              </select>
            </div>
            
            <div className="mb-6">
              <label className="block text-sm font-medium mb-2">Registrado Por</label>
              <select
                name="registeredBy"
                value={newEntry.registeredBy}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                <option value={selectedEmployee.name}>{selectedEmployee.name} (Próprio Funcionário)</option>
                <option value={adminData.name}>{adminData.name} (Administrador)</option>
              </select>
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowEmployeeEntryModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={createTimeEntry}
                disabled={!newEntry.time || !newEntry.date}
                className={`px-4 py-2 rounded-md ${(newEntry.time && newEntry.date) ? 'bg-purple-600 hover:bg-purple-500' : 'bg-purple-900 opacity-50 cursor-not-allowed'}`}
              >
                Registrar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PontoBatidoTab;
