import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Proteção de rota: Se o usuário não estiver logado, redireciona para a tela de login
    if (!localStorage.getItem('user')) {
      navigate('/');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('user'); // Remove os dados do usuário
    navigate('/'); // Redireciona para a tela de login
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white flex flex-col items-center justify-center">
      {/* Cabeçalho */}
      <h1 className="text-4xl font-bold mb-6">Bem-vindo ao Dashboard!</h1>
      
      {/* Card de Informações */}
      <div className="bg-purple-800 bg-opacity-50 p-6 rounded-lg shadow-lg w-96 text-center border border-purple-500">
        <p className="text-lg mb-4">
          Você está logado! Aqui você pode acessar suas configurações e informações.
        </p>
        <button 
          onClick={handleLogout} 
          className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg"
        >
          Sair
        </button>
      </div>
    </div>
  );
};

export default DashboardPage;
