import React, { useState, useEffect } from 'react';
import FuncionariosService from './FuncionariosService';

const ModalGerenciarFuncionarios = ({
  jornadaSelecionada, 
  funcionariosPorJornada, 
  setFuncionariosPorJornada, 
  todosFuncionarios,
  fecharModal 
}) => {
  // Estado para funcionários na jornada atual
  const [funcionariosNaJornada, setFuncionariosNaJornada] = useState(() => 
    funcionariosPorJornada[jornadaSelecionada.id] || []
  );

  // Estado para todos os funcionários não atribuídos
  const [funcionariosDisponiveis, setFuncionariosDisponiveis] = useState([]);

  // Atualizar funcionários disponíveis sempre que a lista de funcionários mudar
  useEffect(() => {
    // Filtrar funcionários que não estão em nenhuma jornada ou estão nesta jornada
    const disponiveis = todosFuncionarios.filter(funcionario => 
      !Object.values(funcionariosPorJornada).some(jornada => 
        jornada.includes(funcionario.id)
      ) || funcionariosNaJornada.includes(funcionario.id)
    );

    setFuncionariosDisponiveis(disponiveis);
  }, [todosFuncionarios, funcionariosPorJornada, funcionariosNaJornada]);

  // Adicionar funcionário à jornada
  const adicionarFuncionario = (funcionarioId) => {
    const novosFuncionariosNaJornada = [...funcionariosNaJornada];
    if (!novosFuncionariosNaJornada.includes(funcionarioId)) {
      novosFuncionariosNaJornada.push(funcionarioId);
    }

    // Atualizar funcionários por jornada
    const novoFuncionariosPorJornada = FuncionariosService.adicionarFuncionarioNaJornada(
      funcionarioId, 
      jornadaSelecionada.id, 
      funcionariosPorJornada
    );

    setFuncionariosNaJornada(novosFuncionariosNaJornada);
    setFuncionariosPorJornada(novoFuncionariosPorJornada);
  };

  // Remover funcionário da jornada
  const removerFuncionario = (funcionarioId) => {
    const novosFuncionariosNaJornada = funcionariosNaJornada.filter(
      id => id !== funcionarioId
    );

    // Atualizar funcionários por jornada
    const novoFuncionariosPorJornada = FuncionariosService.removerFuncionarioDaJornada(
      funcionarioId, 
      jornadaSelecionada.id, 
      funcionariosPorJornada
    );

    setFuncionariosNaJornada(novosFuncionariosNaJornada);
    setFuncionariosPorJornada(novoFuncionariosPorJornada);
  };

  // Salvar e fechar modal
  const salvarEFechar = () => {
    // Atualizar localStorage
    FuncionariosService.atualizarFuncionariosPorJornada(funcionariosPorJornada);
    fecharModal();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-purple-800 rounded-lg shadow-lg p-6 w-full max-w-2xl max-h-screen overflow-y-auto">
        <h3 className="text-xl font-bold mb-4">
          Gerenciar Funcionários - {jornadaSelecionada.nome}
        </h3>

        <div className="grid grid-cols-2 gap-4">
          {/* Funcionários Disponíveis */}
          <div className="bg-purple-900 rounded-lg p-4">
            <h4 className="font-semibold mb-2 text-purple-300">
              Funcionários Disponíveis
            </h4>
            <div className="space-y-2">
              {funcionariosDisponiveis.map(funcionario => (
                <div 
                  key={funcionario.id} 
                  className="flex justify-between items-center bg-purple-700 p-2 rounded"
                >
                  <span>{funcionario.nome}</span>
                  <button 
                    onClick={() => adicionarFuncionario(funcionario.id)}
                    className="bg-green-600 hover:bg-green-700 text-white p-1 rounded"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                    </svg>
                  </button>
                </div>
              ))}
              {funcionariosDisponiveis.length === 0 && (
                <p className="text-purple-400 text-sm text-center">
                  Sem funcionários disponíveis
                </p>
              )}
            </div>
          </div>

          {/* Funcionários na Jornada */}
          <div className="bg-purple-900 rounded-lg p-4">
            <h4 className="font-semibold mb-2 text-purple-300">
              Funcionários na Jornada
            </h4>
            <div className="space-y-2">
              {funcionariosNaJornada.map(funcionarioId => {
                const funcionario = todosFuncionarios.find(f => f.id === funcionarioId);
                return funcionario ? (
                  <div 
                    key={funcionario.id} 
                    className="flex justify-between items-center bg-purple-700 p-2 rounded"
                  >
                    <span>{funcionario.nome}</span>
                    <button 
                      onClick={() => removerFuncionario(funcionario.id)}
                      className="bg-red-600 hover:bg-red-700 text-white p-1 rounded"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                      </svg>
                    </button>
                  </div>
                ) : null;
              })}
              {funcionariosNaJornada.length === 0 && (
                <p className="text-purple-400 text-sm text-center">
                  Nenhum funcionário na jornada
                </p>
              )}
            </div>
          </div>
        </div>

        <div className="flex justify-end space-x-2 mt-4">
          <button 
            onClick={fecharModal}
            className="bg-gray-600 hover:bg-gray-700 text-white font-medium py-2 px-4 rounded"
          >
            Cancelar
          </button>
          <button 
            onClick={salvarEFechar}
            className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded"
          >
            Salvar
          </button>
        </div>
      </div>
    </div>
  );
};

export default ModalGerenciarFuncionarios;
