import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginRegisterPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    username: '',
    password: '',
    confirmPassword: '',
    name: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  // Check if user is already authenticated
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      // Redirect based on user role
      redirectUser(user);
    }
  }, [navigate]);

  // Redirect user based on roles
  const redirectUser = (user) => {
    if (user.roles && user.roles.includes('ADMIN')) {
      navigate('/admin');
    } else {
      navigate('/');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');
    setLoading(true);

    try {
      if (!isLogin) {
        // Registration process
        if (formData.password !== formData.confirmPassword) {
          setErrorMessage('As senhas não coincidem!');
          setLoading(false);
          return;
        }

        // Actual API call for registration
        const response = await axios.post('/api/auth/registro', {
          username: formData.username,
          email: formData.email,
          nome: formData.name,
          password: formData.password
        });
        
        // Show success message
        setSuccessMessage('Cadastro realizado com sucesso! Faça login para continuar.');
        
        // Switch to login screen after a few seconds
        setTimeout(() => {
          setIsLogin(true);
          setSuccessMessage('');
          // Clear registration fields, keep username and password to ease login
          setFormData({
            username: formData.username,
            password: '',
            email: '',
            confirmPassword: '',
            name: ''
          });
        }, 3000);
        
        setLoading(false);
      } else {
        // Login process
        const response = await axios.post('/api/auth/login', {
          username: formData.username,
          password: formData.password
        });
        
        // Extract data from response
        const { token, id, username, nome, roles } = response.data;
        
        // Store data in localStorage
        localStorage.setItem('token', token);
        const userData = {
          id,
          username,
          nome,
          roles
        };
        localStorage.setItem('user', JSON.stringify(userData));
        
        // Redirect based on roles
        redirectUser(userData);
      }
    } catch (error) {
      console.error('Erro:', error);
      setErrorMessage(
        error.response?.data?.message || 
        (isLogin ? 'Usuário ou senha inválidos' : 'Erro no registro. Tente novamente.')
      );
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-purple-900 to-black">
      <div className="flex justify-center mt-10 mb-8">
        <div className="text-white text-4xl font-bold flex items-center">
          <span className="bg-purple-600 rounded-full p-2 mr-2">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
          </span>
          CuidaEmprego
        </div>
      </div>

      <div className="w-full max-w-md mx-auto px-4">
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6 border border-purple-500">
          <div className="flex mb-6">
            <button 
              className={`flex-1 py-2 text-white font-medium rounded-tl-lg rounded-bl-lg ${isLogin ? 'bg-purple-600' : 'bg-purple-900 bg-opacity-50'}`}
              onClick={() => { setIsLogin(true); setErrorMessage(''); setSuccessMessage(''); }}
            >
              Login
            </button>
            <button 
              className={`flex-1 py-2 text-white font-medium rounded-tr-lg rounded-br-lg ${!isLogin ? 'bg-purple-600' : 'bg-purple-900 bg-opacity-50'}`}
              onClick={() => { setIsLogin(false); setErrorMessage(''); setSuccessMessage(''); }}
            >
              Registro
            </button>
          </div>

          {/* Error Message */}
          {errorMessage && (
            <div className="mb-4 p-2 bg-red-500 bg-opacity-30 border border-red-500 rounded text-white text-sm font-semibold text-center">
              {errorMessage}
            </div>
          )}

          {/* Success Message */}
          {successMessage && (
            <div className="mb-4 p-2 bg-green-500 bg-opacity-30 border border-green-500 rounded text-white text-sm font-semibold text-center">
              {successMessage}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            {/* Name Field for Registration */}
            {!isLogin && (
              <div className="mb-4">
                <label className="block text-white text-sm font-medium mb-2" htmlFor="name">
                  Nome Completo
                </label>
                <input
                  className="w-full bg-purple-900 bg-opacity-50 text-white border border-purple-500 rounded py-2 px-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  id="name"
                  name="name"
                  type="text"
                  value={formData.name}
                  onChange={handleChange}
                  required={!isLogin}
                />
              </div>
            )}

            {/* Username Field */}
            <div className="mb-4">
              <label className="block text-white text-sm font-medium mb-2" htmlFor="username">
                Nome de Usuário
              </label>
              <input
                className="w-full bg-purple-900 bg-opacity-50 text-white border border-purple-500 rounded py-2 px-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                id="username"
                name="username"
                type="text"
                value={formData.username}
                onChange={handleChange}
                required
              />
            </div>

            {/* Email Field for Registration */}
            {!isLogin && (
              <div className="mb-4">
                <label className="block text-white text-sm font-medium mb-2" htmlFor="email">
                  Email
                </label>
                <input
                  className="w-full bg-purple-900 bg-opacity-50 text-white border border-purple-500 rounded py-2 px-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  id="email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  required={!isLogin}
                />
              </div>
            )}

            {/* Password Field */}
            <div className="mb-4">
              <label className="block text-white text-sm font-medium mb-2" htmlFor="password">
                Senha
              </label>
              <input
                className="w-full bg-purple-900 bg-opacity-50 text-white border border-purple-500 rounded py-2 px-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                id="password"
                name="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>

            {/* Confirm Password Field for Registration */}
            {!isLogin && (
              <div className="mb-6">
                <label className="block text-white text-sm font-medium mb-2" htmlFor="confirmPassword">
                  Confirmar Senha
                </label>
                <input
                  className="w-full bg-purple-900 bg-opacity-50 text-white border border-purple-500 rounded py-2 px-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required={!isLogin}
                />
              </div>
            )}

            {/* Remember Me and Forgot Password for Login */}
            {isLogin && (
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center">
                  <input
                    id="remember"
                    name="remember"
                    type="checkbox"
                    className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300 rounded"
                  />
                  <label htmlFor="remember" className="ml-2 block text-sm text-white">
                    Lembrar-me
                  </label>
                </div>
                
                <div className="text-sm">
                  <a href="#" className="text-purple-300 hover:text-purple-200">
                    Esqueceu a senha?
                  </a>
                </div>
              </div>
            )}

            {/* Submit Button */}
            <button
              type="submit"
              disabled={loading}
              className={`w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-400 transition duration-200 ease-in-out transform hover:scale-105 shadow-lg ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Processando...
                </span>
              ) : (
                isLogin ? 'Entrar' : 'Registrar'
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginRegisterPage;
