import React, { useState, useEffect } from 'react';

const HorasExtrasTab = () => {
  // Estados para gerenciar as diferentes visualizações
  const [activeView, setActiveView] = useState('horasExtras');
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [showApprovalModal, setShowApprovalModal] = useState(false);
  const [showFeriasModal, setShowFeriasModal] = useState(false);
  const [showFolgasModal, setShowFolgasModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  
  // Estado para armazenar funcionários
  const [employees, setEmployees] = useState(() => {
    const storedEmployees = localStorage.getItem('employees');
    return storedEmployees ? JSON.parse(storedEmployees) : [
      { id: 1, name: 'João Silva', email: 'joao.silva@empresa.com', department: 'TI' },
      { id: 2, name: 'Maria Oliveira', email: 'maria.oliveira@empresa.com', department: 'RH' },
      { id: 3, name: 'Carlos Pereira', email: 'carlos.pereira@empresa.com', department: 'Financeiro' },
      { id: 4, name: 'Ana Santos', email: 'ana.santos@empresa.com', department: 'Marketing' },
      { id: 5, name: 'Bruno Almeida', email: 'bruno.almeida@empresa.com', department: 'Operações' }
    ];
  });
  
  // Estado para armazenar solicitações de horas extras
  const [horasExtrasRequests, setHorasExtrasRequests] = useState(() => {
    const storedRequests = localStorage.getItem('horasExtrasRequests');
    return storedRequests ? JSON.parse(storedRequests) : [
      { id: 1, employeeId: 3, employeeName: 'Carlos Pereira', date: '17/03/2025', startTime: '18:00', endTime: '20:00', reason: 'Fechamento de relatório mensal', status: 'pendente', notified: false },
      { id: 2, employeeId: 1, employeeName: 'João Silva', date: '15/03/2025', startTime: '08:00', endTime: '12:00', reason: 'Manutenção de servidor - final de semana', status: 'aprovado', notified: true },
      { id: 3, employeeId: 2, employeeName: 'Maria Oliveira', date: '18/03/2025', startTime: '18:00', endTime: '21:00', reason: 'Recrutamento urgente', status: 'pendente', notified: false }
    ];
  });
  
  // Estado para armazenar solicitações de férias
  const [feriasRequests, setFeriasRequests] = useState(() => {
    const storedRequests = localStorage.getItem('feriasRequests');
    return storedRequests ? JSON.parse(storedRequests) : [
      { id: 1, employeeId: 2, employeeName: 'Maria Oliveira', startDate: '01/05/2025', endDate: '15/05/2025', status: 'pendente', notified: false },
      { id: 2, employeeId: 4, employeeName: 'Ana Santos', startDate: '10/04/2025', endDate: '25/04/2025', status: 'aprovado', notified: true },
      { id: 3, employeeId: 5, employeeName: 'Bruno Almeida', startDate: '15/06/2025', endDate: '30/06/2025', status: 'pendente', notified: false }
    ];
  });
  
  // Estado para armazenar solicitações de folgas
  const [folgasRequests, setFolgasRequests] = useState(() => {
    const storedRequests = localStorage.getItem('folgasRequests');
    return storedRequests ? JSON.parse(storedRequests) : [
      { id: 1, employeeId: 1, employeeName: 'João Silva', date: '25/03/2025', reason: 'Consulta médica', status: 'pendente', notified: false },
      { id: 2, employeeId: 3, employeeName: 'Carlos Pereira', date: '31/03/2025', reason: 'Aniversário', status: 'aprovado', notified: true },
      { id: 3, employeeId: 5, employeeName: 'Bruno Almeida', date: '27/03/2025', reason: 'Assuntos pessoais', status: 'pendente', notified: false }
    ];
  });
  
  // Formulário para nova solicitação
  const [newRequest, setNewRequest] = useState({
    employeeId: '',
    date: '',
    startTime: '',
    endTime: '',
    reason: '',
    startDate: '',
    endDate: ''
  });
  
  // Salvar dados no localStorage quando mudarem
  useEffect(() => {
    localStorage.setItem('horasExtrasRequests', JSON.stringify(horasExtrasRequests));
    
    // Atualizar notificações dos funcionários quando aprovações forem feitas
    const pendingNotifications = horasExtrasRequests
      .filter(req => req.status !== 'pendente' && !req.notified)
      .map(req => {
        const status = req.status === 'aprovado' ? 'aprovada' : 'rejeitada';
        return {
          id: Date.now() + Math.random(),
          userId: req.employeeId,
          message: `Sua solicitação de hora extra para ${req.date} foi ${status}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
      });
    
    if (pendingNotifications.length > 0) {
      const currentNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      localStorage.setItem('userNotifications', JSON.stringify([
        ...pendingNotifications,
        ...currentNotifications
      ]));
      
      // Marcar como notificado
      setHorasExtrasRequests(horasExtrasRequests.map(req => 
        !req.notified && req.status !== 'pendente' ? { ...req, notified: true } : req
      ));
    }
  }, [horasExtrasRequests]);
  
  useEffect(() => {
    localStorage.setItem('feriasRequests', JSON.stringify(feriasRequests));
    
    // Atualizar notificações para férias
    const pendingNotifications = feriasRequests
      .filter(req => req.status !== 'pendente' && !req.notified)
      .map(req => {
        const status = req.status === 'aprovado' ? 'aprovada' : 'rejeitada';
        return {
          id: Date.now() + Math.random(),
          userId: req.employeeId,
          message: `Sua solicitação de férias de ${req.startDate} a ${req.endDate} foi ${status}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
      });
    
    if (pendingNotifications.length > 0) {
      const currentNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      localStorage.setItem('userNotifications', JSON.stringify([
        ...pendingNotifications,
        ...currentNotifications
      ]));
      
      // Marcar como notificado
      setFeriasRequests(feriasRequests.map(req => 
        !req.notified && req.status !== 'pendente' ? { ...req, notified: true } : req
      ));
    }
  }, [feriasRequests]);
  
  useEffect(() => {
    localStorage.setItem('folgasRequests', JSON.stringify(folgasRequests));
    
    // Atualizar notificações para folgas
    const pendingNotifications = folgasRequests
      .filter(req => req.status !== 'pendente' && !req.notified)
      .map(req => {
        const status = req.status === 'aprovado' ? 'aprovada' : 'rejeitada';
        return {
          id: Date.now() + Math.random(),
          userId: req.employeeId,
          message: `Sua solicitação de folga para ${req.date} foi ${status}`,
          date: new Date().toLocaleDateString('pt-BR'),
          read: false
        };
      });
    
    if (pendingNotifications.length > 0) {
      const currentNotifications = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      localStorage.setItem('userNotifications', JSON.stringify([
        ...pendingNotifications,
        ...currentNotifications
      ]));
      
      // Marcar como notificado
      setFolgasRequests(folgasRequests.map(req => 
        !req.notified && req.status !== 'pendente' ? { ...req, notified: true } : req
      ));
    }
  }, [folgasRequests]);
  
  // Função para abrir modal de aprovação
  const openApprovalModal = (request, type) => {
    setSelectedRequest({ ...request, type });
    setShowApprovalModal(true);
  };
  
  // Função para aprovar ou rejeitar solicitação
  const handleRequestStatus = (status) => {
    if (selectedRequest.type === 'horasExtras') {
      setHorasExtrasRequests(horasExtrasRequests.map(req => 
        req.id === selectedRequest.id ? { ...req, status, notified: false } : req
      ));
    } else if (selectedRequest.type === 'ferias') {
      setFeriasRequests(feriasRequests.map(req => 
        req.id === selectedRequest.id ? { ...req, status, notified: false } : req
      ));
    } else if (selectedRequest.type === 'folgas') {
      setFolgasRequests(folgasRequests.map(req => 
        req.id === selectedRequest.id ? { ...req, status, notified: false } : req
      ));
    }
    
    setShowApprovalModal(false);
  };
  
  // Função para abrir modal de nova solicitação de férias
  const openNewFeriasModal = (employee) => {
    setSelectedEmployee(employee);
    setNewRequest({
      employeeId: employee.id,
      startDate: '',
      endDate: '',
      reason: ''
    });
    setShowFeriasModal(true);
  };
  
  // Função para abrir modal de nova solicitação de folga
  const openNewFolgaModal = (employee) => {
    setSelectedEmployee(employee);
    setNewRequest({
      employeeId: employee.id,
      date: '',
      reason: ''
    });
    setShowFolgasModal(true);
  };
  
  // Função para criar nova solicitação de férias
  const createFeriasRequest = () => {
    const newFerias = {
      id: feriasRequests.length + 1,
      employeeId: newRequest.employeeId,
      employeeName: employees.find(emp => emp.id === parseInt(newRequest.employeeId)).name,
      startDate: newRequest.startDate,
      endDate: newRequest.endDate,
      status: 'pendente',
      notified: false
    };
    
    setFeriasRequests([newFerias, ...feriasRequests]);
    setShowFeriasModal(false);
  };
  
  // Função para criar nova solicitação de folga
  const createFolgaRequest = () => {
    const newFolga = {
      id: folgasRequests.length + 1,
      employeeId: newRequest.employeeId,
      employeeName: employees.find(emp => emp.id === parseInt(newRequest.employeeId)).name,
      date: newRequest.date,
      reason: newRequest.reason,
      status: 'pendente',
      notified: false
    };
    
    setFolgasRequests([newFolga, ...folgasRequests]);
    setShowFolgasModal(false);
  };
  
  // Manipuladores para os inputs do formulário
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewRequest(prev => ({ ...prev, [name]: value }));
  };
  
  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold">Gerenciamento de Horas Extras, Férias e Folgas</h1>
      </div>
      
      {/* Tabs para navegação entre visualizações */}
      <div className="bg-purple-800 bg-opacity-40 rounded-lg p-1 flex space-x-1">
        <button
          onClick={() => setActiveView('horasExtras')}
          className={`px-4 py-2 rounded-md ${activeView === 'horasExtras' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Horas Extras
        </button>
        <button
          onClick={() => setActiveView('ferias')}
          className={`px-4 py-2 rounded-md ${activeView === 'ferias' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Férias
        </button>
        <button
          onClick={() => setActiveView('folgas')}
          className={`px-4 py-2 rounded-md ${activeView === 'folgas' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Folgas
        </button>
        <button
          onClick={() => setActiveView('funcionarios')}
          className={`px-4 py-2 rounded-md ${activeView === 'funcionarios' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
        >
          Funcionários
        </button>
      </div>
      
      {/* Conteúdo baseado na tab ativa */}
      {activeView === 'horasExtras' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
          <div className="p-4 border-b border-purple-700 flex justify-between items-center">
            <h2 className="text-xl font-semibold">Solicitações de Horas Extras</h2>
          </div>
          
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm">
                  <th className="text-left p-4">Funcionário</th>
                  <th className="text-left p-4">Data</th>
                  <th className="text-left p-4">Horário</th>
                  <th className="text-left p-4">Motivo</th>
                  <th className="text-left p-4">Status</th>
                  <th className="text-left p-4">Ações</th>
                </tr>
              </thead>
              <tbody>
                {horasExtrasRequests.length === 0 ? (
                  <tr>
                    <td colSpan={6} className="p-4 text-center">Nenhuma solicitação de hora extra encontrada.</td>
                  </tr>
                ) : (
                  horasExtrasRequests.map(request => (
                    <tr key={request.id} className="border-t border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                      <td className="p-4">{request.employeeName}</td>
                      <td className="p-4">{request.date}</td>
                      <td className="p-4">{request.startTime} - {request.endTime}</td>
                      <td className="p-4">{request.reason}</td>
                      <td className="p-4">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs 
                          ${request.status === 'aprovado' ? 'bg-green-600' : 
                            request.status === 'pendente' ? 'bg-yellow-600' : 'bg-red-600'}`}
                        >
                          {request.status.toUpperCase()}
                        </span>
                      </td>
                      <td className="p-4">
                        {request.status === 'pendente' && (
                          <div className="flex space-x-2">
                            <button
                              onClick={() => openApprovalModal(request, 'horasExtras')}
                              className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded-md text-sm"
                            >
                              Revisar
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
      
      {activeView === 'ferias' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
          <div className="p-4 border-b border-purple-700 flex justify-between items-center">
            <h2 className="text-xl font-semibold">Solicitações de Férias</h2>
          </div>
          
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm">
                  <th className="text-left p-4">Funcionário</th>
                  <th className="text-left p-4">Período</th>
                  <th className="text-left p-4">Dias</th>
                  <th className="text-left p-4">Status</th>
                  <th className="text-left p-4">Ações</th>
                </tr>
              </thead>
              <tbody>
                {feriasRequests.length === 0 ? (
                  <tr>
                    <td colSpan={5} className="p-4 text-center">Nenhuma solicitação de férias encontrada.</td>
                  </tr>
                ) : (
                  feriasRequests.map(request => {
                    // Calcular número de dias
                    const start = new Date(request.startDate.split('/').reverse().join('-'));
                    const end = new Date(request.endDate.split('/').reverse().join('-'));
                    const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
                    
                    return (
                      <tr key={request.id} className="border-t border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                        <td className="p-4">{request.employeeName}</td>
                        <td className="p-4">{request.startDate} a {request.endDate}</td>
                        <td className="p-4">{days} dias</td>
                        <td className="p-4">
                          <span className={`inline-block px-2 py-1 rounded-full text-xs 
                            ${request.status === 'aprovado' ? 'bg-green-600' : 
                              request.status === 'pendente' ? 'bg-yellow-600' : 'bg-red-600'}`}
                          >
                            {request.status.toUpperCase()}
                          </span>
                        </td>
                        <td className="p-4">
                          {request.status === 'pendente' && (
                            <div className="flex space-x-2">
                              <button
                                onClick={() => openApprovalModal(request, 'ferias')}
                                className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded-md text-sm"
                              >
                                Revisar
                              </button>
                            </div>
                          )}
                        </td>
                      </tr>
                    );
                  })
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
      
      {activeView === 'folgas' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
          <div className="p-4 border-b border-purple-700 flex justify-between items-center">
            <h2 className="text-xl font-semibold">Solicitações de Folgas</h2>
          </div>
          
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm">
                  <th className="text-left p-4">Funcionário</th>
                  <th className="text-left p-4">Data</th>
                  <th className="text-left p-4">Motivo</th>
                  <th className="text-left p-4">Status</th>
                  <th className="text-left p-4">Ações</th>
                </tr>
              </thead>
              <tbody>
                {folgasRequests.length === 0 ? (
                  <tr>
                    <td colSpan={5} className="p-4 text-center">Nenhuma solicitação de folga encontrada.</td>
                  </tr>
                ) : (
                  folgasRequests.map(request => (
                    <tr key={request.id} className="border-t border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                      <td className="p-4">{request.employeeName}</td>
                      <td className="p-4">{request.date}</td>
                      <td className="p-4">{request.reason}</td>
                      <td className="p-4">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs 
                          ${request.status === 'aprovado' ? 'bg-green-600' : 
                            request.status === 'pendente' ? 'bg-yellow-600' : 'bg-red-600'}`}
                        >
                          {request.status.toUpperCase()}
                        </span>
                      </td>
                      <td className="p-4">
                        {request.status === 'pendente' && (
                          <div className="flex space-x-2">
                            <button
                              onClick={() => openApprovalModal(request, 'folgas')}
                              className="bg-purple-600 hover:bg-purple-500 px-3 py-1 rounded-md text-sm"
                            >
                              Revisar
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
      
      {activeView === 'funcionarios' && (
        <div className="bg-purple-800 bg-opacity-20 rounded-lg shadow-lg">
          <div className="p-4 border-b border-purple-700">
            <h2 className="text-xl font-semibold">Lista de Funcionários</h2>
            <p className="text-purple-300 text-sm">Gerencie as solicitações por funcionário</p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
            {employees.map(employee => (
              <div key={employee.id} className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h3 className="font-bold text-lg">{employee.name}</h3>
                <p className="text-purple-300">{employee.department}</p>
                <p className="text-sm mb-4">{employee.email}</p>
                
                <div className="flex flex-wrap gap-2 mt-3">
                  <button
                    onClick={() => openNewFeriasModal(employee)}
                    className="bg-purple-600 hover:bg-purple-500 px-2 py-1 rounded text-sm"
                  >
                    Nova Férias
                  </button>
                  <button
                    onClick={() => openNewFolgaModal(employee)}
                    className="bg-purple-600 hover:bg-purple-500 px-2 py-1 rounded text-sm"
                  >
                    Nova Folga
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
      
      {/* Modal de aprovação/rejeição */}
      {showApprovalModal && selectedRequest && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">
              {selectedRequest.type === 'horasExtras' ? 'Revisar Horas Extras' : 
                selectedRequest.type === 'ferias' ? 'Revisar Férias' : 'Revisar Folga'}
            </h3>
            
            <div className="mb-4">
              <p><span className="text-purple-300">Funcionário:</span> {selectedRequest.employeeName}</p>
              
              {selectedRequest.type === 'horasExtras' && (
                <>
                  <p><span className="text-purple-300">Data:</span> {selectedRequest.date}</p>
                  <p><span className="text-purple-300">Horário:</span> {selectedRequest.startTime} - {selectedRequest.endTime}</p>
                  <p><span className="text-purple-300">Motivo:</span> {selectedRequest.reason}</p>
                </>
              )}
              
              {selectedRequest.type === 'ferias' && (
                <>
                  <p><span className="text-purple-300">Período:</span> {selectedRequest.startDate} a {selectedRequest.endDate}</p>
                </>
              )}
              
              {selectedRequest.type === 'folgas' && (
                <>
                  <p><span className="text-purple-300">Data:</span> {selectedRequest.date}</p>
                  <p><span className="text-purple-300">Motivo:</span> {selectedRequest.reason}</p>
                </>
              )}
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowApprovalModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={() => handleRequestStatus('rejeitado')}
                className="px-4 py-2 bg-red-600 hover:bg-red-500 rounded-md"
              >
                Rejeitar
              </button>
              <button 
                onClick={() => handleRequestStatus('aprovado')}
                className="px-4 py-2 bg-green-600 hover:bg-green-500 rounded-md"
              >
                Aprovar
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal para nova solicitação de férias */}
      {showFeriasModal && selectedEmployee && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">Nova Solicitação de Férias</h3>
            
            <div className="mb-4">
              <p><span className="text-purple-300">Funcionário:</span> {selectedEmployee.name}</p>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Data de Início</label>
              <input 
                type="date" 
                name="startDate"
                value={newRequest.startDate}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="mb-6">
              <label className="block text-sm font-medium mb-2">Data de Término</label>
              <input 
                type="date" 
                name="endDate"
                value={newRequest.endDate}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowFeriasModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={createFeriasRequest}
                disabled={!newRequest.startDate || !newRequest.endDate}
                className={`px-4 py-2 rounded-md ${(newRequest.startDate && newRequest.endDate) ? 'bg-purple-600 hover:bg-purple-500' : 'bg-purple-900 opacity-50 cursor-not-allowed'}`}
              >
                Criar Solicitação
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal para nova solicitação de folga */}
      {showFolgasModal && selectedEmployee && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">Nova Solicitação de Folga</h3>
            
            <div className="mb-4">
              <p><span className="text-purple-300">Funcionário:</span> {selectedEmployee.name}</p>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Data da Folga</label>
              <input 
                type="date" 
                name="date"
                value={newRequest.date}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div className="mb-6">
              <label className="block text-sm font-medium mb-2">Motivo</label>
              <textarea 
                name="reason"
                value={newRequest.reason}
                onChange={handleInputChange}
                className="w-full px-3 py-2 bg-purple-800 border border-purple-600 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500 min-h-20"
                placeholder="Descreva o motivo da folga..."
              />
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowFolgasModal(false)}
                className="px-4 py-2 bg-purple-800 hover:bg-purple-700 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={createFolgaRequest}
                disabled={!newRequest.date || !newRequest.reason}
                className={`px-4 py-2 rounded-md ${(newRequest.date && newRequest.reason) ? 'bg-purple-600 hover:bg-purple-500' : 'bg-purple-900 opacity-50 cursor-not-allowed'}`}
              >
                Criar Solicitação
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HorasExtrasTab;
