import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import AjustesPontoTab from '../components/AjustesPontoTab';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, logoutUser } = useUser(); // Use o contexto aqui
  const [activeTab, setActiveTab] = useState('ajustes-ponto');
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  
  // Verificar autorização no carregamento
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (!user.authenticated || !user.roles || !user.roles.includes('ADMIN')) {
      navigate('/login');
    }
  }, [navigate]);
  
  // Deslogar o usuário
  const handleLogout = () => {
    logoutUser(); // Use a função do contexto para logout
    navigate('/login');
  };
  
  // Dados do usuário
  const userData = {
    name: currentUser?.name || 'Admin',
    email: currentUser?.email || 'admin@example.com',
    initials: getInitials(currentUser?.name || 'Admin')
  };
  
  // Função para obter iniciais do nome
  function getInitials(name) {
    if (!name) return 'A';
    const nameParts = name.split(' ');
    if (nameParts.length === 1) return nameParts[0].charAt(0).toUpperCase();
    return (nameParts[0].charAt(0) + nameParts[nameParts.length - 1].charAt(0)).toUpperCase();
  }
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      {/* Header */}
      <header className="bg-purple-900 bg-opacity-80 shadow-lg sticky top-0 z-10">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center">
            <div className="bg-purple-600 rounded-full p-1 mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
              </svg>
            </div>
            <span className="text-xl font-bold">CuidaEmprego | Admin</span>
          </div>
          
          <div className="flex items-center space-x-4">
            {/* Perfil do usuário */}
            <div className="relative">
              <button
                onClick={() => setShowProfileMenu(!showProfileMenu)}
                className="flex items-center space-x-2 focus:outline-none"
              >
                <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center">
                  <span className="font-medium text-sm">{userData.initials}</span>
                </div>
                <span className="hidden md:inline-block">{userData.name}</span>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>
              
              {showProfileMenu && (
                <div className="absolute right-0 mt-2 w-48 bg-purple-800 rounded-md shadow-lg py-1 z-20">
                  <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Configurações</a>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left px-4 py-2 text-sm hover:bg-purple-700"
                  >
                    Sair
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-6">
        <h1 className="text-3xl font-bold mb-6">Painel Administrativo</h1>
        
        {/* Tabs para diferentes seções */}
        <div className="mb-6">
          <div className="flex overflow-x-auto space-x-2 bg-purple-800 bg-opacity-30 p-1 rounded-lg">
            <button 
              onClick={() => setActiveTab('ajustes-ponto')}
              className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'ajustes-ponto' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
            >
              Ajustes de Ponto
            </button>
            <button 
              onClick={() => setActiveTab('atestados')}
              className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'atestados' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
            >
              Atestados
            </button>
            <button 
              onClick={() => setActiveTab('relatorios')}
              className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'relatorios' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
            >
              Relatórios
            </button>
            <button 
              onClick={() => setActiveTab('funcionarios')}
              className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'funcionarios' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
            >
              Gerenciar Funcionários
            </button>
          </div>
        </div>
        
        {/* Conteúdo baseado na tab ativa */}
        {activeTab === 'ajustes-ponto' && <AjustesPontoTab />}
        
        {activeTab === 'atestados' && (
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold mb-4">Atestados Médicos</h2>
            <p>Funcionalidade de atestados em desenvolvimento.</p>
          </div>
        )}
        
        {activeTab === 'relatorios' && (
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold mb-4">Relatórios</h2>
            <p>Funcionalidade de relatórios em desenvolvimento.</p>
          </div>
        )}
        
        {activeTab === 'funcionarios' && (
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-bold mb-4">Gerenciar Funcionários</h2>
            <p>Funcionalidade de gerenciamento de funcionários em desenvolvimento.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminDashboard;
