import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { format, parseISO } from 'date-fns';
import { Modal, Form, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
  const navigate = useNavigate();
  const [isAdmin, setIsAdmin] = useState(false);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [lastAction, setLastAction] = useState(null);
  const [userData, setUserData] = useState({ name: 'Carregando...' });
  const [timeEntries, setTimeEntries] = useState([]);
  const [error, setError] = useState(null);
  const [showRevisaoModal, setShowRevisaoModal] = useState(false);
  const [novaRevisao, setNovaRevisao] = useState({
    tipo: '',
    descricao: ''
  });
  const [estatisticas, setEstatisticas] = useState({
    horasTrabalhadasSemana: "0h 0min",
    horasExtrasMes: "0h 0min",
    proximaFolga: "Nenhuma agendada"
  });
  const [showGestaoTempoModal, setShowGestaoTempoModal] = useState(false);
  const [gestaoTempoData, setGestaoTempoData] = useState({
    horasExtras: [],
    ferias: [],
    folgas: [],
    contadores: {}
  });
  const [activeGestaoTab, setActiveGestaoTab] = useState('horasExtras');

  // Carregar os dados do usuário do localStorage e fazer chamada à API
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const headers = { Authorization: `Bearer ${token}` };
    
    // Verificar se o usuário é admin
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setIsAdmin(parsedUser.isAdmin || false);
        setUserData({
          name: parsedUser.nome || 'Usuário'
        });
      } catch (error) {
        console.error('Erro ao analisar dados do usuário:', error);
        setUserData({ name: 'Usuário' });
      }
    }

    // Carregar dados do usuário e registros
    axios.get('/api/funcionario/gestao-tempo', { headers })
      .then(response => {
        const data = response.data;
        setUserData(prevData => ({
          ...prevData,
          name: data.nome,
          saldoBancoHoras: data.saldoBancoHoras,
          proximaFolga: data.proximaFolga ? data.proximaFolga.dataInicio : null,
          proximasFerias: data.proximasFerias ? data.proximasFerias.dataInicio : null
        }));
        
        // Guardar dados de gestão de tempo para exibir no modal
        setGestaoTempoData({
          horasExtras: data.horasExtras || [],
          ferias: data.ferias || [],
          folgas: data.folgas || [],
          contadores: data.contadores || {}
        });
        
        // Formatar banco de horas
        const horas = Math.floor(data.saldoBancoHoras / 60);
        const minutos = data.saldoBancoHoras % 60;
        
        // Atualizar estatísticas
        setEstatisticas({
          horasTrabalhadasSemana: "32h 15min", // Calcular com base em registros reais
          horasExtrasMes: `${horas}h ${minutos}min`,
          proximaFolga: data.proximaFolga ? 
            format(parseISO(data.proximaFolga.dataInicio), 'dd/MM/yyyy') : 
            'Nenhuma agendada'
        });
        
        // Pegar registros de ponto mais recentes
        axios.get('/api/funcionario/ponto', { headers })
          .then(pontosResponse => {
            const pontos = pontosResponse.data;
            setTimeEntries(pontos.slice(0, 10).map(rp => ({
              type: rp.tipoRegistro === 'ENTRADA_EXPEDIENTE' || rp.tipoRegistro === 'RETORNO_ALMOCO' ? 
                'entrada' : 'saída',
              time: format(parseISO(rp.dataHora), 'HH:mm'),
              date: format(parseISO(rp.dataHora), 'dd/MM/yyyy'),
              validated: rp.validado
            })));
          })
          .catch(error => console.error('Erro ao carregar registros de ponto:', error));
      })
      .catch(error => {
        console.error('Erro ao carregar dados:', error);
        setError('Falha ao carregar dados do usuário');
      });
  }, [navigate]);

  // Pega as iniciais do nome para o avatar
  const getInitials = (name) => {
    if (!name) return 'U';
    const nameParts = name.split(' ');
    if (nameParts.length === 1) return nameParts[0].charAt(0).toUpperCase();
    return (nameParts[0].charAt(0) + nameParts[nameParts.length - 1].charAt(0)).toUpperCase();
  };

  // Atualiza o relógio a cada segundo
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Formata a hora atual
  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  // Registra entrada ou saída
  const registerTimeEntry = (type) => {
    const tipoRegistro = type === 'entrada' 
      ? 'ENTRADA_EXPEDIENTE' 
      : 'SAIDA_EXPEDIENTE';
    
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Você precisa estar logado para registrar ponto');
      return;
    }
    
    axios.post('/api/funcionario/ponto', {
      tipoRegistro: tipoRegistro
    }, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        const now = new Date();
        const newEntry = {
          type,
          time: now.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' }),
          date: now.toLocaleDateString('pt-BR'),
          validated: false
        };
        setTimeEntries([newEntry, ...timeEntries]);
        setLastAction(`${type.charAt(0).toUpperCase() + type.slice(1)} registrada às ${newEntry.time}`);
      })
      .catch(error => {
        console.error('Erro ao registrar ponto:', error);
        setError('Falha ao registrar ponto: ' + (error.response?.data || 'Erro desconhecido'));
      });
  };

  // Solicitar revisão
  const handleSolicitarRevisao = (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Você precisa estar logado para solicitar revisão');
      return;
    }
    
    axios.post('/api/funcionario/gestao-tempo/solicitar-revisao', novaRevisao, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        setShowRevisaoModal(false);
        setNovaRevisao({ tipo: '', descricao: '' });
        alert('Solicitação enviada com sucesso!');
      })
      .catch(error => {
        console.error('Erro ao solicitar revisão:', error);
        setError('Falha ao enviar solicitação: ' + (error.response?.data || 'Erro desconhecido'));
      });
  };

  // Função para deslogar o usuário
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      {/* Header */}
      <header className="bg-purple-900 bg-opacity-80 shadow-lg sticky top-0 z-10">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center">
            <div className="bg-purple-600 rounded-full p-1 mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
              </svg>
            </div>
            <span className="text-xl font-bold">CuidaEmprego</span>
          </div>
          <div className="relative">
            <button
              onClick={() => setShowProfileMenu(!showProfileMenu)}
              className="flex items-center space-x-2 focus:outline-none"
            >
              <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center">
                <span className="font-medium text-sm">{getInitials(userData.name)}</span>
              </div>
              <span className="hidden md:inline-block">{userData.name}</span>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            {showProfileMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-purple-800 rounded-md shadow-lg py-1 z-20">
                <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Meu Perfil</a>
                <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Configurações</a>
                <button
                  onClick={handleLogout}
                  className="block w-full text-left px-4 py-2 text-sm hover:bg-purple-700"
                >
                  Sair
                </button>
              </div>
            )}
          </div>
        </div>
      </header>
      {/* Main Content */}
      <main className="container mx-auto px-4 py-6">
        {error && (
          <div className="bg-red-500 bg-opacity-70 text-white p-3 rounded-lg mb-4">
            {error}
            <button className="float-right" onClick={() => setError(null)}>×</button>
          </div>
        )}
        
        {/* Relógio */}
        <div className="text-center mb-8">
          <p className="text-sm text-purple-300">{currentTime.toLocaleDateString('pt-BR', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          <p className="text-4xl font-bold text-white">{formatTime(currentTime)}</p>
          {lastAction && (
            <div className="mt-2 text-sm bg-purple-600 inline-block px-3 py-1 rounded-full">
              {lastAction}
            </div>
          )}
        </div>
        {/* Botões de Ação */}
        <div className="flex flex-wrap justify-center gap-4 mb-8">
          <button
            onClick={() => registerTimeEntry('entrada')}
            className="w-full sm:w-64 bg-gradient-to-r from-purple-600 to-purple-800 hover:from-purple-700 hover:to-purple-900 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
            </svg>
            Registrar Entrada
          </button>
          <button
            onClick={() => registerTimeEntry('saída')}
            className="w-full sm:w-64 bg-gradient-to-r from-purple-600 to-purple-800 hover:from-purple-700 hover:to-purple-900 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
            </svg>
            Registrar Saída
          </button>
          <button
            onClick={() => setShowGestaoTempoModal(true)}
            className="w-full sm:w-64 bg-gradient-to-r from-purple-500 to-purple-700 hover:from-purple-600 hover:to-purple-800 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
            </svg>
            Ver Horas Extras, Férias e Folgas
          </button>
          {isAdmin && (
            <button
              onClick={() => navigate('/admin')}
              className="w-full sm:w-64 bg-gradient-to-r from-indigo-500 to-indigo-700 hover:from-indigo-600 hover:to-indigo-800 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              Painel de Administração
            </button>
          )}
        </div>
        {/* Registros Recentes */}
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4 mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold flex items-center">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              Registros Recentes
            </h2>
            <button 
              onClick={() => setShowRevisaoModal(true)}
              className="bg-purple-600 hover:bg-purple-700 text-white text-sm px-3 py-1 rounded-full"
            >
              Solicitar Revisão
            </button>
          </div>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm">
                  <th className="text-left p-2">Data</th>
                  <th className="text-left p-2">Hora</th>
                  <th className="text-left p-2">Tipo</th>
                  <th className="text-left p-2">Status</th>
                </tr>
              </thead>
              <tbody>
                {timeEntries.length === 0 ? (
                  <tr>
                    <td colSpan="4" className="text-center p-4">Nenhum registro encontrado</td>
                  </tr>
                ) : (
                  timeEntries.map((entry, index) => (
                    <tr key={index} className="border-t border-purple-700">
                      <td className="p-2">{entry.date}</td>
                      <td className="p-2">{entry.time}</td>
                      <td className="p-2">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs ${entry.type === 'entrada' ? 'bg-green-600' : 'bg-red-600'}`}>
                          {entry.type.toUpperCase()}
                        </span>
                      </td>
                      <td className="p-2">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs ${entry.validated ? 'bg-green-600' : 'bg-yellow-600'}`}>
                          {entry.validated ? 'VALIDADO' : 'PENDENTE'}
                        </span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
        {/* Estatísticas */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <h3 className="text-sm text-purple-300 mb-1">Horas Trabalhadas (Esta Semana)</h3>
            <p className="text-2xl font-bold">{estatisticas.horasTrabalhadasSemana}</p>
          </div>
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <h3 className="text-sm text-purple-300 mb-1">Banco de Horas</h3>
            <p className="text-2xl font-bold">{estatisticas.horasExtrasMes}</p>
          </div>
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <h3 className="text-sm text-purple-300 mb-1">Próxima Folga</h3>
            <p className="text-2xl font-bold">{estatisticas.proximaFolga}</p>
          </div>
        </div>
      </main>
      {/* Modal para visualizar Horas Extras, Férias e Folgas */}
      <Modal 
        show={showGestaoTempoModal} 
        onHide={() => setShowGestaoTempoModal(false)}
        size="lg"
        centered
      >
        <Modal.Header closeButton className="bg-purple-900 text-white">
          <Modal.Title>Horas Extras, Férias e Folgas</Modal.Title>
        </Modal.Header>
        <Modal.Body className="bg-purple-800 text-white">
          <div className="mb-4">
            <div className="flex border-b border-purple-700 mb-4">
              <button 
                className={`px-4 py-2 mr-2 focus:outline-none ${activeGestaoTab === 'horasExtras' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
                onClick={() => setActiveGestaoTab('horasExtras')}
              >
                Horas Extras
              </button>
              <button 
                className={`px-4 py-2 mr-2 focus:outline-none ${activeGestaoTab === 'ferias' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
                onClick={() => setActiveGestaoTab('ferias')}
              >
                Férias
              </button>
              <button 
                className={`px-4 py-2 focus:outline-none ${activeGestaoTab === 'folgas' ? 'border-b-2 border-purple-400 font-semibold' : 'text-purple-300'}`}
                onClick={() => setActiveGestaoTab('folgas')}
              >
                Folgas
              </button>
            </div>
            
            {/* Conteúdo de Horas Extras */}
            {activeGestaoTab === 'horasExtras' && (
              <div>
                <div className="flex justify-between items-center mb-3">
                  <h5 className="font-semibold">Minhas Horas Extras</h5>
                  <div className="flex space-x-2 text-sm">
                    <span className="bg-green-600 px-2 py-1 rounded">
                      Aprovadas: {gestaoTempoData.contadores.horasExtrasAprovadas || 0}
                    </span>
                    <span className="bg-yellow-600 px-2 py-1 rounded">
                      Pendentes: {gestaoTempoData.contadores.horasExtrasPendentes || 0}
                    </span>
                  </div>
                </div>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="text-purple-300 text-sm">
                        <th className="text-left p-2">Data</th>
                        <th className="text-left p-2">Horário</th>
                        <th className="text-left p-2">Duração</th>
                        <th className="text-left p-2">Status</th>
                        <th className="text-left p-2">Observação</th>
                      </tr>
                    </thead>
                    <tbody>
                      {gestaoTempoData.horasExtras.length === 0 ? (
                        <tr>
                          <td colSpan="5" className="text-center p-4">Nenhuma hora extra registrada</td>
                        </tr>
                      ) : (
                        gestaoTempoData.horasExtras.map((hora, index) => (
                          <tr key={index} className="border-t border-purple-700">
                            <td className="p-2">{format(parseISO(hora.dataHoraInicio), 'dd/MM/yyyy')}</td>
                            <td className="p-2">
                              {format(parseISO(hora.dataHoraInicio), 'HH:mm')} - 
                              {format(parseISO(hora.dataHoraFim), 'HH:mm')}
                            </td>
                            <td className="p-2">{hora.duracaoFormatada}</td>
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                                hora.status === 'APROVADA' ? 'bg-green-600' : 
                                hora.status === 'REJEITADA' ? 'bg-red-600' : 
                                hora.status === 'PAGA' ? 'bg-blue-600' : 
                                hora.status === 'COMPENSADA' ? 'bg-indigo-600' : 'bg-yellow-600'
                              }`}>
                                {hora.status}
                              </span>
                            </td>
                            <td className="p-2">{hora.observacaoAprovador || '-'}</td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
            
            {/* Conteúdo de Férias */}
            {activeGestaoTab === 'ferias' && (
              <div>
                <div className="flex justify-between items-center mb-3">
                  <h5 className="font-semibold">Minhas Férias</h5>
                  <div className="flex space-x-2 text-sm">
                    <span className="bg-green-600 px-2 py-1 rounded">
                      Aprovadas: {gestaoTempoData.contadores.feriasAprovadas || 0}
                    </span>
                    <span className="bg-yellow-600 px-2 py-1 rounded">
                      Pendentes: {gestaoTempoData.contadores.feriasPendentes || 0}
                    </span>
                  </div>
                </div>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="text-purple-300 text-sm">
                        <th className="text-left p-2">Período</th>
                        <th className="text-left p-2">Dias Úteis</th>
                        <th className="text-left p-2">Status</th>
                        <th className="text-left p-2">Observação</th>
                      </tr>
                    </thead>
                    <tbody>
                      {gestaoTempoData.ferias.length === 0 ? (
                        <tr>
                          <td colSpan="4" className="text-center p-4">Nenhuma férias registrada</td>
                        </tr>
                      ) : (
                        gestaoTempoData.ferias.map((ferias, index) => (
                          <tr key={index} className="border-t border-purple-700">
                            <td className="p-2">
                              {format(parseISO(ferias.dataInicio), 'dd/MM/yyyy')} a {format(parseISO(ferias.dataFim), 'dd/MM/yyyy')}
                            </td>
                            <td className="p-2">{ferias.diasUteis}</td>
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                                ferias.status === 'APROVADA' ? 'bg-green-600' : 
                                ferias.status === 'REJEITADA' ? 'bg-red-600' : 
                                ferias.status === 'CONCLUIDA' ? 'bg-blue-600' : 
                                ferias.status === 'CANCELADA' ? 'bg-gray-600' : 'bg-yellow-600'
                              }`}>
                                {ferias.status}
                              </span>
                            </td>
                            <td className="p-2">{ferias.observacao || '-'}</td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
            
            {/* Conteúdo de Folgas */}
            {activeGestaoTab === 'folgas' && (
              <div>
                <div className="flex justify-between items-center mb-3">
                  <h5 className="font-semibold">Minhas Folgas</h5>
                  <div className="flex space-x-2 text-sm">
                    <span className="bg-green-600 px-2 py-1 rounded">
                      Aprovadas: {gestaoTempoData.contadores.folgasAprovadas || 0}
                    </span>
                    <span className="bg-yellow-600 px-2 py-1 rounded">
                      Pendentes: {gestaoTempoData.contadores.folgasPendentes || 0}
                    </span>
                  </div>
                </div>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead>
                      <tr className="text-purple-300 text-sm">
                        <th className="text-left p-2">Data</th>
                        <th className="text-left p-2">Tipo</th>
                        <th className="text-left p-2">Motivo</th>
                        <th className="text-left p-2">Status</th>
                        <th className="text-left p-2">Observação</th>
                      </tr>
                    </thead>
                    <tbody>
                      {gestaoTempoData.folgas.length === 0 ? (
                        <tr>
                          <td colSpan="5" className="text-center p-4">Nenhuma folga registrada</td>
                        </tr>
                      ) : (
                        gestaoTempoData.folgas.map((folga, index) => (
                          <tr key={index} className="border-t border-purple-700">
                            <td className="p-2">
                              {format(parseISO(folga.dataInicio), 'dd/MM/yyyy')}
                              {folga.dataInicio !== folga.dataFim && ` a ${format(parseISO(folga.dataFim), 'dd/MM/yyyy')}`}
                            </td>
                            <td className="p-2">
                              {folga.tipoFolga === 'COMPENSATORIA' ? 'Compensatória' : 
                               folga.tipoFolga === 'ABONO' ? 'Abono' : 'Banco de Horas'}
                            </td>
                            <td className="p-2">{folga.motivo || '-'}</td>
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs ${
                                folga.status === 'APROVADA' ? 'bg-green-600' : 
                                folga.status === 'REJEITADA' ? 'bg-red-600' : 
                                folga.status === 'CANCELADA' ? 'bg-gray-600' : 'bg-yellow-600'
                              }`}>
                                {folga.status}
                              </span>
                            </td>
                            <td className="p-2">{folga.observacao || '-'}</td>
                          </tr>
                        ))
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            )}
          </div>
          
          <div className="flex justify-end mt-4">
            <button
              className="bg-purple-600 hover:bg-purple-700 text-white font-medium py-2 px-4 rounded"
              onClick={() => setShowRevisaoModal(true)}
            >
              Solicitar Revisão
            </button>
            <button
              className="bg-gray-600 hover:bg-gray-700 text-white font-medium py-2 px-4 rounded ml-2"
              onClick={() => setShowGestaoTempoModal(false)}
            >
              Fechar
            </button>
          </div>
        </Modal.Body>
      </Modal>
      
      {/* Modal para solicitar revisão */}
      <Modal 
        show={showRevisaoModal} 
        onHide={() => setShowRevisaoModal(false)}
        centered
      >
        <Modal.Header closeButton className="bg-purple-900 text-white">
          <Modal.Title>Solicitar Revisão</Modal.Title>
        </Modal.Header>
        <Modal.Body className="bg-purple-800 text-white">
          <Form onSubmit={handleSolicitarRevisao}>
            <Form.Group className="mb-3">
              <Form.Label>Tipo de Solicitação</Form.Label>
              <Form.Select 
                name="tipo"
                value={novaRevisao.tipo}
                onChange={(e) => setNovaRevisao({...novaRevisao, tipo: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
              >
                <option value="">Selecione um tipo</option>
                <option value="REVISAO_BANCO_HORAS">Revisão de Banco de Horas</option>
                <option value="AJUSTE_PONTO">Ajuste de Ponto</option>
                <option value="OUTROS">Outro Assunto</option>
              </Form.Select>
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label>Descrição</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="descricao"
                value={novaRevisao.descricao}
                onChange={(e) => setNovaRevisao({...novaRevisao, descricao: e.target.value})}
                required
                className="bg-purple-700 text-white border-purple-600"
                placeholder="Descreva detalhadamente sua solicitação"
              />
            </Form.Group>
            
            <div className="flex justify-end mt-4">
              <Button 
                variant="secondary" 
                className="mr-2 bg-gray-600 hover:bg-gray-700"
                onClick={() => setShowRevisaoModal(false)}
              >
                Cancelar
              </Button>
              <Button 
                variant="primary" 
                type="submit"
                className="bg-purple-600 hover:bg-purple-700"
              >
                Enviar Solicitação
              </Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
      
      {/* Decorações */}
      <div className="fixed top-20 left-10 w-20 h-20 bg-purple-500 rounded-full opacity-5 blur-xl"></div>
      <div className="fixed bottom-20 right-10 w-32 h-32 bg-purple-400 rounded-full opacity-5 blur-xl"></div>
      <div className="fixed top-40 right-20 w-16 h-16 bg-purple-300 rounded-full opacity-5 blur-xl"></div>
    </div>
  );
};

export default DashboardPage;
