import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginRegisterPage from "./pages/LoginRegisterPage";
import UserDashboard from "./pages/UserDashboard";
import AdminDashboard from "./pages/AdminDashboard";
import { UserProvider } from "./context/UserProvider"; // Adicionei importação do UserContext
import WorkshiftProvider from "./context/WorkshiftContext";

// Componente para proteger rotas com base no papel do usuário
const ProtectedRoute = ({ children, requiredRole }) => {
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  // Verificar se o usuário está autenticado
  if (!user?.authenticated) {
    return <Navigate to="/" replace />;
  }

  // Se for uma rota que requer ADMIN, verificar se o usuário é admin
  if (requiredRole === "ADMIN" && !user.roles?.includes("ADMIN")) {
    return <Navigate to="/dashboard" replace />;
  }

  // Se for uma rota que requer FUNCIONARIO, permitir também ADMIN (pois admin pode acessar tudo)
  if (requiredRole === "FUNCIONARIO" && 
      !user.roles?.includes("FUNCIONARIO") && 
      !user.roles?.includes("ADMIN")) {
    return <Navigate to="/" replace />;
  }

  return children;
};

const App = () => {
  return (
    <UserProvider> {/* Adicionei o UserProvider para gerenciar o contexto de usuários */}
      <WorkshiftProvider>
        <Router>
          <Routes>
            {/* Página inicial (login e registro) */}
            <Route path="/" element={<LoginRegisterPage />} />

            {/* Rota protegida do Dashboard para funcionários */}
            <Route
              path="/dashboard"
              element={
                <ProtectedRoute requiredRole="FUNCIONARIO">
                  <UserDashboard />
                </ProtectedRoute>
              }
            />

            {/* Rota protegida da Administração apenas para admins */}
            <Route
              path="/admin"
              element={
                <ProtectedRoute requiredRole="ADMIN">
                  <AdminDashboard />
                </ProtectedRoute>
              }
            />

            {/* Redirecionamento para a página inicial caso a rota não exista */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </Router>
      </WorkshiftProvider>
    </UserProvider>
  );
};

export default App;
