import React, { useState, useEffect } from 'react';

const ApprovedDataComponent = () => {
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('horasExtras');
  
  // Estados para armazenar dados aprovados pelo administrador
  const [horasExtrasApproved, setHorasExtrasApproved] = useState([]);
  const [feriasApproved, setFeriasApproved] = useState([]);
  const [folgasApproved, setFolgasApproved] = useState([]);
  const [bancoHoras, setBancoHoras] = useState(null);
  const [ausenciasApproved, setAusenciasApproved] = useState([]);
  const [jornada, setJornada] = useState(null);
  
  // Obter dados do usuário logado
  const [userData, setUserData] = useState(() => {
    const storedUser = JSON.parse(localStorage.getItem('user') || '{}');
    return {
      id: storedUser.id || 1, // Valor padrão para teste
      name: storedUser.name || 'Usuário',
      email: storedUser.email || ''
    };
  });
  
  // Carregar dados aprovados ao iniciar
  useEffect(() => {
    // Horas Extras aprovadas
    const storedHorasExtras = localStorage.getItem('horasExtrasRequests');
    if (storedHorasExtras) {
      const horasExtras = JSON.parse(storedHorasExtras);
      const approved = horasExtras.filter(item => 
        item.employeeId === userData.id && item.status === 'aprovado'
      );
      setHorasExtrasApproved(approved);
    }
    
    // Férias aprovadas
    const storedFerias = localStorage.getItem('feriasRequests');
    if (storedFerias) {
      const ferias = JSON.parse(storedFerias);
      const approved = ferias.filter(item => 
        item.employeeId === userData.id && item.status === 'aprovado'
      );
      setFeriasApproved(approved);
    }
    
    // Folgas aprovadas
    const storedFolgas = localStorage.getItem('folgasRequests');
    if (storedFolgas) {
      const folgas = JSON.parse(storedFolgas);
      const approved = folgas.filter(item => 
        item.employeeId === userData.id && item.status === 'aprovado'
      );
      setFolgasApproved(approved);
    }
    
    // Banco de Horas
    const storedBancoHoras = localStorage.getItem('bancoHoras');
    if (storedBancoHoras) {
      const bancoHorasData = JSON.parse(storedBancoHoras);
      const userBancoHoras = bancoHorasData.find(item => item.employeeId === userData.id);
      if (userBancoHoras) {
        setBancoHoras(userBancoHoras);
      }
    }
    
    // Ausências aprovadas
    const storedAusencias = localStorage.getItem('ausencias');
    if (storedAusencias) {
      const ausencias = JSON.parse(storedAusencias);
      const approved = ausencias.filter(item => 
        item.employeeId === userData.id && item.status === 'aprovado'
      );
      setAusenciasApproved(approved);
    }
    
    // Jornada de trabalho
    const storedJornadas = localStorage.getItem('jornadas');
    if (storedJornadas) {
      const jornadas = JSON.parse(storedJornadas);
      const userJornada = jornadas.find(item => item.employeeId === userData.id);
      if (userJornada) {
        setJornada(userJornada);
      }
    }
  }, [userData.id]);
  
  // Função para formatar período
  const formatPeriod = (start, end) => {
    return start === end ? start : `${start} - ${end}`;
  };
  
  return (
    <>
      <button
        onClick={() => setShowModal(true)}
        className="w-full sm:w-64 bg-gradient-to-r from-purple-500 to-purple-700 hover:from-purple-600 hover:to-purple-800 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
        </svg>
        Horas Extras, Férias e Folgas
      </button>
      
      {/* Modal com os dados aprovados */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">Informações Aprovadas</h3>
              <button 
                onClick={() => setShowModal(false)}
                className="text-purple-300 hover:text-white"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            
            {/* Tabs de navegação */}
            <div className="mb-6">
              <div className="flex overflow-x-auto space-x-2 bg-purple-800 bg-opacity-30 p-1 rounded-lg">
                <button 
                  onClick={() => setActiveTab('horasExtras')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'horasExtras' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Horas Extras
                </button>
                <button 
                  onClick={() => setActiveTab('ferias')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'ferias' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Férias
                </button>
                <button 
                  onClick={() => setActiveTab('folgas')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'folgas' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Folgas
                </button>
                <button 
                  onClick={() => setActiveTab('bancoHoras')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'bancoHoras' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Banco de Horas
                </button>
                <button 
                  onClick={() => setActiveTab('ausencias')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'ausencias' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Ausências
                </button>
                <button 
                  onClick={() => setActiveTab('jornada')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'jornada' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Jornada de Trabalho
                </button>
              </div>
            </div>
            
            {/* Conteúdo baseado na tab ativa */}
            {activeTab === 'horasExtras' && (
              <div className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h4 className="text-lg font-medium mb-4">Horas Extras Aprovadas</h4>
                
                {horasExtrasApproved.length === 0 ? (
                  <p className="text-purple-300">Nenhuma hora extra aprovada encontrada.</p>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="text-purple-300 text-sm">
                          <th className="text-left p-2">Data</th>
                          <th className="text-left p-2">Horário</th>
                          <th className="text-left p-2">Motivo</th>
                        </tr>
                      </thead>
                      <tbody>
                        {horasExtrasApproved.map(item => (
                          <tr key={item.id} className="border-t border-purple-700">
                            <td className="p-2">{item.date}</td>
                            <td className="p-2">{item.startTime} - {item.endTime}</td>
                            <td className="p-2">{item.reason}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}
            
            {activeTab === 'ferias' && (
              <div className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h4 className="text-lg font-medium mb-4">Férias Aprovadas</h4>
                
                {feriasApproved.length === 0 ? (
                  <p className="text-purple-300">Nenhuma férias aprovada encontrada.</p>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="text-purple-300 text-sm">
                          <th className="text-left p-2">Período</th>
                          <th className="text-left p-2">Dias</th>
                        </tr>
                      </thead>
                      <tbody>
                        {feriasApproved.map(item => {
                          // Calcular número de dias
                          const start = new Date(item.startDate.split('/').reverse().join('-'));
                          const end = new Date(item.endDate.split('/').reverse().join('-'));
                          const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;
                          
                          return (
                            <tr key={item.id} className="border-t border-purple-700">
                              <td className="p-2">{item.startDate} a {item.endDate}</td>
                              <td className="p-2">{days} dias</td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}
            
            {activeTab === 'folgas' && (
              <div className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h4 className="text-lg font-medium mb-4">Folgas Aprovadas</h4>
                
                {folgasApproved.length === 0 ? (
                  <p className="text-purple-300">Nenhuma folga aprovada encontrada.</p>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="text-purple-300 text-sm">
                          <th className="text-left p-2">Data</th>
                          <th className="text-left p-2">Motivo</th>
                        </tr>
                      </thead>
                      <tbody>
                        {folgasApproved.map(item => (
                          <tr key={item.id} className="border-t border-purple-700">
                            <td className="p-2">{item.date}</td>
                            <td className="p-2">{item.reason}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}
            
                            {activeTab === 'bancoHoras' && (
              <div className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h4 className="text-lg font-medium mb-4">Banco de Horas</h4>
                
                {!bancoHoras ? (
                  <p className="text-purple-300">Nenhum registro de banco de horas encontrado.</p>
                ) : (
                  <div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                      <div className="bg-purple-900 bg-opacity-40 p-4 rounded-lg">
                        <h5 className="text-sm text-purple-300 mb-1">Saldo Positivo</h5>
                        <p className="text-2xl font-bold text-green-400">{bancoHoras.saldoPositivo}</p>
                      </div>
                      <div className="bg-purple-900 bg-opacity-40 p-4 rounded-lg">
                        <h5 className="text-sm text-purple-300 mb-1">Saldo Negativo</h5>
                        <p className="text-2xl font-bold text-red-400">{bancoHoras.saldoNegativo}</p>
                      </div>
                      <div className="bg-purple-900 bg-opacity-40 p-4 rounded-lg">
                        <h5 className="text-sm text-purple-300 mb-1">Saldo Total</h5>
                        <p className="text-2xl font-bold">{bancoHoras.totalHoras}</p>
                      </div>
                    </div>
                    <p className="text-purple-300 text-sm">Última atualização: {bancoHoras.updatedAt}</p>
                    
                    {/* Aqui poderíamos adicionar um histórico de movimentações se tivéssemos acesso */}
                  </div>
                )}
              </div>
            )}
            
            {activeTab === 'ausencias' && (
              <div className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h4 className="text-lg font-medium mb-4">Ausências Aprovadas</h4>
                
                {ausenciasApproved.length === 0 ? (
                  <p className="text-purple-300">Nenhuma ausência aprovada encontrada.</p>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="text-purple-300 text-sm">
                          <th className="text-left p-2">Tipo</th>
                          <th className="text-left p-2">Período</th>
                          <th className="text-left p-2">Motivo</th>
                        </tr>
                      </thead>
                      <tbody>
                        {ausenciasApproved.map(item => (
                          <tr key={item.id} className="border-t border-purple-700">
                            <td className="p-2">
                              <span className={`inline-block px-2 py-1 rounded-full text-xs 
                                ${item.tipo === 'atestado' ? 'bg-blue-600' : 
                                  item.tipo === 'falta' ? 'bg-yellow-600' : 'bg-green-600'}`}
                              >
                                {item.tipo.toUpperCase()}
                              </span>
                            </td>
                            <td className="p-2">{formatPeriod(item.dataInicio, item.dataFim)}</td>
                            <td className="p-2">{item.motivo}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}
            
            {activeTab === 'jornada' && (
              <div className="bg-purple-800 bg-opacity-40 rounded-lg p-4">
                <h4 className="text-lg font-medium mb-4">Jornada de Trabalho</h4>
                
                {!jornada ? (
                  <p className="text-purple-300">Nenhuma jornada de trabalho definida.</p>
                ) : (
                  <div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                      <div>
                        <h5 className="text-md font-medium mb-2">Informações Gerais</h5>
                        <ul className="space-y-2 bg-purple-900 bg-opacity-40 p-4 rounded-lg">
                          <li>
                            <span className="text-purple-300">Tipo de Jornada:</span>{' '}
                            <span className="font-medium">{jornada.tipo.toUpperCase()}</span>
                          </li>
                          <li>
                            <span className="text-purple-300">Horas Diárias:</span>{' '}
                            <span className="font-medium">{jornada.horasDiarias}h</span>
                          </li>
                          <li>
                            <span className="text-purple-300">Tolerância:</span>{' '}
                            <span className="font-medium">{jornada.tolerancia} minutos</span>
                          </li>
                        </ul>
                      </div>
                      
                      <div>
                        <h5 className="text-md font-medium mb-2">Horários</h5>
                        <ul className="space-y-2 bg-purple-900 bg-opacity-40 p-4 rounded-lg">
                          <li>
                            <span className="text-purple-300">Entrada:</span>{' '}
                            <span className="font-medium">{jornada.horarios.entrada}</span>
                          </li>
                          {jornada.horarios.saidaAlmoco && jornada.horarios.retornoAlmoco && (
                            <>
                              <li>
                                <span className="text-purple-300">Saída para Almoço:</span>{' '}
                                <span className="font-medium">{jornada.horarios.saidaAlmoco}</span>
                              </li>
                              <li>
                                <span className="text-purple-300">Retorno do Almoço:</span>{' '}
                                <span className="font-medium">{jornada.horarios.retornoAlmoco}</span>
                              </li>
                            </>
                          )}
                          <li>
                            <span className="text-purple-300">Saída:</span>{' '}
                            <span className="font-medium">{jornada.horarios.saida}</span>
                          </li>
                        </ul>
                      </div>
                    </div>
                    
                    <div>
                      <h5 className="text-md font-medium mb-2">Dias de Trabalho</h5>
                      <div className="flex flex-wrap gap-2 bg-purple-900 bg-opacity-40 p-4 rounded-lg">
                        {jornada.diasSemana.map(dia => (
                          <span key={dia} className="bg-purple-700 px-3 py-1 rounded-lg text-sm">
                            {dia.charAt(0).toUpperCase() + dia.slice(1)}
                          </span>
                        ))}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </>
  );
};

export default ApprovedDataComponent;
