import { useState, useEffect } from "react";

const JornadasTab = () => {
  // Estado para armazenar as jornadas
  const [jornadas, setJornadas] = useState([]);

  // Carrega jornadas do localStorage ou define valores padrão
  useEffect(() => {
    const storedJornadas = localStorage.getItem("jornadas");
    if (storedJornadas) {
      setJornadas(JSON.parse(storedJornadas));
    } else {
      const jornadasPadrao = [
        {
          id: 1,
          nome: "Padrão",
          descricao: "Jornada padrão de 8 horas diárias",
          horaInicio: "08:00",
          horaFim: "17:00",
          intervaloInicio: "12:00",
          intervaloFim: "13:00",
          diasTrabalho: [1, 2, 3, 4, 5], // Segunda a sexta
          cargaHorariaSemanal: 40,
          toleranciaEntrada: 10,
          toleranciaSaida: 10,
          ativo: true,
        },
        {
          id: 2,
          nome: "Meio período - Manhã",
          descricao: "Jornada de meio período matutino",
          horaInicio: "08:00",
          horaFim: "12:00",
          intervaloInicio: null,
          intervaloFim: null,
          diasTrabalho: [1, 2, 3, 4, 5],
          cargaHorariaSemanal: 20,
          toleranciaEntrada: 10,
          toleranciaSaida: 10,
          ativo: true,
        },
        {
          id: 3,
          nome: "Meio período - Tarde",
          descricao: "Jornada de meio período vespertino",
          horaInicio: "13:00",
          horaFim: "17:00",
          intervaloInicio: null,
          intervaloFim: null,
          diasTrabalho: [1, 2, 3, 4, 5],
          cargaHorariaSemanal: 20,
          toleranciaEntrada: 10,
          toleranciaSaida: 10,
          ativo: true,
        },
        {
          id: 4,
          nome: "Escala 6x1",
          descricao: "Trabalho de segunda a sábado",
          horaInicio: "08:00",
          horaFim: "16:20",
          intervaloInicio: "12:00",
          intervaloFim: "13:00",
          diasTrabalho: [1, 2, 3, 4, 5, 6],
          cargaHorariaSemanal: 44,
          toleranciaEntrada: 10,
          toleranciaSaida: 10,
          ativo: true,
        },
        {
          id: 5,
          nome: "Escala 12x36",
          descricao: "Trabalho 12 horas e folga 36 horas",
          horaInicio: "07:00",
          horaFim: "19:00",
          intervaloInicio: "12:00",
          intervaloFim: "13:00",
          diasTrabalho: [],
          cargaHorariaSemanal: 36,
          toleranciaEntrada: 15,
          toleranciaSaida: 15,
          ativo: true,
          escalaEspecial: true,
        },
      ];
      setJornadas(jornadasPadrao);
      localStorage.setItem("jornadas", JSON.stringify(jornadasPadrao));
    }
  }, []);

  // Estado para nova jornada
  const [novaJornada, setNovaJornada] = useState({
    nome: "",
    descricao: "",
    horaInicio: "",
    horaFim: "",
    intervaloInicio: "",
    intervaloFim: "",
    diasTrabalho: [],
    cargaHorariaSemanal: 0,
    toleranciaEntrada: 10,
    toleranciaSaida: 10,
    ativo: true,
    escalaEspecial: false,
  });

  // Adicionar nova jornada
  const adicionarJornada = () => {
    if (!novaJornada.nome.trim() || !novaJornada.horaInicio || !novaJornada.horaFim) {
      alert("Preencha todos os campos obrigatórios!");
      return;
    }

    const novaLista = [...jornadas, { ...novaJornada, id: jornadas.length + 1 }];
    setJornadas(novaLista);
    localStorage.setItem("jornadas", JSON.stringify(novaLista));

    setNovaJornada({
      nome: "",
      descricao: "",
      horaInicio: "",
      horaFim: "",
      intervaloInicio: "",
      intervaloFim: "",
      diasTrabalho: [],
      cargaHorariaSemanal: 0,
      toleranciaEntrada: 10,
      toleranciaSaida: 10,
      ativo: true,
      escalaEspecial: false,
    });
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Jornadas de Trabalho</h2>
      <ul>
        {jornadas.map((jornada) => (
          <li key={jornada.id} className="border p-4 mb-2 rounded-md bg-gray-100">
            <h3 className="text-lg font-semibold">{jornada.nome}</h3>
            <p>{jornada.descricao}</p>
            <p>
              <strong>Horário:</strong> {jornada.horaInicio} - {jornada.horaFim}
            </p>
            <p>
              <strong>Intervalo:</strong>{" "}
              {jornada.intervaloInicio ? `${jornada.intervaloInicio} - ${jornada.intervaloFim}` : "Sem intervalo"}
            </p>
            <p>
              <strong>Dias de Trabalho:</strong>{" "}
              {jornada.diasTrabalho.length > 0
                ? jornada.diasTrabalho.map((dia) => ["Dom", "Seg", "Ter", "Qua", "Qui", "Sex", "Sáb"][dia]).join(", ")
                : "Escala Especial"}
            </p>
          </li>
        ))}
      </ul>

      <h3 className="text-lg font-bold mt-4">Adicionar Nova Jornada</h3>
      <input
        type="text"
        placeholder="Nome da Jornada"
        value={novaJornada.nome}
        onChange={(e) => setNovaJornada({ ...novaJornada, nome: e.target.value })}
        className="border p-2 rounded-md w-full"
      />
      <input
        type="time"
        placeholder="Hora Início"
        value={novaJornada.horaInicio}
        onChange={(e) => setNovaJornada({ ...novaJornada, horaInicio: e.target.value })}
        className="border p-2 rounded-md w-full mt-2"
      />
      <input
        type="time"
        placeholder="Hora Fim"
        value={novaJornada.horaFim}
        onChange={(e) => setNovaJornada({ ...novaJornada, horaFim: e.target.value })}
        className="border p-2 rounded-md w-full mt-2"
      />

      <button
        onClick={adicionarJornada}
        className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4"
      >
        Adicionar Jornada
      </button>
    </div>
  );
};

export default JornadasTab;
