import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
  const navigate = useNavigate();
  
  // Obter dados do usuário logado do localStorage
  const [userData, setUserData] = useState(() => {
    const storedUser = localStorage.getItem('currentUser');
    return storedUser ? JSON.parse(storedUser) : {
      name: 'Usuário',
      initials: 'US',
      isAdmin: true
    };
  });
  
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [lastAction, setLastAction] = useState(null);
  
  // Obter registros de ponto do localStorage
  const [timeEntries, setTimeEntries] = useState(() => {
    const storedEntries = localStorage.getItem('timeEntries');
    return storedEntries ? JSON.parse(storedEntries) : [
      { type: 'entrada', time: '08:03', date: '12/03/2025' },
      { type: 'saída', time: '12:00', date: '12/03/2025' },
      { type: 'entrada', time: '13:05', date: '12/03/2025' },
      { type: 'saída', time: '17:30', date: '12/03/2025' },
      { type: 'entrada', time: '08:00', date: '13/03/2025' },
    ];
  });

  // Estatísticas
  const [stats, setStats] = useState({
    weeklyHours: '32h 15min',
    monthlyOvertime: '5h 45min',
    nextDayOff: '15/03/2025'
  });

  // Atualiza o relógio a cada segundo
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Atualiza localStorage quando os registros mudarem
  useEffect(() => {
    localStorage.setItem('timeEntries', JSON.stringify(timeEntries));
    
    // Atualizar estatísticas
    calculateStats();
  }, [timeEntries]);

  // Calcular estatísticas baseadas nas entradas
  const calculateStats = () => {
    // Esta é uma versão simplificada; a versão real seria mais complexa
    const today = new Date();
    const weekStart = new Date(today);
    weekStart.setDate(today.getDate() - today.getDay());
    
    // Filtrar entradas da semana atual
    const weeklyEntries = timeEntries.filter(entry => {
      const entryDate = parseDate(entry.date);
      return entryDate >= weekStart && entryDate <= today;
    });

    // Calcular horas trabalhadas (simplificado)
    let weeklyMinutes = 0;
    for (let i = 0; i < weeklyEntries.length; i += 2) {
      if (i + 1 < weeklyEntries.length && weeklyEntries[i].type === 'entrada' && weeklyEntries[i+1].type === 'saída') {
        const startTime = parseTime(weeklyEntries[i].time);
        const endTime = parseTime(weeklyEntries[i+1].time);
        weeklyMinutes += (endTime - startTime) / (60 * 1000);
      }
    }

    const weeklyHours = Math.floor(weeklyMinutes / 60);
    const remainingMinutes = Math.floor(weeklyMinutes % 60);
    
    // Calcular horas extras (simplificado)
    const standardWeeklyHours = 40; // 40 horas padrão por semana
    const overtimeMinutes = Math.max(0, weeklyMinutes - (standardWeeklyHours * 60));
    const overtimeHours = Math.floor(overtimeMinutes / 60);
    const overtimeRemaining = Math.floor(overtimeMinutes % 60);
    
    // Próxima folga (simplificado)
    const nextOffDay = new Date();
    nextOffDay.setDate(nextOffDay.getDate() + (6 - nextOffDay.getDay()));
    
    setStats({
      weeklyHours: `${weeklyHours}h ${remainingMinutes}min`,
      monthlyOvertime: `${overtimeHours}h ${overtimeRemaining}min`,
      nextDayOff: nextOffDay.toLocaleDateString('pt-BR')
    });
  };

  // Helper para parsear data
  const parseDate = (dateString) => {
    const [day, month, year] = dateString.split('/').map(Number);
    return new Date(year, month - 1, day);
  };
  
  // Helper para parsear tempo
  const parseTime = (timeString) => {
    const [hour, minute] = timeString.split(':').map(Number);
    const date = new Date();
    date.setHours(hour, minute, 0, 0);
    return date.getTime();
  };

  // Formata a hora atual
  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  // Verifica se pode registrar entrada/saída (regra de 8h)
  const canRegisterTimeEntry = (type) => {
    if (timeEntries.length === 0) return true;
    
    const lastEntry = timeEntries[0];
    
    // Se última entrada foi saída, pode registrar entrada
    if (type === 'entrada' && lastEntry.type === 'saída') {
      return true;
    }
    
    // Se última entrada foi entrada, pode registrar saída
    if (type === 'saída' && lastEntry.type === 'entrada') {
      return true;
    }
    
    // Se última entrada foi saída e quer registrar entrada, verifica 8h
    if (type === 'entrada' && lastEntry.type === 'saída') {
      const lastTime = parseTime(lastEntry.time);
      const now = currentTime.getTime();
      // Verifica se passou ao menos 8 horas (28800000 ms)
      return (now - lastTime) >= 28800000;
    }
    
    return false;
  };

  // Registra entrada ou saída
  const registerTimeEntry = (type) => {
    if (!canRegisterTimeEntry(type)) {
      setLastAction(`Não é possível registrar ${type} agora. Verifique o intervalo de 8h.`);
      return;
    }
    
    const now = new Date();
    const newEntry = {
      type,
      time: now.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' }),
      date: now.toLocaleDateString('pt-BR')
    };
    
    const updatedEntries = [newEntry, ...timeEntries];
    setTimeEntries(updatedEntries);
    setLastAction(`${type.charAt(0).toUpperCase() + type.slice(1)} registrada às ${newEntry.time}`);
  };

  // Função para fazer logout
  const handleLogout = () => {
    // Aqui você poderia limpar dados de autenticação
    localStorage.removeItem('currentUser');
    // Redirecionar para login (supondo que existe uma navegação configurada)
    window.location.href = '/';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      {/* Header */}
      <header className="bg-purple-900 bg-opacity-80 shadow-lg sticky top-0 z-10">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center">
            <div className="bg-purple-600 rounded-full p-1 mr-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
              </svg>
            </div>
            <span className="text-xl font-bold">CuidaEmprego</span>
          </div>
          
          <div className="relative">
            <button 
              onClick={() => setShowProfileMenu(!showProfileMenu)}
              className="flex items-center space-x-2 focus:outline-none"
            >
              <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center">
                <span className="font-medium text-sm">{userData.initials}</span>
              </div>
              <span className="hidden md:inline-block">{userData.name}</span>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            
            {showProfileMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-purple-800 rounded-md shadow-lg py-1 z-20">
                <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Meu Perfil</a>
                <a href="#" className="block px-4 py-2 text-sm hover:bg-purple-700">Configurações</a>
                <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-sm hover:bg-purple-700">Sair</button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-6">
        {/* Relógio */}
        <div className="text-center mb-8">
          <p className="text-sm text-purple-300">{currentTime.toLocaleDateString('pt-BR', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
          <p className="text-4xl font-bold text-white">{formatTime(currentTime)}</p>
          {lastAction && (
            <div className="mt-2 text-sm bg-purple-600 inline-block px-3 py-1 rounded-full">
              {lastAction}
            </div>
          )}
        </div>

        {/* Botões de Ação */}
        <div className="flex flex-wrap justify-center gap-4 mb-8">
          <button 
            onClick={() => registerTimeEntry('entrada')}
            className={`w-full sm:w-64 bg-gradient-to-r from-purple-600 to-purple-800 hover:from-purple-700 hover:to-purple-900 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center ${!canRegisterTimeEntry('entrada') ? 'opacity-50 cursor-not-allowed' : ''}`}
            disabled={!canRegisterTimeEntry('entrada')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
            </svg>
            Registrar Entrada
          </button>
          
          <button 
            onClick={() => registerTimeEntry('saída')}
            className={`w-full sm:w-64 bg-gradient-to-r from-purple-600 to-purple-800 hover:from-purple-700 hover:to-purple-900 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center ${!canRegisterTimeEntry('saída') ? 'opacity-50 cursor-not-allowed' : ''}`}
            disabled={!canRegisterTimeEntry('saída')}
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
            </svg>
            Registrar Saída
          </button>
          
          {userData.isAdmin && (
            <button 
              onClick={() => navigate('/admin')}
              className="w-full sm:w-64 bg-gradient-to-r from-purple-500 to-purple-700 hover:from-purple-600 hover:to-purple-800 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
              </svg>
              Horas Extras, Férias e Folgas
            </button>
          )}
        </div>

        {/* Registros Recentes */}
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4 mb-8">
          <h2 className="text-xl font-bold mb-4 flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            Registros Recentes
          </h2>
          
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="text-purple-300 text-sm">
                  <th className="text-left p-2">Data</th>
                  <th className="text-left p-2">Hora</th>
                  <th className="text-left p-2">Tipo</th>
                </tr>
              </thead>
              <tbody>
                {timeEntries.map((entry, index) => (
                  <tr key={index} className="border-t border-purple-700">
                    <td className="p-2">{entry.date}</td>
                    <td className="p-2">{entry.time}</td>
                    <td className="p-2">
                      <span className={`inline-block px-2 py-1 rounded-full text-xs ${entry.type === 'entrada' ? 'bg-green-600' : 'bg-red-600'}`}>
                        {entry.type.toUpperCase()}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Estatísticas */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <h3 className="text-sm text-purple-300 mb-1">Horas Trabalhadas (Esta Semana)</h3>
            <p className="text-2xl font-bold">{stats.weeklyHours}</p>
          </div>
          
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <h3 className="text-sm text-purple-300 mb-1">Horas Extras (Este Mês)</h3>
            <p className="text-2xl font-bold">{stats.monthlyOvertime}</p>
          </div>
          
          <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
            <h3 className="text-sm text-purple-300 mb-1">Próxima Folga</h3>
            <p className="text-2xl font-bold">{stats.nextDayOff}</p>
          </div>
        </div>
      </main>

      {/* Decorações */}
      <div className="fixed top-20 left-10 w-20 h-20 bg-purple-500 rounded-full opacity-5 blur-xl"></div>
      <div className="fixed bottom-20 right-10 w-32 h-32 bg-purple-400 rounded-full opacity-5 blur-xl"></div>
      <div className="fixed top-40 right-20 w-16 h-16 bg-purple-300 rounded-full opacity-5 blur-xl"></div>
    </div>
  );
};

export default DashboardPage;
