import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginRegisterPage = ({ setIsAuthenticated }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isLogin) {
      // Simulação de login bem-sucedido
      localStorage.setItem('user', JSON.stringify({ email: formData.email }));
      setIsAuthenticated(true);
      navigate('/dashboard');
    } else {
      // Simulação de registro bem-sucedido
      setIsLogin(true);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-purple-900 to-black">
      <div className="w-full max-w-md mx-auto px-4">
        <div className="bg-purple-800 bg-opacity-40 rounded-lg shadow-lg p-6">
          <div className="flex mb-6">
            <button 
              className={`flex-1 py-2 text-white font-medium ${isLogin ? 'bg-purple-600' : 'bg-purple-900'}`}
              onClick={() => setIsLogin(true)}
            >
              Login
            </button>
            <button 
              className={`flex-1 py-2 text-white font-medium ${!isLogin ? 'bg-purple-600' : 'bg-purple-900'}`}
              onClick={() => setIsLogin(false)}
            >
              Registro
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <input
                name="name"
                type="text"
                placeholder="Nome Completo"
                value={formData.name}
                onChange={handleChange}
                required
                className="w-full bg-purple-900 text-white border border-purple-500 rounded py-2 px-3 mb-4"
              />
            )}
            
            <input
              name="email"
              type="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full bg-purple-900 text-white border border-purple-500 rounded py-2 px-3 mb-4"
            />

            <input
              name="password"
              type="password"
              placeholder="Senha"
              value={formData.password}
              onChange={handleChange}
              required
              className="w-full bg-purple-900 text-white border border-purple-500 rounded py-2 px-3 mb-4"
            />

            {!isLogin && (
              <input
                name="confirmPassword"
                type="password"
                placeholder="Confirmar Senha"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
                className="w-full bg-purple-900 text-white border border-purple-500 rounded py-2 px-3 mb-4"
              />
            )}

            <button
              type="submit"
              className="w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded-lg"
            >
              {isLogin ? 'Entrar' : 'Registrar'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginRegisterPage;
