import React, { useState, useEffect } from 'react';

const AusenciasTab = () => {
  // Estado para ausências
  const [ausencias, setAusencias] = useState(() => {
    const storedAusencias = localStorage.getItem('ausencias');
    return storedAusencias ? JSON.parse(storedAusencias) : [
      { 
        id: 1, 
        funcionarioId: 101,
        funcionarioNome: 'João Silva',
        tipo: 'atestado',
        dataInicio: '15/03/2025',
        dataFim: '17/03/2025',
        motivo: 'Atestado médico',
        status: 'aprovado',
        observacao: 'Apresentou atestado',
        documentoAnexo: true,
        dataSolicitacao: '14/03/2025'
      },
      { 
        id: 2, 
        funcionarioId: 102,
        funcionarioNome: 'Maria Oliveira',
        tipo: 'falta',
        dataInicio: '10/03/2025',
        dataFim: '10/03/2025',
        motivo: 'Problemas pessoais',
        status: 'pendente',
        observacao: '',
        documentoAnexo: false,
        dataSolicitacao: '11/03/2025'
      },
      { 
        id: 3, 
        funcionarioId: 103,
        funcionarioNome: 'Carlos Pereira',
        tipo: 'atraso',
        dataInicio: '18/03/2025',
        dataFim: '18/03/2025',
        motivo: 'Trânsito intenso',
        status: 'pendente',
        observacao: '',
        documentoAnexo: false,
        dataSolicitacao: '18/03/2025'
      },
      { 
        id: 4, 
        funcionarioId: 104,
        funcionarioNome: 'Ana Souza',
        tipo: 'licenca',
        dataInicio: '01/03/2025',
        dataFim: '15/03/2025',
        motivo: 'Licença maternidade',
        status: 'aprovado',
        observacao: 'Licença legal',
        documentoAnexo: true,
        dataSolicitacao: '15/02/2025'
      }
    ];
  });
  
  // Estado para filtros
  const [filtros, setFiltros] = useState({
    funcionario: '',
    tipo: '',
    status: '',
    periodo: ''
  });
  
  // Estado para modal de visualização
  const [modalAberto, setModalAberto] = useState(false);
  const [ausenciaSelecionada, setAusenciaSelecionada] = useState(null);
  
  // Estado para modal de rejeição
  const [modalRejeicaoAberto, setModalRejeicaoAberto] = useState(false);
  const [motivoRejeicao, setMotivoRejeicao] = useState('');
  
  // Lista de funcionários
  const [funcionarios] = useState(() => {
    const storedFuncionarios = localStorage.getItem('funcionarios');
    return storedFuncionarios ? JSON.parse(storedFuncionarios) : [
      { id: 101, nome: 'João Silva' },
      { id: 102, nome: 'Maria Oliveira' },
      { id: 103, nome: 'Carlos Pereira' },
      { id: 104, nome: 'Ana Souza' },
      { id: 105, nome: 'Pedro Santos' }
    ];
  });
  
  // Salvar ausências no localStorage quando mudar
  useEffect(() => {
    localStorage.setItem('ausencias', JSON.stringify(ausencias));
  }, [ausencias]);
  
  // Filtrar ausências
  const ausenciasFiltradas = ausencias.filter(ausencia => {
    const matchFuncionario = filtros.funcionario === '' || ausencia.funcionarioId.toString() === filtros.funcionario;
    const matchTipo = filtros.tipo === '' || ausencia.tipo === filtros.tipo;
    const matchStatus = filtros.status === '' || ausencia.status === filtros.status;
    
    // Implementação básica do filtro de período
    let matchPeriodo = true;
    if (filtros.periodo === 'mes') {
      const hoje = new Date();
      const primeiroDia = new Date(hoje.getFullYear(), hoje.getMonth(), 1);
      const ultimoDia = new Date(hoje.getFullYear(), hoje.getMonth() + 1, 0);
      
      const partesData = ausencia.dataInicio.split('/');
      const dataAusencia = new Date(
        parseInt(partesData[2]), 
        parseInt(partesData[1]) - 1, 
        parseInt(partesData[0])
      );
      
      matchPeriodo = dataAusencia >= primeiroDia && dataAusencia <= ultimoDia;
    }
    
    return matchFuncionario && matchTipo && matchStatus && matchPeriodo;
  });
  
  // Ordenar ausências (mais recentes primeiro por data de solicitação)
  const ausenciasOrdenadas = [...ausenciasFiltradas].sort((a, b) => {
    // Converter datas do formato DD/MM/YYYY para objetos Date para comparação
    const [diaA, mesA, anoA] = a.dataSolicitacao.split('/').map(Number);
    const [diaB, mesB, anoB] = b.dataSolicitacao.split('/').map(Number);
    
    const dateA = new Date(anoA, mesA - 1, diaA);
    const dateB = new Date(anoB, mesB - 1, diaB);
    
    return dateB - dateA; // Ordem decrescente (mais recente primeiro)
  });
  
  // Aprovar ausência
  const aprovarAusencia = (id) => {
    const ausenciasAtualizadas = ausencias.map(ausencia => 
      ausencia.id === id ? 
        { 
          ...ausencia, 
          status: 'aprovado',
          observacao: 'Aprovado pelo administrador'
        } : 
        ausencia
    );
    
    setAusencias(ausenciasAtualizadas);
    
    // Enviar notificação para o funcionário
    const ausencia = ausencias.find(a => a.id === id);
    if (ausencia) {
      const notificacoes = JSON.parse(localStorage.getItem('userNotifications') || '[]');
      notificacoes.push({
        id: Date.now(),
        userId: ausencia.funcionarioId,
        message: `Sua solicitação de ${tipoAusenciaTexto(ausencia.tipo)} foi aprovada.`,
        date: new Date().toLocaleDateString('pt-BR'),
        read: false
      });
      localStorage.setItem('userNotifications', JSON.stringify(notificacoes));
    }
    
    alert('Ausência aprovada com sucesso!');
  };
  
  // Abrir modal de rejeição
  const abrirModalRejeicao = (ausencia) => {
    setAusenciaSelecionada(ausencia);
    setMotivoRejeicao('');
    setModalRejeicaoAberto(true);
  };
  
  // Rejeitar ausência
  const rejeitarAusencia = () => {
    if (!ausenciaSelecionada) return;
    
    const ausenciasAtualizadas = ausencias.map(ausencia => 
      ausencia.id === ausenciaSelecionada.id ? 
        { 
          ...ausencia, 
          status: 'rejeitado',
          observacao: motivoRejeicao || 'Rejeitado pelo administrador'
        } : 
        ausencia
    );
    
    setAusencias(ausenciasAtualizadas);
    
    // Enviar notificação para o funcionário
    const notificacoes = JSON.parse(localStorage.getItem('userNotifications') || '[]');
    notificacoes.push({
      id: Date.now(),
      userId: ausenciaSelecionada.funcionarioId,
      message: `Sua solicitação de ${tipoAusenciaTexto(ausenciaSelecionada.tipo)} foi rejeitada. Motivo: ${motivoRejeicao || 'Não especificado'}.`,
      date: new Date().toLocaleDateString('pt-BR'),
      read: false
    });
    localStorage.setItem('userNotifications', JSON.stringify(notificacoes));
    
    // Fechar modal
    setModalRejeicaoAberto(false);
    setAusenciaSelecionada(null);
    setMotivoRejeicao('');
    
    alert('Ausência rejeitada com sucesso!');
  };
  
  // Abrir modal de visualização
  const verDetalhes = (ausencia) => {
    setAusenciaSelecionada(ausencia);
    setModalAberto(true);
  };
  
  // Função para exibir texto do tipo de ausência
  const tipoAusenciaTexto = (tipo) => {
    switch(tipo) {
      case 'atestado':
        return 'Atestado Médico';
      case 'falta':
        return 'Falta';
      case 'atraso':
        return 'Atraso';
      case 'licenca':
        return 'Licença';
      default:
        return 'Ausência';
    }
  };
  
  // Função para definir a classe de cor com base no tipo
  const tipoAusenciaClasse = (tipo) => {
    switch(tipo) {
      case 'atestado':
        return 'bg-yellow-600';
      case 'falta':
        return 'bg-red-600';
      case 'atraso':
        return 'bg-orange-600';
      case 'licenca':
        return 'bg-blue-600';
      default:
        return 'bg-gray-600';
    }
  };
  
  // Função para definir a classe de cor com base no status
  const statusAusenciaClasse = (status) => {
    switch(status) {
      case 'aprovado':
        return 'bg-green-600';
      case 'rejeitado':
        return 'bg-red-600';
      case 'pendente':
        return 'bg-yellow-600';
      default:
        return 'bg-gray-600';
    }
  };
  
  return (
    <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6">
      <h1 className="text-2xl font-bold mb-6">Gerenciamento de Ausências</h1>
      
      {/* Filtros */}
      <div className="bg-purple-900 bg-opacity-40 rounded-lg p-4 mb-6">
        <h2 className="text-xl font-semibold mb-4">Filtros</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm text-purple-300 mb-1">Funcionário</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.funcionario}
              onChange={(e) => setFiltros({...filtros, funcionario: e.target.value})}
            >
              <option value="">Todos os funcionários</option>
              {funcionarios.map(funcionario => (
                <option key={funcionario.id} value={funcionario.id}>{funcionario.nome}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Tipo</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.tipo}
              onChange={(e) => setFiltros({...filtros, tipo: e.target.value})}
            >
              <option value="">Todos os tipos</option>
              <option value="atestado">Atestado Médico</option>
              <option value="falta">Falta</option>
              <option value="atraso">Atraso</option>
              <option value="licenca">Licença</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Status</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.status}
              onChange={(e) => setFiltros({...filtros, status: e.target.value})}
            >
              <option value="">Todos os status</option>
              <option value="pendente">Pendente</option>
              <option value="aprovado">Aprovado</option>
              <option value="rejeitado">Rejeitado</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-purple-300 mb-1">Período</label>
            <select 
              className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
              value={filtros.periodo}
              onChange={(e) => setFiltros({...filtros, periodo: e.target.value})}
            >
              <option value="">Todos os períodos</option>
              <option value="mes">Mês atual</option>
              <option value="trimestre">Último trimestre</option>
              <option value="ano">Ano atual</option>
            </select>
          </div>
        </div>
      </div>
      
      {/* Lista de Ausências */}
      <div>
        <h2 className="text-xl font-semibold mb-4">Registros de Ausências</h2>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="text-purple-300 text-sm border-b border-purple-700">
                <th className="px-4 py-2 text-left">Funcionário</th>
                <th className="px-4 py-2 text-left">Tipo</th>
                <th className="px-4 py-2 text-left">Data Início</th>
                <th className="px-4 py-2 text-left">Data Fim</th>
                <th className="px-4 py-2 text-left">Motivo</th>
                <th className="px-4 py-2 text-left">Status</th>
                <th className="px-4 py-2 text-left">Ações</th>
              </tr>
            </thead>
            <tbody>
              {ausenciasOrdenadas.length > 0 ? (
                ausenciasOrdenadas.map(ausencia => (
                  <tr key={ausencia.id} className="border-b border-purple-700 hover:bg-purple-700 hover:bg-opacity-30">
                    <td className="px-4 py-3">{ausencia.funcionarioNome}</td>
                    <td className="px-4 py-3">
                      <span className={`inline-block px-2 py-1 rounded-full text-xs ${tipoAusenciaClasse(ausencia.tipo)}`}>
                        {tipoAusenciaTexto(ausencia.tipo).toUpperCase()}
                      </span>
                    </td>
                    <td className="px-4 py-3">{ausencia.dataInicio}</td>
                    <td className="px-4 py-3">{ausencia.dataFim}</td>
                    <td className="px-4 py-3">
                      <div className="truncate max-w-xs" title={ausencia.motivo}>
                        {ausencia.motivo}
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      <span className={`inline-block px-2 py-1 rounded-full text-xs ${statusAusenciaClasse(ausencia.status)}`}>
                        {ausencia.status.toUpperCase()}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <div className="flex space-x-2">
                        <button 
                          onClick={() => verDetalhes(ausencia)}
                          className="bg-purple-600 hover:bg-purple-700 text-white text-xs px-2 py-1 rounded-md"
                          title="Ver detalhes"
                        >
                          Detalhes
                        </button>
                        
                        {ausencia.status === 'pendente' && (
                          <>
                            <button 
                              onClick={() => aprovarAusencia(ausencia.id)}
                              className="bg-green-600 hover:bg-green-700 text-white text-xs px-2 py-1 rounded-md"
                              title="Aprovar ausência"
                            >
                              Aprovar
                            </button>
                            <button 
                              onClick={() => abrirModalRejeicao(ausencia)}
                              className="bg-red-600 hover:bg-red-700 text-white text-xs px-2 py-1 rounded-md"
                              title="Rejeitar ausência"
                            >
                              Rejeitar
                            </button>
                          </>
                        )}
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="7" className="px-4 py-6 text-center text-purple-300">
                    Nenhum registro de ausência encontrado com os filtros selecionados.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
      
      {/* Estatísticas */}
      <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-purple-900 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <h3 className="text-sm text-purple-300 mb-1">Total de Ausências (Mês)</h3>
          <p className="text-2xl font-bold">{ausencias.length}</p>
        </div>
        <div className="bg-purple-900 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <h3 className="text-sm text-purple-300 mb-1">Atestados Médicos</h3>
          <p className="text-2xl font-bold">{ausencias.filter(a => a.tipo === 'atestado').length}</p>
        </div>
        <div className="bg-purple-900 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <h3 className="text-sm text-purple-300 mb-1">Faltas</h3>
          <p className="text-2xl font-bold">{ausencias.filter(a => a.tipo === 'falta').length}</p>
        </div>
        <div className="bg-purple-900 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4">
          <h3 className="text-sm text-purple-300 mb-1">Atrasos</h3>
          <p className="text-2xl font-bold">{ausencias.filter(a => a.tipo === 'atraso').length}</p>
        </div>
      </div>
      
      {/* Modal de Visualização de Detalhes */}
      {modalAberto && ausenciaSelecionada && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-2xl">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">Detalhes da Ausência</h3>
              <button 
                onClick={() => setModalAberto(false)}
                className="text-purple-300 hover:text-white"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
              <div>
                <h4 className="text-sm text-purple-300 mb-1">Funcionário</h4>
                <p className="text-lg font-semibold">{ausenciaSelecionada.funcionarioNome}</p>
              </div>
              <div>
                <h4 className="text-sm text-purple-300 mb-1">Tipo de Ausência</h4>
                <p className="text-lg">
                  <span className={`inline-block px-2 py-1 rounded-full text-xs ${tipoAusenciaClasse(ausenciaSelecionada.tipo)}`}>
                    {tipoAusenciaTexto(ausenciaSelecionada.tipo).toUpperCase()}
                  </span>
                </p>
              </div>
              <div>
                <h4 className="text-sm text-purple-300 mb-1">Data de Início</h4>
                <p className="text-lg">{ausenciaSelecionada.dataInicio}</p>
              </div>
              <div>
                <h4 className="text-sm text-purple-300 mb-1">Data de Fim</h4>
                <p className="text-lg">{ausenciaSelecionada.dataFim}</p>
              </div>
              <div>
                <h4 className="text-sm text-purple-300 mb-1">Status</h4>
                <p className="text-lg">
                  <span className={`inline-block px-2 py-1 rounded-full text-xs ${statusAusenciaClasse(ausenciaSelecionada.status)}`}>
                    {ausenciaSelecionada.status.toUpperCase()}
                  </span>
                </p>
              </div>
              <div>
                <h4 className="text-sm text-purple-300 mb-1">Data da Solicitação</h4>
                <p className="text-lg">{ausenciaSelecionada.dataSolicitacao}</p>
              </div>
            </div>
            
            <div className="mb-6">
              <h4 className="text-sm text-purple-300 mb-1">Motivo</h4>
              <div className="bg-purple-800 bg-opacity-50 p-3 rounded-lg">
                <p>{ausenciaSelecionada.motivo}</p>
              </div>
            </div>
            
            {ausenciaSelecionada.observacao && (
              <div className="mb-6">
                <h4 className="text-sm text-purple-300 mb-1">Observações</h4>
                <div className="bg-purple-800 bg-opacity-50 p-3 rounded-lg">
                  <p>{ausenciaSelecionada.observacao}</p>
                </div>
              </div>
            )}
            
            {ausenciaSelecionada.documentoAnexo && (
              <div className="mb-6">
                <h4 className="text-sm text-purple-300 mb-1">Documento Anexo</h4>
                <div className="bg-purple-800 bg-opacity-50 p-3 rounded-lg flex items-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2 text-purple-300" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" />
                  </svg>
                  <span>documento_anexo.pdf</span>
                  <button className="ml-auto bg-purple-600 hover:bg-purple-700 text-white text-xs px-2 py-1 rounded-md">
                    Visualizar
                  </button>
                </div>
              </div>
            )}
            
            <div className="flex justify-end space-x-3">
              {ausenciaSelecionada.status === 'pendente' && (
                <>
                  <button 
                    onClick={() => {
                      setModalAberto(false);
                      aprovarAusencia(ausenciaSelecionada.id);
                    }}
                    className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md"
                  >
                    Aprovar
                  </button>
                  <button 
                    onClick={() => {
                      setModalAberto(false);
                      abrirModalRejeicao(ausenciaSelecionada);
                    }}
                    className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md"
                  >
                    Rejeitar
                  </button>
                </>
              )}
              <button 
                onClick={() => setModalAberto(false)}
                className="bg-purple-700 hover:bg-purple-600 text-white px-4 py-2 rounded-md"
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Modal de Rejeição */}
      {modalRejeicaoAberto && ausenciaSelecionada && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">Rejeitar Ausência</h3>
            <p className="mb-4">
              Você está rejeitando a ausência do tipo <strong>{tipoAusenciaTexto(ausenciaSelecionada.tipo)}</strong> para o funcionário <strong>{ausenciaSelecionada.funcionarioNome}</strong>.
            </p>
            
            <div className="mb-6">
              <label className="block text-sm text-purple-300 mb-1">Motivo da Rejeição</label>
              <textarea 
                className="w-full bg-purple-800 border border-purple-700 rounded-md p-2 text-white"
                value={motivoRejeicao}
                onChange={(e) => setMotivoRejeicao(e.target.value)}
                rows={3}
                placeholder="Informe o motivo da rejeição"
              ></textarea>
            </div>
            
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setModalRejeicaoAberto(false)}
                className="bg-purple-700 hover:bg-purple-600 text-white px-4 py-2 rounded-md"
              >
                Cancelar
              </button>
              <button 
                onClick={rejeitarAusencia}
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md"
              >
                Confirmar Rejeição
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AusenciasTab;
