import React, { useState, useEffect } from 'react';

const ApprovedDataComponent = () => {
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('horasExtras');
  
  // Estados para armazenar dados aprovados pelo administrador
  const [horasExtrasApproved, setHorasExtrasApproved] = useState([]);
  const [feriasApproved, setFeriasApproved] = useState([]);
  const [folgasApproved, setFolgasApproved] = useState([]);
  const [bancoHoras, setBancoHoras] = useState(null);
  const [ausenciasApproved, setAusenciasApproved] = useState([]);
  const [jornada, setJornada] = useState(null);

  // Obter dados do usuário logado (no caso, Carlos)
  const [userData, setUserData] = useState({
    id: 1,
    name: 'Carlos Pereira',
    email: 'carlos@email.com'
  });

  useEffect(() => {
    // Carregar dados das jornadas
    const storedJornadas = localStorage.getItem('jornadas');
    const storedFuncionariosPorJornada = localStorage.getItem('funcionariosPorJornada');

    if (storedJornadas && storedFuncionariosPorJornada) {
      const jornadas = JSON.parse(storedJornadas);
      const funcionariosPorJornada = JSON.parse(storedFuncionariosPorJornada);

      // Encontrar a jornada do Carlos
      const jornadaId = Object.entries(funcionariosPorJornada).find(
        ([id, funcionarios]) => funcionarios.includes('Carlos Pereira')
      )?.[0];

      if (jornadaId) {
        const jornadaCarlos = jornadas.find(j => j.id === parseInt(jornadaId));
        if (jornadaCarlos) {
          setJornada({
            ...jornadaCarlos,
            funcionarios: funcionariosPorJornada[jornadaId],
            horarios: [
              {
                dia: jornadaCarlos.diasTrabalho.length === 5 ? 'Segunda a Sexta' :
                     jornadaCarlos.diasTrabalho.length === 6 ? 'Segunda a Sábado' :
                     'Escala Especial',
                entrada: jornadaCarlos.horaInicio,
                saida: jornadaCarlos.horaFim
              }
            ]
          });
        }
      }
    }

    // Carregar outros dados (mantendo os existentes para exemplo)
    setHorasExtrasApproved([
      {
        id: 1,
        date: '2025-03-20',
        hours: 2,
        reason: 'Finalização de projeto urgente',
        status: 'aprovado'
      }
    ]);

    setFeriasApproved([
      {
        id: 1,
        dataInicio: '2025-07-01',
        dataFim: '2025-07-20',
        diasTotais: 20,
        observacao: 'Férias de julho',
        status: 'aprovado'
      }
    ]);

    setFolgasApproved([
      {
        id: 1,
        data: '2025-04-15',
        tipo: 'banco de horas',
        periodo: 'dia',
        motivo: 'Compensação de horas extras',
        status: 'aprovado'
      }
    ]);

    setBancoHoras({
      saldo: 10.5,
      ultimaAtualizacao: '24/03/2025'
    });

    setAusenciasApproved([
      {
        id: 1,
        date: '2025-03-10',
        type: 'Atestado Médico',
        justification: 'Consulta de rotina',
        status: 'aprovado'
      }
    ]);
  }, []);

  // Função para renderizar status com cor
  const renderStatus = (status) => {
    let className = '';
    let text = status.toUpperCase();
    switch(status) {
      case 'aprovado':
        className = 'bg-green-600';
        break;
      case 'pendente':
        className = 'bg-yellow-600';
        break;
      case 'rejeitado':
        className = 'bg-red-600';
        break;
      case 'detectado':
        className = 'bg-blue-600';
        break;
      default:
        className = 'bg-gray-600';
    }
    return (
      <span className={`inline-block px-2 py-1 rounded-full text-xs ${className}`}>
        {text}
      </span>
    );
  };

  // Função para formatar os dias de trabalho
  const formatarDiasTrabalho = (dias) => {
    if (!dias) return 'Não definido';
    if (dias.length === 5) return 'Segunda a Sexta';
    if (dias.length === 6) return 'Segunda a Sábado';
    if (dias.length === 7) return 'Todos os dias';
    return 'Escala Especial';
  };

  return (
    <>
      <button
        onClick={() => setShowModal(true)}
        className="w-full sm:w-64 bg-gradient-to-r from-purple-500 to-purple-700 hover:from-purple-600 hover:to-purple-800 text-white font-bold py-4 rounded-lg shadow-lg transition duration-300 ease-in-out transform hover:scale-105 flex items-center justify-center"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
        </svg>
        Dados do Colaborador
      </button>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-purple-900 rounded-lg shadow-xl p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">Dados do Colaborador - {userData.name}</h3>
              <button
                onClick={() => setShowModal(false)}
                className="text-purple-300 hover:text-white"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Tabs de navegação */}
            <div className="mb-6">
              <div className="flex overflow-x-auto space-x-2 bg-purple-800 bg-opacity-30 p-1 rounded-lg">
                <button
                  onClick={() => setActiveTab('horasExtras')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'horasExtras' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Horas Extras
                </button>
                <button
                  onClick={() => setActiveTab('ferias')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'ferias' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Férias
                </button>
                <button
                  onClick={() => setActiveTab('folgas')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'folgas' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Folgas
                </button>
                <button
                  onClick={() => setActiveTab('bancoHoras')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'bancoHoras' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Banco de Horas
                </button>
                <button
                  onClick={() => setActiveTab('ausencias')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'ausencias' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Ausências
                </button>
                <button
                  onClick={() => setActiveTab('jornada')}
                  className={`px-4 py-2 text-sm font-medium rounded-md whitespace-nowrap ${activeTab === 'jornada' ? 'bg-purple-600' : 'hover:bg-purple-700'}`}
                >
                  Jornada de Trabalho
                </button>
              </div>
            </div>

            {/* Conteúdo das abas */}
            <div className="bg-purple-800 bg-opacity-30 rounded-lg p-4">
              {/* ... outros conteúdos de abas permanecem os mesmos ... */}

              {/* Aba de Jornada atualizada */}
              {activeTab === 'jornada' && (
                <div>
                  <h4 className="text-lg font-semibold mb-4">Jornada de Trabalho</h4>
                  {jornada ? (
                    <div className="bg-purple-700 bg-opacity-40 rounded-lg p-6">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                          <h5 className="font-semibold mb-3">Informações da Jornada</h5>
                          <div className="space-y-4">
                            <div>
                              <p className="text-sm text-purple-300">Nome da Jornada</p>
                              <p className="text-lg">{jornada.nome}</p>
                            </div>
                            <div>
                              <p className="text-sm text-purple-300">Descrição</p>
                              <p className="text-lg">{jornada.descricao}</p>
                            </div>
                            <div>
                              <p className="text-sm text-purple-300">Horário</p>
                              <p className="text-lg">{jornada.horaInicio} - {jornada.horaFim}</p>
                            </div>
                            <div>
                              <p className="text-sm text-purple-300">Intervalo</p>
                              <p className="text-lg">
                                {jornada.intervaloInicio && jornada.intervaloFim
                                  ? `${jornada.intervaloInicio} - ${jornada.intervaloFim}`
                                  : 'Sem intervalo'}
                              </p>
                            </div>
                          </div>
                        </div>
                        <div>
                          <h5 className="font-semibold mb-3">Detalhes Adicionais</h5>
                          <div className="space-y-4">
                            <div>
                              <p className="text-sm text-purple-300">Dias de Trabalho</p>
                              <p className="text-lg">{formatarDiasTrabalho(jornada.diasTrabalho)}</p>
                            </div>
                            <div>
                              <p className="text-sm text-purple-300">Carga Horária Semanal</p>
                              <p className="text-lg">{jornada.cargaHorariaSemanal}h</p>
                            </div>
                            <div>
                              <p className="text-sm text-purple-300">Tolerância</p>
                              <p className="text-lg">
                                Entrada: {jornada.toleranciaEntrada}min / Saída: {jornada.toleranciaSaida}min
                              </p>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Seção de Funcionários na mesma jornada */}
                      <div className="mt-6">
                        <h5 className="font-semibold mb-3">Funcionários na mesma jornada</h5>
                        <div className="flex flex-wrap gap-2">
                          {jornada.funcionarios?.map((funcionario, index) => (
                            <span
                              key={index}
                              className={`px-3 py-1 rounded-full text-sm ${
                                funcionario === userData.name
                                  ? 'bg-purple-500'
                                  : 'bg-purple-700'
                              }`}
                            >
                              {funcionario}
                              {funcionario === userData.name && ' (Você)'}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>
                  ) : (
                    <p className="text-center text-purple-300 py-4">
                      Informações de jornada não disponíveis.
                    </p>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ApprovedDataComponent;
