import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginRegisterPage from "./pages/LoginRegisterPage";
import UserDashboard from "./pages/UserDashboard";
import AdminDashboard from "./pages/AdminDashboard";

// Componente para proteger rotas com base no papel do usuário
const ProtectedRoute = ({ children, requiredRole }) => {
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  if (!user?.authenticated) {
    return <Navigate to="/login" replace />;
  }

  if (requiredRole === "ADMIN" && !user.roles?.includes("ADMIN")) {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

const App = () => {
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  return (
    <Router>
      <Routes>
        {/* Página de login (se o usuário já estiver autenticado, redireciona) */}
        <Route 
          path="/login" 
          element={user?.authenticated ? <Navigate to={user.roles?.includes("ADMIN") ? "/admin" : "/dashboard"} replace /> : <LoginRegisterPage />} 
        />

        {/* Página inicial força redirecionamento para login */}
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Rota protegida para funcionários */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute requiredRole="FUNCIONARIO">
              <UserDashboard />
            </ProtectedRoute>
          }
        />

        {/* Rota protegida para admins */}
        <Route
          path="/admin"
          element={
            <ProtectedRoute requiredRole="ADMIN">
              <AdminDashboard />
            </ProtectedRoute>
          }
        />

        {/* Redirecionamento para login caso a rota não exista */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
