import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginRegisterPage from "./LoginRegisterPage";
import Dashboard from "./Dashboard";

const App = () => {
  return (
    <Router>
      <Routes>
        {/* LoginRegisterPage será a página inicial */}
        <Route path="/" element={<LoginRegisterPage />} />
        {/* Protegendo a rota do Dashboard */}
        <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
        {/* Redireciona qualquer rota inválida para o login */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

// Função para proteger a rota do Dashboard
const ProtectedRoute = ({ children }) => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user && user.authenticated ? children : <Navigate to="/" />;
};

export default App;
