import React, { useState, useEffect } from 'react';

const DashboardPage = () => {
  const [isAdmin, setIsAdmin] = useState(true);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [lastAction, setLastAction] = useState(null);
  const [timeEntries, setTimeEntries] = useState([]);

  useEffect(() => {
    const storedEntries = localStorage.getItem('timeEntries');
    if (storedEntries) {
      setTimeEntries(JSON.parse(storedEntries));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('timeEntries', JSON.stringify(timeEntries));
  }, [timeEntries]);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const formatTime = (date) => {
    return date.toLocaleTimeString('pt-BR', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const registerTimeEntry = (type) => {
    const now = new Date();
    const newEntry = {
      type,
      time: now.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' }),
      date: now.toLocaleDateString('pt-BR')
    };
    const updatedEntries = [newEntry, ...timeEntries];
    setTimeEntries(updatedEntries);
    localStorage.setItem('timeEntries', JSON.stringify(updatedEntries));
    setLastAction(`${type.charAt(0).toUpperCase() + type.slice(1)} registrada às ${newEntry.time}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 to-black text-white">
      <header className="bg-purple-900 bg-opacity-80 shadow-lg sticky top-0 z-10">
        <div className="container mx-auto px-4 py-3 flex justify-between items-center">
          <div className="flex items-center">
            <span className="text-xl font-bold">CuidaEmprego</span>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-6">
        <div className="text-center mb-8">
          <p className="text-4xl font-bold text-white">{formatTime(currentTime)}</p>
          {lastAction && (
            <div className="mt-2 text-sm bg-purple-600 inline-block px-3 py-1 rounded-full">
              {lastAction}
            </div>
          )}
        </div>

        <div className="flex flex-wrap justify-center gap-4 mb-8">
          <button 
            onClick={() => registerTimeEntry('entrada')}
            className="w-full sm:w-64 bg-purple-600 text-white font-bold py-4 rounded-lg shadow-lg"
          >
            Registrar Entrada
          </button>

          <button 
            onClick={() => registerTimeEntry('saída')}
            className="w-full sm:w-64 bg-red-600 text-white font-bold py-4 rounded-lg shadow-lg"
          >
            Registrar Saída
          </button>
        </div>

        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-4 mb-8">
          <h2 className="text-xl font-bold mb-4">Registros Recentes</h2>
          <ul>
            {timeEntries.map((entry, index) => (
              <li key={index} className="flex justify-between border-b border-purple-600 py-2">
                <span>{entry.date}</span>
                <span className={`font-bold ${entry.type === 'entrada' ? 'text-green-400' : 'text-red-400'}`}>
                  {entry.type.charAt(0).toUpperCase() + entry.type.slice(1)}
                </span>
                <span>{entry.time}</span>
              </li>
            ))}
          </ul>
        </div>
      </main>
    </div>
  );
};

export default DashboardPage;
