import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const LoginRegisterPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(isLogin ? 'Login com:' : 'Registro com:', formData);
    
    if (isLogin) {
      // Simulando login bem-sucedido:
      const userInfo = {
        name: 'Usuário',
        isAdmin: false // Defina como true se for um usuário administrador
      };
      localStorage.setItem('user', JSON.stringify(userInfo));
      
      // Redireciona para a página correta com base no tipo de usuário
      if (userInfo.isAdmin) {
        window.location.href = '/admin';
      } else {
        window.location.href = '/dashboard';
      }
    } else {
      // Registro: Armazenar informações do usuário
      const userInfo = {
        name: formData.name,
        isAdmin: false // Defina como true se for um usuário administrador
      };
      localStorage.setItem('user', JSON.stringify(userInfo));
      
      // Redireciona para a página correta com base no tipo de usuário
      if (userInfo.isAdmin) {
        window.location.href = '/admin';
      } else {
        window.location.href = '/dashboard';
      }
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-purple-900 to-black">
      {/* ... */}
      
      {/* Card do formulário */}
      <div className="w-full max-w-md mx-auto px-4">
        <div className="bg-purple-800 bg-opacity-40 backdrop-blur-sm rounded-lg shadow-lg p-6 border border-purple-500">
          {/* ... */}
          
          {/* Formulário */}
          <form onSubmit={handleSubmit}>
            {/* ... */}
          </form>
        </div>
      </div>

      {/* ... */}
    </div>
  );
};

export default LoginRegisterPage;
